ADV-Check.script:
{Log("Fktn.: ADV-Check")}

{_ SetState('std_dockingcomp_inst', false) _}
{_ SetState('std_dockingcomp_en', false) _}
{_ SetState('adv_dockingcomp_inst', false) _}
{_ SetState('adv_dockingcomp_en', false) _}

{_ SetState('cellbank_inst', false) _}

{for compartment in ship.compartments:
	{if compartment.module.name = "Standard-Landecomputer": 
		{SetState('std_dockingcomp_inst', true)}
		{if compartment.module.enabled: {SetState('std_dockingcomp_en', true)}
			{_ L C verbaut _}
		}
    |elif compartment.module.name = "Fortschrittlicher Landecomputer": 
       	{SetState('adv_dockingcomp_inst', true)}
		{if compartment.module.enabled: {SetState('adv_dockingcomp_en', true)}
			{_  F L C verbaut _}
		|else:
			{_ manuelles Starten und Landen. _}
		}
	{_ elif compartment.module.name = "Schildzellenbank":
	   SetState('cellbank_inst', true) _}
	}
}

AFMU repairs.script:
{Log("EVENT: AFMU repairs")}

{set translate to event.item} {set Modul to F("Translate Ship Module")}
{set V to rand(1,2)}
{if V=1:{article}} {Modul}

{if event.repairedfully:
    {if V=1:wurde |else: ist} {Occasionally(3, OneOf('komplett','vollständig'))} repariert
|else:
    {if V=1:ist zu {(event.health*100)}%} funktionsbereit
}
{Occasionally(3, OneOf('und{if V=1: ist} bereit zur re-aktivierung','und ist einsatzbereit'))}

{_
- item           The module that was repaired
- repairedfully  Whether the module was fully repaired (true/false)
- health         The health of the module (1.000000 = fully repaired)
_}

AFMU repairs.defaultValue:
{event.item} 
{if event.repairedfully:
    fully repaired
|else:
    partially repaired 
    {Occasionally(2, cat(
        OneOf("to ", "at "),
        Humanise(event.health * 100),
        " percent functionality"
    ))}
}

{Occasionally(2, 
    cat(OneOf(", ", "and is"), " ready for re-activation")
)}.

