            }
        }
        {if ship.role = "Erkundung":
        |elif ship.role = "Handel":
        |elif ship.role = "Journalismus":
        |elif ship.role = "Racing":
        |elif ship.role = "Reparatur":
        |elif ship.role = "Auftanken":
        |elif ship.role = "Suche und Rettung":
        |elif ship.role = "Taxi":
        |elif ship.role = "Tourismus":
        |elif ship.role = "Wissenschaft":
        |else:
