Blueprint make report.script:
{Log("Fktn.: Blueprint make report")}

{_ Fetch from context / state }
{set blueprintname to state.blueprint}
{set blueprintgrade to state.blueprintgrade}

{if blueprintname && blueprintgrade:
    {_ Fetch full blueprint details }
    {set blueprint to BlueprintDetails(blueprintname, blueprintgrade)}

    {_ Context }
    {SetState('eddi_context_last_subject', 'blueprint')}
    {SetState('eddi_context_blueprint_name', blueprintname)}
    {SetState('eddi_context_blueprint_grade', blueprintgrade)}
}

{if !blueprint:
    {_ Fetch from context }
    {set blueprint to BlueprintDetails(GetState('eddi_context_blueprint_name'), GetState('eddi_context_blueprint_grade'))}
}

{_ Function to obtain current inventory of a material}
{set _inventory(required) to:
    {for material in materials:
        {if material.material = required:
            {return material}
        }
    }
}

{set canbemade to 999}

{if blueprint:
    {_ Find the smallest number of this blueprint that can be made }
    
    {set cur to 0}
    {while cur < len(blueprint.materials):
        {set materialInventory to _inventory(blueprint.materials[cur].material)}
        {if !materialInventory:
            {set canbemade to 0}
        |else:
            {if materialInventory.amount < canbemade:
                {set canbemade to materialInventory.amount}
            }
        }
        {set cur to cur + 1}
    }

    {if canbemade = 0:
        Wir können keinen {blueprintname} Grad {blueprintgrade} herstellen
    |elif canbemade = 1:
        Wir können eine {blueprintname} Grad {blueprintgrade} herstellen
    |else:
        Wir können {canbemade} {blueprintname} Grad {blueprintgrade} herstellen
    }
|else:
    Es sind keine Informationen zum Bauplan {blueprintname} und Bauplan Grad {blueprintgrade} verfügbar
}.

Blueprint make report.defaultValue:
{_ Context }
{SetState('eddi_context_last_subject', 'blueprint')}
    
{_ Fetch from context / state _}
{set blueprintname to state.eddi_context_blueprint_name}
{set blueprintgrade to state.eddi_context_blueprint_grade}

{if blueprintname && blueprintgrade:
    {_ Fetch full blueprint details }
    {set blueprint to BlueprintDetails(blueprintname, blueprintgrade)}
}

{_ Function to obtain current inventory of a material}
{set GetMaterialFromInventory(requiredMaterialName) to:
    {for material in materials:
        {if material.material = requiredMaterialName:
            {return material}
        }
    }
}

{set canbemade to 999}

{if blueprint:
    {_ Find the smallest number of this blueprint that can be made }
    
    {set cur to 0}
    {while cur < len(blueprint.materials):
        {set materialInventory to GetMaterialFromInventory(blueprint.materials[cur].material)}
        {if !materialInventory:
            {set canbemade to 0}
        |else:
            {if materialInventory.amount < canbemade:
                {set canbemade to materialInventory.amount}
            }
        }
        {set cur to cur + 1}
    }

    {if canbemade = 0:
        You cannot make any {blueprintname} grade {blueprintgrade}
    |elif canbemade = 1:
        You can make one {blueprintname} grade {blueprintgrade}
    |else:
        You can make {blueprintname} grade {blueprintgrade} {Humanise(canbemade)} times.
    }
    
|else:
    No information available on materials required for {blueprintname} grade {blueprintgrade}
}.

Blueprint material report.script:
{Log("Fktn.: Blueprint material report")}

{_ Fetch blueprint from state }
{set blueprintname to state.blueprint}
{set blueprintgrade to state.blueprintgrade}

{if blueprintname && blueprintgrade:
    {_ Fetch full blueprint details }
    {set blueprint to BlueprintDetails(blueprintname, blueprintgrade)}

    {_ Context }
    {SetState('eddi_context_last_subject', 'blueprint')}
    {SetState('eddi_context_blueprint_name', blueprintname)}
    {SetState('eddi_context_blueprint_grade', blueprintgrade)}
}

{if blueprintname:
    {_ Fetch full blueprint details }
    {set blueprint to BlueprintDetails(blueprintname, blueprintgrade)}

    {_ Context }
    {SetState('eddi_context_last_subject', 'blueprint')}
    {SetState('eddi_context_blueprint_name', blueprintname)}
    {SetState('eddi_context_blueprint_grade', blueprintgrade)}
}

{if !blueprint || !blueprintgrade:
    {_ Fetch from context }
    {set blueprint to BlueprintDetails(GetState('eddi_context_blueprint_name'), GetState('eddi_context_blueprint_grade'))}
}

{if blueprint && blueprintgrade:
    {set materials to len(blueprint.materials)}
    {blueprintname} Grad {blueprintgrade} erfordert 
    {if materials = 1:
        {blueprint.materials[0].material}
    |elif materials = 2:
        {blueprint.materials[0].material} und {blueprint.materials[1].material}
    |else:
        {set cur to 0}
        {while cur < len(blueprint.materials):
            {if cur = 0:
                {blueprint.materials[cur].material}
            |elif cur < len(blueprint.materials) - 1:
                , {blueprint.materials[cur].material}
            |else:
                , und {blueprint.materials[cur].material}
            }
            {set cur to cur + 1}
        }
    }
|else:
    Es sind keine Informationen wie z.B. benötigte Materialien für den Bauplan {blueprintname} vorhanden
}.

Blueprint material report.defaultValue:
{_ Context }
{SetState('eddi_context_last_subject', 'blueprint')}
    
{_ Fetch from context / state _}
{set blueprintname to state.eddi_context_blueprint_name}
{set blueprintgrade to state.eddi_context_blueprint_grade}

{if blueprintname && blueprintgrade:
    {_ Fetch full blueprint details }
    {set blueprint to BlueprintDetails(blueprintname, blueprintgrade)}
}

{if blueprint:
    {set blueprintMaterials to []}
    {for material in blueprint.materials:
         {set blueprintMaterials to cat(blueprintMaterials, [material.material])}
    }
    {blueprintname} grade {blueprintgrade} requires 
    {List(blueprintMaterials)}.
|else:
    No information available on materials required for {blueprintname} grade {blueprintgrade}
}.

Bodies Exploration Report.script:
{Log("Fktn.: Bodies Exploration Report")}

{_ Bodies Exploration Report _}
{_ Report on the current exploration status of the current system. _}

{_ Preferences _}
{F("A_Einstellungen")}

{_ Fetch from context _}
{set reportSystem to SystemDetails(GetState('eddi_context_system_name'))}
{if !reportSystem: {set reportSystem to SystemDetails(system.systemname)}}

{_ Identify bodies worth mapping _}
{set totalBodies to reportSystem.totalbodies}
{set scannedBodies to len(reportSystem.bodies)}
{set toMap to 0}
{set mappedBodies to 0}

{_ For some reason, on rare occasions the number of system.bodies is greater than it should _}
{_ be when loading, i.e. 23 in a system with only 22 bodies. This tries to fix that. _}
{if scannedBodies > totalBodies: {set scannedBodies to totalBodies}}

{for body in reportSystem.bodies:
    {if body.mapped :								{set mappedBodies to mappedBodies + 1}
    |elif body.bodyType.invariantName = "Star" :	{set scannedStars to scannedStars + 1}
    |elif body.terraformState.invariantName != "Not terraformable" || body.planettype = "Planet mit hochwertigen Metallen" && !body.alreadymapped :
        {if state.reportterraformables:					{set toMap to toMap + 1}
        }
    |elif GetState('reportplanettypes[body.planetClass.invariantName]') || body.estimatedvalue > GetState('highvaluethreshold'):
        {set toMap to toMap + 1}
    }
}

{if totalBodies > 0: Dieses System wurde
    {if totalBodies = scannedBodies:
    |elif scannedBodies - scannedStars > 0:
        {Occasionally(2, "nur")} teilweise
        {set pre to "bisher"}
        {set mid to "bisher"}
        {set post to "schon"}
    |else: noch nicht
        {set pre to "so dass"}
        {set post to "noch"}
    }
    gescannt,
    
    {if scannedBodies < totalBodies: Es
		{set remaining to totalBodies - scannedBodies}
		{if remaining = 1: kann noch ein Körper |else: können noch {remaining} Körper} gescannt werden,
    }
    {if toMap > 0 : {F("Bodies to map")}
    |elif toMap = 0 && mappedBodies > 0 :
        , und alle Körper die es wert sind {mid}, wurden {OneOf("gemeppt", "kartografiert", "vermessen")}.
    |else: es wurden keine
        {if totalBodies - scannedStars > 0 : Körper, bei denen das Kartografieren lohnt, entdeckt.
        |else: Körper zum Vermessen entdeckt.
        }
    }
}

Bodies forgotten to map.script:
{Log("Fktn.: Bodies forgotten to map")}

{_ Preferences _}
{F("A_Einstellungen")}

