        {if body.bodyType.invariantName != "Stern":
            {if body.terraformState.invariantName = "Kandidat zur Terraformierung" 
             || body.terraformState.invariantName = "Terraformiert":
                {if state.reportterraformables:
                    {if !toMapList[body.shortname]: 
                        {set toMapList to cat(toMapList, [body.shortname])}
                    } 
                }
            |elif state.reportplanettypes[body.planetClass.invariantName]:
                {if !toMapList[body.shortname]: 
                    {set toMapList to cat(toMapList, [body.shortname])}
                }
            |elif body.estimatedvalue > state.highvaluethreshold:
                {if !toMapList[body.shortname]: 
                    {set toMapList to cat(toMapList, [body.shortname])}
                } 
            }
        }
    }
}

{_ Begin speaking _}

{if len(toMapList) > 0:

    {if len(toMapList) = 1: Objekt |else: Objekte}

    {set cur to 0}
    {while cur < len(toMapList):
        {toMapList[cur]}
        {set cur to cur + 1}
        {if cur = len(toMapList) - 1: und
        |elif cur < len(toMapList) - 1:,
        }
    }

    {if len(toMapList) = 1: ist |else: sind }
    {if event.type = "Body mapped": {OneOf("auch")} }
    {if len(toMapList) = 1: ein
    {OneOf("empfehlenswerter", "guter")}
    |elif  len(toMapList) > 1:
    {OneOf("empfehlenswerte", "gute")}
    }
    {set candidates to:
        {if len(toMapList) > 1: Kandidaten |else: Kandidat}
    }
    {OneOf('{candidates} für eine Kartografierung', '{candidates} für eine Vermessung', 'Kartografierungs {candidates}')}.

    Wir sollten 
    {if len(toMapList) = 1: das Objekt |else: die Objekte }
    vor Verlassen des Systems kartografieren.
}

