Bodies to map.script:
{Log("Fktn.: Bodies to map")}

{_ Fetch from Context from "Body mapped" _}
{_ SetState('eddi_context_mapped', event.bodyname)}
{_ SetState('eddi_context_mapped_system', event.systemname)}

{set reportbody to BodyDetails(GetState('eddi_context_mapped'), GetState('eddi_context_mapped_system'))}
{if !reportbody:
    {set reportbody to BodyDetails(GetState('eddi_context_mapped'))}}

{set reportSystem to SystemDetails(system.systemname)}

{_ Identify bodies worth mapping _}

{set toMapList to []}
{set object to ""}
{set mappedBodies to 0}
{set totalBodies to 0}

{for body in reportSystem.bodies:
    {set totalBodies to totalBodies + 1}
    {if body.mapped:
        {set mappedBodies to mappedBodies + 1}
    |else:
        {if body.planettype = "Planet mit hochwertigen Metallen" ||
            body.planettype = "Metallreicher Körper":
                {if body.terraformstate = "Kandidat zur Terraformierung" ||
                    body.terraformstate = "Terraformierung" ||
                    body.terraformstate = "Terraformiert":
                    {set object to cat(body.shortname, " " body.planettype)}
                    {set toMapList to cat(toMapList,[object])}
                    {_ if find(body.terraformstate, "Terraform")  >= 0 : }
                }
        }
        {if body.planettype = "Ammoniakwelt" || 
            body.planettype = "Wasserwelt" ||
            body.planettype = "Erdähnliche Welt" :
            {set object to cat(body.shortname, ", ", body.planettype, ", ")}
            {set toMapList to cat(toMapList,[object])}
        }
    }
}
{set toMapCount to len(toMapList)}

{_ Begin speaking _}

{if toMapCount > 0:
    {if toMapCount = 1:
        {if reportbody.bodytype:{reportbody.bodytype}
        |else:
            {OneOf("Objekt,", "Körper,")}}
    |else:
        Die {OneOf("Objekte,", "Körper,")}}

    {set cur to 0}
    {while cur < toMapCount:
        {toMapList[cur]}
        {set cur to cur + 1}
        {if cur = toMapCount - 1: und
        |elif cur < toMapCount - 1:
        }
    }

    {if toMapCount = 1: ist |else: sind }
    {if event.type = "Body mapped": {OneOf("auch")} }
    {if toMapCount = 1: ein {OneOf("empfehlenswerter", "guter")}
    |elif  toMapCount > 1: {OneOf("empfehlenswerte", "gute")}
    }
    {set candidates to: {if toMapCount > 1: Kandidaten |else: Kandidat}}
    {OneOf('{candidates} für eine Kartografierung', '{candidates} für eine Vermessung', 'Kartografierungs {candidates}')}.

|elif mappedBodies > 0:
    
    {if mappedBodies < totalBodies:
        {OneOf(
        "Wir haben alle {OneOf('lohnenswerten', 'finanziell interessanten')} Körper in diesem System kartografiert.",
        "Alle {OneOf('lohnenswerten Körper', 'finanziell interessanten Körper')}
        {OneOf('wurden kartografiert', 'sind jetzt kartografiert.')}"
        )}
    |else:
        {OneOf(
        "Wir haben alle Körper in diesem System kartografiert.",
        "Alle {OneOf('Körper', 'interessanten Körper')}
        {OneOf('wurden kartografiert.', 'sind jetzt kartografiert.')}"
        )}
    }

|elif toMapCount = 0:
    {if state.object_anzahl_bio = "" : 
    |else:
        {if GetState('object_ersterfassung') = true :   Trotz möglicher Ersterfassung, lohnt sich die Vermessung 
                                                        eines Körpers in diesem System, finanziell nicht.
        |else:
            {if GetState('system_nur_sonnen') = true: {_ keine Ansage }
            |else:
                {char(10)}{char(13)}{Occasionally(2,"Leider ")}kein lohnendes Objekt zum Kartographieren gefunden.
            }
        }         
	}
}

{SetState('object_anzahl_bio', "")}

Bodies to map.defaultValue:
{_ Preferences: _}
{set recommendAlreadyMapped to true}   {_ If true, recommend bodies that have already been mapped by other players _}
{set reportTerraformables to true}     {_ If true, all terraformable and terraformed bodies shall be mapping candidates _}
{set highValueThreshold to 80000}      {_ All bodies with a minimum estimated scan value exceeding this threshold shall be mapping candidates _}
 
{_ When true, these planet types shall be mapping candidates _}
{set reportPlanetTypes to [
    "Ammonia world": true,
    "Earth-like world": true,
    "Gas giant with ammonia based life": false,
    "Gas giant with water based life": false,
    "Helium gas giant": false,
    "Helium-rich gas giant": false,
    "High metal content world": false,
    "Icy body": false,
    "Metal-rich body": false,
    "Rocky body": false,
    "Rocky ice world": false,
    "Class I gas giant": false,
    "Class II gas giant": false,
    "Class III gas giant": false,
    "Class IV gas giant": false,
    "Class V gas giant": false,
    "Water giant": false,
    "Water giant with life": false,
    "Water world": true,
]}

{_ Fetch from Context, if a context is set _}
{if state.eddi_context_system_name:
    {set reportSystem to SystemDetails(state.eddi_context_system_name)}
|else:
    {set reportSystem to SystemDetails(system.systemname)}
}

{_ Identify bodies worth mapping _}

{set toMapList to []}
{set mappedBodies to 0}
{set totalBodies to 0}

{for compartment in ship.compartments:
    {if compartment.module.invariantName = "Detailed Surface Scanner":
        {set hasDSS to true}
    }
}
        
{if hasDSS:
    {for body in reportSystem.bodies:
        {set totalBodies to totalBodies + 1}
        {if body.mapped:
            {set mappedBodies to mappedBodies + 1}
        |else:
            {if body.bodyType.invariantName != "Star" && 
               (recommendAlreadyMapped || !body.alreadymapped):
                {if reportTerraformables && (body.terraformState.invariantName != "Not terraformable"):
                    {if !toMapList[body.shortname]: 
                        {set toMapList to cat(toMapList, [body.shortname])}
                    } 
                |elif reportPlanetTypes[body.planetClass.invariantName]:
                    {if !toMapList[body.shortname]: 
                        {set toMapList to cat(toMapList, [body.shortname])}
                    }
                |elif body.estimatedvalue > highValueThreshold:
                    {if !toMapList[body.shortname]: 
                        {set toMapList to cat(toMapList, [body.shortname])}
                    } 
                }
            }
        }
    }
}
{set toMapCount to len(toMapList)}

{_ Begin speaking _}

{if toMapCount > 0:

    {if toMapCount = 1: Body |else: Bodies}

    {set cur to 0}
    {while cur < toMapCount:
        {P(toMapList[cur], "body")}
        {set cur to cur + 1}
        {if cur = toMapCount - 1: and
        |elif cur < toMapCount - 1:,
        }
    }

    {if toMapCount = 1: is |else: are }
    {if event.type = "Body mapped": also }
    {if toMapCount = 1: a }
    {OneOf("recommended", "good")}
    {set candidates to when(toMapCount = 1, "candidate", "candidates")}
    {OneOf('{candidates} for mapping', '{candidates} to map', 'mapping {candidates}')}.

|elif mappedBodies > 0:

    {OneOf(
        "You have mapped all {if mappedBodies < totalBodies: {OneOf('worthwhile', 'interesting')}} bodies.",
        "All {OneOf( '{if mappedBodies < totalBodies: worthwhile} bodies', 'bodies of interest' )} {OneOf( 'have been mapped.', 'are now mapped.' )}"
    )}
}

