         {set class to: {if V<2: 1 |else: erster}} {set gas_temp to "weniger als 150"}
         {set composition to: und eine{if V<2:r} dichte{if V<2:n} Wolkenschicht aus gasförmigem Amm moniak}
      |elif roman = "II" :
         {set class to: {if V<2: 2 |else: zweiter}} {set gas_temp to "ca. 250"}
         {set composition to: und eine{if V<2:r} dichte{if V<2:n} Wolkenschicht aus Wasserdampf}
      |elif roman = "III":
         {set class to: {if V<2: 3 |else: dritter}} {set gas_temp to "rund 600"}
         {set composition to: , bei de{if V<2:m |else:nen} keine Wolkenbildung durch gaasförmige Elemente vorkommt}
      |elif roman = "IV" :
         {set class to: {if V<2: 4 |else: vierter}} {set gas_temp to "rund 1000"}
         {set composition to: und eine{if V<2:r} dichte{if V<2:n} Wolkenschicht aus Alkalimetall Dämpfen}
      |elif roman = "V"  :
         {set class to: {if V<2: 5 |else: fünfter}} {set gas_temp to "über 1500"}
         {set composition to: und eine{if V<2:r} dichte{if V<2:n} Wolkenschicht aus Silikaten und Eisen}
      |else: Fehler bei Klasse: {reportbody.planettype} roman: {roman}
      }
      {  if V<2: Es handet sich um einen {sudarsky} Klasse {class} Gasriesen mit
      |elif V<3: Typisch für {sudarsky} Gasriesen {class} Klasse sind
      |else    : Gasriesen {class} Klasse wie dieser hier haben}
      Oberflächentemperaturen von {gas_temp} Grad Kelvin {composition}.
   }

|elif reportbody.pressure <= 0.001 || len(atmo) = 0 || !atmo:
   {OneOf("Objekt ist frei von jeglicher Atmosphäre",
          "Es wird keine{Occasionally(3, 'rlei')} Atmosphäre {OneOf('registriert','angezeigt','festgestellt')}",
          "Wenn dieses Objekt jemals eine Atmosphäre hatte, ist sie über die Jahre verloren gegangen")}.

|else: 
   {set press to (reportbody.pressure / 1.01325)}
   {  if press <    0.1 : {set pressdesc to OneOf("spuren hafte", "extrem dünne")}
   |elif press <    0.25: {set pressdesc to "sehr dünne"}
   |elif press <    0.5 : {set pressdesc to "dünne"}
   |elif press <    2   : {set pressdesc to ""}
   |elif press <   10   : {set pressdesc to "dicke"}
   |elif press <   50   : {set pressdesc to OneOf("dichte", "sehr dicke")}
   |elif press <  100   : {set pressdesc to OneOf("sehr dichte", "extrem dicke")}
   |elif press >= 100   : {set pressdesc to OneOf("komprimierte", "extrem dichte")}
   }
  
   {if find(reportbody.atmosphere, "rich") >= 0:
      {set element to lcase(join(split(atmo, "heiß "), ""))}
      {set element to lcase(join(split(atmo, "heißes "), ""))}
      {set element to lcase(join(split(atmo, "heißem "), ""))}
      {set desc to OneOf("{reportbody.name} hat eine", "Detektiere eine", "Sensoren zeigen eine")}
      {OneOf("{desc} {pressdesc} {element} Atmosphäre", 
             "Die {pressdesc} Atmosphäre ist äußerst {element}reich", 
             "Die {pressdesc} Atmosphäre ist reich an {element}",
             "Die {pressdesc} Atmosphäre enthält hohe Anteile an {element} Gas")}

   |elif find(atmo, "dampf") >= 0:
      {set atmo to token(atmo, "dampf", " dampf")}
      {OneOf("Die {pressdesc} Atmosphäre setzt sich zusammen aus {atmo}",
             "Die {pressdesc} Atmosphäre ist reich an {atmo}",
             "Die {pressdesc} Atmosphäre enthält höhere Anteile an {atmo}")}

   |elif atmo = "Wasser":
      Es ist
      {OneOf("ein Ozeanplanet", "ein {planettype}")}

   |elif atmo = "für wasser basiertes Leben geeignet":
      Es ist
      {OneOf("eine Meereswelt", "ein {planettype}")},
      geeignet für Wasser basierendes Leben

   |else:
      {set mostly to OneOf("primär", "hauptsächlich", "größtenteils", "zu großen Teilen")}
      {OneOf("Die {pressdesc} Atmosphäre besteht {mostly} aus", "Der Hauptbestandteil der {if pressdesc:{pressdesc}n} Atmosphäre ist")}
      {if atmo = "Kohlendioxidreich": Kohlendioxid |else: {atmo}}
   }

   mit einem Oberflächendruck von {set value to round(press,1)}
   {if value = 1: eihner Atmosphäre |else: {F("Humanise ValueGer")} Atmosphären}
   {if reportbody.temperature:
      und einer durchschnittlichen Temperatur von
      {if celsius:
         {set value to round(reportbody.temperature - 273.15)} {F("Humanise ValueGer")}
         Grad Celsius.
      |elif !celsius:
         {set value to (reportbody.temperature * 1.8 - 459.67)} {F("Humanise ValueGer")}
         Grad Fahrenheit.
      }
   }
}

Body atmosphere report.script:
{Log("Fktn.: Body atmosphere report")}

{_ Preferences _}
{set reportAtmoPressure to 0}   {_ Set to 1 if you want to hear about the atmospheric pressure. _}
{set reportAtmoTempScale to 1}  {_ Set to 0 if you want to hear about temperatures in Kelvin _}
                                {_ Set to 1 if you want to hear about temperatures in Celsius _}
                                {_ Set to 2 if you want to hear about temperatures in Fahrenheit _}
                                {_ Set to 3 if you don't want to hear about the temperature of the body _}

{_ Define functions for converting temperature scales _}
{set ToCelsius(kelvin) to: {round(kelvin - 273)}}
{set ToFahrenheit(kelvin) to: {round((kelvin-273)*9/5 + 32)}}
{set TempCalc(kelvin) to: 
    {if reportAtmoTempScale = 0: {round(kelvin)}}
    {if reportAtmoTempScale = 1: {ToCelsius(kelvin)}}
    {if reportAtmoTempScale = 2: {ToFahrenheit(kelvin)}}
}

{_ Name our temperature scale _}
{if reportAtmoTempScale = 0: {set tempscale to: Kelvin}}
{if reportAtmoTempScale = 1: {set tempscale to: Grad Celsius}}
{if reportAtmoTempScale = 2: {set tempscale to: Grad Fahrenheit}}

{_ Fetch from context _}
{_ set reportbody to BodyDetails("Jupiter", "Sol")} {_ zum Testen _}
{set reportbody to BodyDetails(GetState('eddi_context_body_name'), GetState('eddi_context_body_system'))}
{if !reportbody: 
    {set reportbody to BodyDetails(GetState('eddi_context_body_name'))}
}

{if !reportbody.name || reportbody.name = "": Ich weiß nicht {OneOf("welchen Körper", "welches Objekt")} Du meinst?
|else:

    {if find(reportbody.planettype, "Gasriese") >= 0:
        {if find(reportbody.planettype, "Klasse") >= 0:
            {set sudarsky to Occasionally(3, "Su-dars-ki")}
        }
