            }
            eine dichte Wolkenschicht {OneOf("aus verdampften Silikaten und Eisen", "mit Silikat und Eisendämpfen")}.
        |elif find(reportbody.planettype, "Klasse IV") >= 0:
            {planetdesc} 
            {if reportAtmoTempScale < 3: 
                {set tempmin to: {TempCalc(354)}}
                {set tempmax to: {TempCalc(903)}}
                Temperaturen zwischen {Humanise(tempmin)} und {Humanise(tempmax)} {tempscale}, 
                und bildet
            }
            dichte Wolken {OneOf("aus Alkalimetalldämpfen", "aus verdampften Alkalimetalle")}.   
        |elif find(reportbody.planettype, "Klasse III") >= 0:
            {planetdesc} 
            {if reportAtmoTempScale < 3: 
                {set tempmin to: {TempCalc(251)}}
                {set tempmax to: {TempCalc(353)}}
                Oberflächentemperaturen zwischen {Humanise(tempmin)} und {Humanise(tempmax)} {tempscale},
            }
            Temperaturen bei denen {OneOf("gasförmige Elemente keine Wolken bilden",
            "keine geeigneten Chemikalien in der Atmosphäre vorhanden sind, um eine anhaltende Wolkenschicht zu bilden", 
            "die Atmosphäre keine Wolkenschicht aufrechterhalten kann")}.
        |elif find(reportbody.planettype, "Klasse II") >= 0:
            {planetdesc} 
            {if reportAtmoTempScale < 3: 
                {set tempmin to: {TempCalc(154)}}
                {set tempmax to: {TempCalc(250)}}
                Oberflächentemperaturen zwischen {Humanise(tempmin)} und {Humanise(tempmax)} {tempscale}, und bildet
            }
            dichte Wolken aus Wasserdampf.  
        |elif find(reportbody.planettype, "Klasse I") >= 0:
            {planetdesc} 
            {if reportAtmoTempScale < 3: 
                {set tempmin to: {TempCalc(153)}}
                {set tempmax to: {TempCalc(83)}}
                Oberflächentemperaturen von {Humanise(tempmax)} {tempscale} {Occasionally(2, OneOf(", oder weniger,", ", höchstens,"))},
                und bildet
             }
             dichte Wolken aus Ammoniakdampf.
        }

    |elif reportbody.atmosphere = "No atmosphere" || reportbody.atmosphere = "Keine Atmosphäre" || !reportbody.atmosphere:
        {Occasionally(4, OneOf("Hier gibt es keine Atmosphäre.", "Es ist keine Atmosphäre vorhanden.", "Die Jahre haben ihn von jeglicher Atmosphäre gereinigt."))}
        {if reportbody.temperature && reportAtmoTempScale < 3:
            Die Oberflächentemperatur beträgt im Durchschnitt
            {TempCalc(reportbody.temperature)} {tempscale}.
        }

    |elif reportbody.pressure: 
        {if reportbody.pressure < 0.1	: {set pressdesc to OneOf("kaum vorhandene", "super dünne", "kaum wahrnehmbare")}
        |elif reportbody.pressure < 0.25: {set pressdesc to "sehr dünne"}
        |elif reportbody.pressure < 0.5	: {set pressdesc to "dünne"}
        |elif reportbody.pressure < 2 	: {set pressdesc to "normale"}
        |elif reportbody.pressure < 10	: {set pressdesc to "dicke"}
        |elif reportbody.pressure < 50	: {set pressdesc to OneOf("dichte", "sehr dicke")}
        |elif reportbody.pressure < 100	: {set pressdesc to OneOf("sehr dichte", "super dicke")}
        |elif reportbody.pressure >= 100: {set pressdesc to OneOf("erdrückende", "super dichte")}
        }
        {if find(reportbody.atmosphere, "reich") >= 0:
            {set element to token(reportbody.atmosphere, "reich", "")}
            {set desc to OneOf("Dort findet man eine ", "Sensoren entdecken eine ", "Messgeräte erfassen eine")}
            {OneOf("{desc} {pressdesc} {reportbody.atmosphere}e Atmosphäre,", "Die {pressdesc} Atmosphäre besteht aus {element},", 
            "Die {pressdesc} Atmosphäre ist reich an {element},")}
        |elif find(reportbody.atmosphere, "dampf") >= 0:
            {OneOf("Die {pressdesc} Atmosphäre besteht aus {reportbody.atmosphere}s", "Die {pressdesc} Atmosphäre ist reich an {reportbody.atmosphere}s", 
            "Die {pressdesc} Atmosphäre enthält hohe Anteile von {reportbody.atmosphere}s")}
        |elif reportbody.planetClass.invariantName = "Earth-like world":
            {OneOf("Der Planet verfügt über eine erd,ähnliche Atmosphäre",
            "Die Atmosphäre des Planeten ist der Erde sehr ähnlich")}, 
        |else:
        	{set element to token(reportbody.atmosphere, "reich", "")}
            {set mostly to OneOf("primär", "hauptsächlich", "vorwiegend", "größtenteils", "überwiegend")}
            {OneOf("Die {pressdesc} Atmosphäre besteht {mostly} aus {element}", 
            "Vorwiegend findet man eine {pressdesc} {reportbody.atmosphere} Atmosphäre vor,",
            "Wir messen eine {pressdesc} {reportbody.atmosphere} Atmosphäre,")}
            
        }
        { if reportAtmoPressure > 0:
            mit einem Oberflächendruck von {Humanise(reportbody.pressure)} Erd Atmosphären
        }
        {if reportbody.temperature && reportAtmoTempScale < 3:
            {set temp to: {TempCalc(reportbody.temperature)}}
            {if find(temp, "-") >= 0: 
                {set temp to cat(temp*-1, tempscale, "unter Null")}
            |else:
                {set temp to cat(temp, tempscale)}
            }
            mit einer durchschnittlichen Oberflächentemperatur von {temp}
        }.
    }
}

