        }.
    }
}

Body compare.script:
 {Log("Fktn.: Body compare")}   
 
 {if reportbody.radius || reportbody.earthmass:
 	{set compare(stat) to:
    	{  if stat > 0.95 && stat < 1.05 : {OneOf("annähernd","grob","nahezu","in etwa")}
    	|elif stat > 1                   : {set value to stat} de{if compare_earthmass:r |else:m} {F("Humanise ValueGer")} fachen
    	|elif stat < 1                   : {if stat > 0.89 : {OneOf("geringfügig","nur etwas")}} weniger als
    	}
    	{  if stat < 0.01 : ein{if dativ:em} %
    	|elif stat < 0.1  : 10%
    	|elif stat < 0.2  : ein{if dativ:em} fünftel
    	|elif stat < 0.3  : ein{if dativ:em} drittel
    	|elif stat < 0.4  : 40%
    	|elif stat < 0.5  : der Hälfte
    	|elif stat < 0.6  : zwei drittel
    	|elif stat < 0.7  : 70%
    	|elif stat < 0.8  : 80%
    	|elif stat < 0.9  : 90%
    	}
    	{set dativ to []}
 	}
 }

    {_ Radius _}
    {if reportbody.radius:
        {set value to reportbody.radius}
        {if eventdata: {set value to value / 1}} 								{_ Convert radius if data is from event as this is in meters1_}
        {set compare_radius to round(value / 6371.1)} {set value to round(value,1)}
        {OneOf("{PersPron} weist einen Radius von {F('Humanise ValueGer')} Kilometern auf",
               "{OneOf('{PossArt}','{RelaPron}')} Radius beträgt {F('Humanise ValueGer')} Kilometer")}
        , was {set dativ to true} {compare(compare_radius)} {OneOf("des Radius der Erde","des Erd Radius")} entspricht
    }
    
    {_ Earth Mass _}
    {if reportbody.earthmass: {set compare_earthmass to round(reportbody.earthmass,1)}
        {if reportbody.radius : , bei {set dativ to true} 
        |else                 : {PersPron} hat
        }
        {compare(compare_earthmass)} Erd Masse.
    }

    {if reportbody.rotationalperiod && !reportbody.tidallylocked:
        {PersPron} hat eine
        {  if reportbody.rotationalperiod < -20.0 : hohe gegenläufige
        |elif reportbody.rotationalperiod <  -0.5 : gegenläufige
        |elif reportbody.rotationalperiod <   0   : verkürzt gegenläufige
        |elif reportbody.rotationalperiod <=  0.5 : schnelle
        |elif reportbody.rotationalperiod >  20.0 : langsame
        }
        Rotationszeit von
        {if round(reportbody.rotationalperiod, 1) =  1 || 
            round(reportbody.rotationalperiod, 1) = -1 : einem Tag
        |else                                          : {set value to abs(round(reportbody.rotationalperiod, 1))} {F("Humanise ValueGer")} Tagen
        }.
    }

Body desired materials.script:
{Log("Fktn.: Body desired materials")}

{_ Preferences _}
{F("A_Einstellungen")}

{_ Function to obtain a material from inventory}
{set _inventory(required) to:
    {for material in materials:
        {if material.material = required:
            {return material}
        }
    }
}

{_ Fetch from context }
{set reportbody to BodyDetails(state.eddi_context_body_name, state.eddi_context_body_system)}
{if !reportbody:
    {set reportbody to event}
}

{if !reportbody.name || reportbody.name = "":
    Ich bin {OneOf("nicht sicher","unsicher")}
    welches Objekt {OneOf("Sie meinen","gemeint ist")}.
|else:
    {set desiredmaterials to []}
    {for material in reportbody.materials:
       {set percent to material.percentage}
       {set material to MaterialDetails(material.name)}
       {set inventory to _inventory(material.name)}
       {if inventory:
           {if !state.max_materials && inventory.desired && inventory.desired > inventory.amount:
               {if !state.good_materials_included && percent >= MaterialDetails(material.name).greatpctbody:
                  {set under to inventory.desired - inventory.amount}
                  {if under >= 1 :
                      {set desiredmaterials to cat(desiredmaterials, [material])}
                  }
               |elif state.good_materials_included && percent >= MaterialDetails(material.name).goodpctbody:
                  {set under to inventory.desired - inventory.amount}
                  {if under >= 1 :
                      {set desiredmaterials to cat(desiredmaterials, [material])}
                  }
               }
           |elif state.max_materials && inventory.maximum && inventory.maximum > inventory.amount:
               {if !state.good_materials_included && percent >= MaterialDetails(material.name).greatpctbody:
                  {set under to inventory.maximum - inventory.amount}
                  {if under >= 1 :
                      {set desiredmaterials to cat(desiredmaterials, [material])}
                  }
               |elif state.good_materials_included && percent >= MaterialDetails(material.name).goodpctbody:
                  {set under to inventory.maximum - inventory.amount}
                  {if under >= 1 :
                      {set desiredmaterials to cat(desiredmaterials, [material])}
                  }
              }
           |elif inventory.maximum:
               {_ do nothing (chks if material amount is above "maximum" threshold only) _}
           |elif inventory.desired:
               {_ do nothing (chks if material amount is above "desired" threshold only) _}
           |elif inventory.minimum :
               {_ do nothing (chks if material amount is above "minimum" threshold only) _}
           |else:
               {_ do nothing (no threshold has been set in the EDDI app) _}
           }
       }
    }

    {_ Respond if materials are found which meet the desired criteria. _}

