{_ Body Name Tidy _}
{_ Tidy up body names _}
{_ If the body name contains the system name, it is removed _}
{_ Adds Star, Planet, or Moon depending on body location within the system _}
{_ Corrects pronounciation of letters in body names. No more 'hour' for 'h' with some TTS voices _}
{_ Returns 'body' for use in other scripts _}

{Log("Fktn.: Body Name Tidy")}

{if event.bodyname:
    {set body to event.bodyname}
    {set system to system.systemname}
|elif reportbody.stellarclass: 
    {set body to state.eddi_context_star_star}
    {set system to state.eddi_context_star_system}
|else:
    {set body to state.eddi_context_body_name}
    {set system to state.eddi_context_body_system}
}

{if (!find(body, system)) && (body != system):
    {set body to slice(body, len(system)+1)}

    {_ Add body type _}
    {if len(body) < 3:
        {if ord(body) > 64:
            {set body to cat("Stern ", body)}
        |else:
            {set body to OneOf(cat("Planet ", body), cat("der ", body,". Planet"))}
        }
    |elif len(body) < 5 && ord(body) > 64:
        {set body to cat("Planet ", body)}
    |elif !find(body, "Gürtel"):
        {set body to cat("Mond ", body)}
    }
}

{  if body = system:
    {set body to "Das Primärgestirn"}
|else:

   {_ exchange the last letter}

   {set idx to len(body)-1}
   {set chk to ucase(slice(body, idx-1, 1))}
   {if chk = "-" || chk = " ":
      {set chk to ucase(slice(body, idx, 1))}
      {set part to slice(body, 0, (idx))}
   
      {  if chk = "A": {set chk to "Alpha"}
      |elif chk = "B": {set chk to "Bravo"}
      |elif chk = "C": {set chk to "Charlie"}
      |elif chk = "D": {set chk to "Delta"}
      |elif chk = "E": {set chk to "Ecko"}
      |elif chk = "F": {set chk to "Foxtrott"}
      |elif chk = "G": {set chk to "Golf"}
      |elif chk = "H": {set chk to "Hotel"}
      |elif chk = "I": {set chk to "India"}
      |elif chk = "J": {set chk to "Juliett"}
      |elif chk = "K": {set chk to "Kilo"}
      |elif chk = "L": {set chk to "Lima"}
      |elif chk = "M": {set chk to "Mike"}
      |elif chk = "N": {set chk to "November"}
      |elif chk = "O": {set chk to "Oscar"}
      |elif chk = "P": {set chk to "Pappa"}
      |elif chk = "Q": {set chk to "Quebec"}
      |elif chk = "R": {set chk to "Romeo"}
      |elif chk = "S": {set chk to "Sierra"}
      |elif chk = "T": {set chk to "Tango"}
      |elif chk = "U": {set chk to "Juniform"}
      |elif chk = "V": {set chk to "Victor"}
      |elif chk = "W": {set chk to "Whiskey"}
      |elif chk = "X": {set chk to "X-ray"}
      |elif chk = "Y": {set chk to "Yankee"}
      |elif chk = "Z": {set chk to "Zulu"}
      |else          : {set chk to []}
      }

      {if chk:
          {set body to cat(part,chk)}
      }

      {if find(body, ".") > -1:
          {set dot to []}
          {set dot to split(body,".")}
          {set body to cat(dot[0]," ",dot[1]," ",dot[2])}
      }
   }
