Cargo depot.script:
{Log("EVENT: Cargo mission-depot")}

{_ Cargo depot _}
{_ Triggered when engaging with a mission depot _}
{SetState('eddi_context_last_event', 'cargo_depot')}

{if event.updatetype = "WingUpdate":
	{if event.collected > 0: Ein Geschwadermitglied hat {event.amount} Tonne{if event.amount != 1:n}
		{set translate to event.commodity} {F("Translate Commodity")} aus dem Missionslager eingeladen.
	|elif event.delivered > 0: Ein Geschwadermitglied hat {event.amount} Tonne{if event.amount != 1:n}
		{set translate to event.commodity} {F("Translate Commodity")} an das Missionslager geliefert.
	}
	
	{Pause(500)}
	{set remaining to event.totaltodeliver - event.delivered}
	{if remaining = 0: Geschwadermission ist jetzt vollständig erfüllt.
	|else: Geschwadermission ist nun zu 
		{round(event.delivered / event.totaltodeliver * 100, 0)} Prozent erfüllt.
	}

|elif event.updatetype = "Collect": Wir haben {event.amount} Tonne{if event.amount != 1:n} 
	{set translate to event.commodity} {F("Translate Commodity")} aus dem Missionslager eingeladen.
|elif event.updatetype = "Deliver": Wir haben {event.amount} Tonne{if event.amount != 1:n} 
	{set translate to event.commodity} {F("Translate Commodity")} an das Missionslager geliefert.

    {Pause(500)}
    {set remaining to event.totaltodeliver - event.delivered}
    {set haulage to HaulageDetails(event.missionid)}

    {if find(haulage.name, "Wing") > -1:   Geschwader
    |elif find(haulage.name, "Rank") > -1: Rang
    |else:                                 Einzel
    }

    {if remaining = 0 : Mission wurde erfolgreich beendet.
    |else: Mission ist jetzt zu 
    	{round(event.delivered / event.totaltodeliver * 100, 0)} Prozent abgeschlossen.
    }
}

{SetState('mission_depot', true)}

{_
amount			- The amount of cargo collected or delivered for this event 
collected 		- The total amount of cargo collected 
commodity 		- The type of cargo (commodity) 
delivered 		- The total amount of cargo delivered 
endmarketid 	- The Market ID of the 'delivery' mission depot 
missionid 		- The ID of the mission 
startmarketid 	- The Market ID of the 'collection' mission depot, 0 if not applicable 
totaltodeliver 	- The total amount of cargo to deliver to complete the mission 
updatetype 		- The update type. One of: 'Collect', 'Deliver', 'WingUpdate' 
_}

Cargo depot.defaultValue:
{if event.updatetype = "Collect":
    You have collected {event.amount} tonne{if event.amount != 1:s} of {event.commodity} from the mission depot.
|elif event.updatetype = "Deliver":
    You have delivered {event.amount} tonne{if event.amount != 1:s} of {event.commodity} to the mission depot.

    {Pause(500)}
    {set remaining to event.totaltodeliver - event.delivered}
    {set mission to MissionDetails(event.missionid)}

    {if find(mission.name, "Wing") > -1:
        Wing
    |elif find(mission.name, "Rank") > -1:
        Naval
    |else:
        Solo
    }

    {if remaining = 0:
        mission requirements are now completed.
    |else:
        mission is now {round(event.delivered / event.totaltodeliver * 100, 0)} percent complete.
    }
}

