Cargo legality check.script:
{Log("Fktn.: Cargo legality check")}

{set station to StationDetails(state.eddi_context_body_name, state.eddi_context_system_name)}

{_ Function to find a given commodity in market _}
{set market(commodity) to:
    {for marketcommodity in station.commodities:
        {if marketcommodity.EDDBID = commodity.EDDBID:
            {return marketcommodity}
        }
    }
}

{set stolencommodities to []}
{set unknowncommodities to []}

{for cargo in ship.cargo:
    {if cargo.commodity.name != 'Drohne':
        {set marketcommodity to market(cargo.commodity)}
        {_ We have no market information for this commodity.  Legal status unknown. _}
        {if !marketcommodity: {set unknowncommodities to cat(unknowncommodities, [cargo.commodity])}
        }
        {if cargo.stolen = true: {set stolencommodities to cat(stolencommodities, [cargo.commodity])}
        }
    }
}

{_ Let the user know about any stolen commodities in their hold _}
{set cargo_desc to OneOf("Fracht", "Waren")}
{set stolen to OneOf("gestohlene", "illegale")}

{if len(stolencommodities) > 0: Erinnerung: Wir
     {OneOf("haben {stolen} {cargo_desc} an Bord","transportieren {stolen} {cargo_desc}")}.
|elif len(stolencommodities) = 0 && len(unknowncommodities) > 0: Ein Teil 
	{set pref to OneOf("unserer", "Ihrer", "der")}
    {OneOf("{pref} Waren", "{pref} Güter", "{pref} Fracht")}
    im Frachtraum konnte nicht katalogisiert werden. Wir 
    {OneOf("könnten {stolen} {cargo_desc} an Bord haben",
