{Log("EVENT: Carrier jump engaged")}

{_ Context }
{SetState('eddi_context_last_subject', 'carrier jump')}
{SetState('eddi_context_last_action', 'start')}
{SetState('eddi_context_system_name', event.system)}
{SetState('eddi_context_system_system', event.system)}
{SetState('eddi_context_system_lastvisit', system.lastVisitSeconds)}

{if environment = "Docked" || status.docked = true :

    {_ Zero the 'remaining jump' context to only use values following this event _}
    {SetState('eddi_context_remaining_jumps', 0)}

    {set reportsystem to SystemDetails(event.systemname)}

	Wir {OneOf("sind", "befinden uns")} {OneOf("auf Kurs", "auf dem Weg", "auf dem Sprung")} zum System 
	{if (reportsystem.systemname) = "Shinrarta Dezhra": schin-rahta |else: {reportsystem.systemname}}
        {if len(event.shortname) > 0:
        {set reportBody to BodyDetails(event.bodyname, event.systemname)} 
        nahe
        {if len(reportBody.bodytype) > 0: {reportBody.bodytype} |else: Himmelskörper}
        {event.shortname}
    }.

    {if reportsystem.systemname = homesystem.systemname:
        Willkommen zu Hause, {F("Honorific")}
    |else:       
        {if lastsystem.allegiance && lastsystem.allegiance != "None" && lastsystem.allegiance != "Keine" 
        && lastsystem.allegiance != "Independent" && lastsystem.allegiance != "Unabhängig" 
        && (!reportsystem.allegiance || reportsystem.allegiance = "None" || reportsystem.allegiance = "Keine"
        || reportsystem.allegiance = "Independent") || reportsystem.allegiance = "Unabhängig":
            und verlassen {OneOf("jetzt", "gerade", "hiermit", "somit", "derzeit")} {lastsystem.allegiance}en Raum.
        |elif lastsystem.allegiance && lastsystem.allegiance != reportsystem.allegiance && system.allegiance 
        && system.allegiance != "None" && system.allegiance != "Keine":
            und betreten {OneOf("jetzt", "gerade", "hiermit", "somit", "derzeit")} {reportsystem.allegiance}s space.
            {if cmdr.title != "Commander" && cmdr.title != "Kommandant" : {set welcome to cat('Willkommen zurück ', F('Honorific'))}.
            }
            
        }

        {if reportsystem.visits = 0: Dies ist unser erster Besuch.
        |elif reportsystem.visits = 1: Wir haben dieses System bereits einmal besucht.
        |elif reportsystem.visits = 2: {Occasionally(2, "Dies ist unser dritter Besuch")}
        |elif reportsystem.visits = 3: {Occasionally(2, "Dies ist unser vierter Besuch.")}
        |else: Wir haben dieses System {OneOf("bereits", "schon")} {reportsystem.visits} mal besucht.
        }

        {if reportsystem.population && reportsystem.population > 0:
            {if SecondsSince(reportsystem.lastVisitSeconds) > 3600: {F("System report")}
            }
        |else: {OneOf("Dieses System ist unbesiedelt", "Registriere keinerlei Anzeichen auf humanoides Leben", 
				"Dieses  System wurde noch nicht kolonisiert")}.
        }

		{if homesystem.name = "Shinrarta Dezhra": {set homesystem to SystemDetails("Shinrarte dessra")}}

        {if reportsystem.distancefromhome:
            {set ReportDistanceFromHome() to:
                {return cat(
                	OneOf("Wir sind", "aktuelle Position,", "Wir sind jetzt"), " ",
	                Humanise(reportsystem.distancefromhome), " Lichtjahre von ",
	                OneOf("zu Hause", "{homesystem.name}"), " entfernt."
	            )}
            }
            {Occasionally(7, ReportDistanceFromHome())}
        }

        {if reportsystem.comment:
            Wir haben {OneOf("eine Notiz", "einen Vermerk", "Einen Kommentar")} {OneOf("über", "für", "zu")} diesem System.  
			{OneOf("Mit dem Inhalt:", "Der Besagt,", "lese vor:", " Der Inhalt lautet?")} {reportsystem.comment}.
        }
    }
|else:

    {OneOf('Unser Fleet Carrier', 'unser Flottenträger', carrier.name)} ist im System {P(event.systemname, "starsystem")}
    {if event.bodyname != event.systemname && len(event.shortname) > 0:
        {set reportBody to BodyDetails(event.bodyname, event.systemname)} 
        {OneOf('in dere Nähe von', 'nahe')}
        {if len(reportBody.bodytype) > 0: {reportBody.bodytype} |else: Himmelskörper}
        {P(event.shortname, "body")}
    }angekommen.

}

{_ event. xxx
bodyname 	- The name of the destination body, if any 
docked 		- True if docked with the carrier as it jumps 
shortname 	- The short name of the destination body, if any 
systemname 	- The name of the destination star system
_}

