{_ SecondsSince(state.time_start_game)}

{if SecondsSince(state.time_start_reputation) < 180 || SecondsSince(state.time_start_reputation) > 3600 : 
	{_ SecondsSince(state.time_start_reputation)} {_ Ansage der Sekunden seit Variablen-Start} 
	{SetState('time_start_reputation', SecondsSince(A))}
	
	{Pause(500)}

	{char(10)}{char(13)}Unser {OneOf("Ansehen", "Ruf", "Standing")} beim Imperium ist
	{if GetState('empire_reputation') < -90   : feindlich
	|elif GetState('empire_reputation') < -35 : unfreundlich
	|elif GetState('empire_reputation') < 4   : neutral
	|elif GetState('empire_reputation') < 35  : herzlich
	|elif GetState('empire_reputation') < 90  : befreundet
	|elif GetState('empire_reputation') > 90  : verbündet
	},
	{round(GetState('empire_reputation'), 0)} %,

	bei der Förderation
