Crime Check Ship.script:
{Log("Fktn.: Crime Check Ship")}

{_ Crime Check Ship _}
{_ Reports crimes associated only with your current ship _}

{set ship_factions to []}
{set ship_systems to []}
{set bounties_count to 0}
{set fines_count to 0}
{set ship_bounties to 0}
{set ship_fines to 0}

{for record in criminalrecord:
    {set reports to record.finesIncurred}
    {if len(reports) > 0:
    	Fraktion: {record.faction}, Geldbußen
        {for report in reports:
        {report.shipId}, {ship.LocalId}.
            {if report.shipId = ship.LocalId:
                {set ship_fines to ship_fines + report.amount}
                {set fines_count to fines_count + 1}

                {if find(ship_factions, record.faction) = -1:
                    {set ship_factions to cat(ship_factions, [P(record.faction, "faction")])}
                }
                {if find(ship_systems, record.system) = -1:
                    {set ship_systems to cat(ship_systems, [P(record.system, "starsystem")])}
                }
            }
        }
    }

    {set reports to record.bountiesIncurred}
    {if len(reports) > 0:
    	Fraktion: {record.faction}, Kopfgelder
        {for report in reports:
            {if report.shipId = ship.LocalId:
                {set ship_bounties to ship_bounties + report.amount}
                {set bounty_count to bounty_count + 1}

                {if find(ship_factions, record.faction) = -1:
                    {set ship_factions to cat(ship_factions, [P(record.faction, "faction")])}
                }
                {if find(ship_systems, record.system) = -1:
                    {set ship_systems to cat(ship_systems, [P(record.system, "starsystem")])}
                }
            }
        }
    }
}

{if bounties_count > 0 || fines_count > 0:
    {if bounties_count > 0: Achtung:
    |elif fines_count > 0: Vorsicht:
    }
    Dieses Schiff wird gesucht!

    {if len(ship_factions) = 1: von {ship_factions[0]}
    |elif len(ship_factions) > 1: von {List(ship_factions)}
    }

    {if len(ship_systems) = 1: im System {ship_systems[0]}
    |elif ship_systems > 1: in den Systemen {List(ship_systems)}
    }

    {if ship_bounties > 0 || ship_fines > 0:
        für Kopfgelder und Geldbußen in Höhe von {ship_bounties + ship_fines} credditts.
    }
|else:
    Dieses Schiff {OneOf("ist sauber.", "ist clean.", "ist unproblematisch.", "wird nicht gesucht.")}
}

Crime check station.script:
{Log("Fktn.: Crime check station")}

{_ Crime check station }
{_ Check claims, fines & bounties related to the current station }

{set CheckPad(current_station) to:
    {set largest_pad to lcase(current_station.largestpad)}
    {if largest_pad != "none":
        {if largest_pad = "large": {return true}
        |elif largest_pad = "medium":
            {if lcase(ship.size) = "large": {return false}
            |else: {return true}
			}
		}
        {if lcase(ship.size) = "small": {return true}
		}
	}
	{return false}
}

{set HasService(service) to:
	{if CheckPad(current_station):
		{if find(current_station.stationservices, service) > -1 : {return true}
		|else: {return false}
		}
	|else: {return false}
	}
}

{if state.eddi_context_body_type = 'Station' && station.name && station.name != "":
    {set current_station to StationDetails(station.name)}
    {set factions_claims to []}
    {set factions_fines to []}
    {set factions_bounties to []}
    {set station_report to false}
    {set system_claims to 0}
    {set system_fines to 0}
    {set system_bounties to 0}

	{for record in criminalrecord:
		{for faction in system.factions:
			{if record.faction = faction.name:

				{if record.claims > 0 :
					{set factions_claims to cat(factions_claims, [record.faction])}
					{set system_claims to system_claims + record.claims}
				}

				{set reports to record.finesIncurred}
				{if len(reports) > 0 :
					{for report in reports:
						{if report.shipId = ship.LocalId:
							{set system_fines to system_fines + report.amount}
							{if find(factions_fines, record.faction) < 0 :
								{set factions_fines to cat(factions_fines, [record.faction])}
							}
						}
					}
				}

				{set reports to record.bountiesIncurred}
				{if len(reports) > 0 :
					{for report in reports:
						{if report.shipId = ship.LocalId:
							{set system_bounties to system_bounties + report.amount}
							{if find(factions_bounties, record.faction) < 0 :
								{set factions_bounties to cat(factions_bounties, [record.faction])}
							}
						}
					}
				}
			}
		}
	}

	{set claim_count to len(factions_claims)}
	{set fine_count to len(factions_fines)}
	{set bounty_count to len(factions_bounties)}
    {set bounties to system_bounties > 0 && HasService("Interstellar Factors Contact")}
    {set fines to system_fines > 0 && HasService("Contacts")}
    {set claims to system_claims > 0 && HasService("Contacts")}

    {if bounties || fines || claims:
        {set actions to []}
        {if bounties:
            {set action to:
            	Einlösen
                {if bounty_count = 1: eines offenen Kopfgeldes |else: von offenen Kopfgeldern } 
            }
            {set actions to cat(actions, [action])}            
        }
        {if fines: 
            {set action to:
                Bezahlen
                {if fine_count = 1: einer offenen Geldstrafe |else: offener Geldstrafen }
            }
            {set actions to cat(actions, [action])}            
        }
        {if claims:
            {set action to:
                Einfordern
                {if claim_count = 1: einer offenen Belohnung |else: offener Belohnungen }
                für erbrachte Jagdleistungen
            }
            {set actions to cat(actions, [action])}
        }
        Auf dieser Station gibt es Einrichtungen zum {List(actions)}.
    }
}

Crime check station.defaultValue:
{_ Preferences }
{set suggestSurrender to 0} {_ set to 1 if you'd like suggestions to turn yourself in to authorities when there is a bounty on your head }

{set CheckPad(current_station) to:
    {set largest_pad to lcase(current_station.largestpad)}
    {if largest_pad != "none":
        {if largest_pad = "large":
            {return true}
        |elif largest_pad = "medium":
            {if lcase(ship.size) = "large":
                {return false}
            |else:
                {return true}
            }
        }
        {if lcase(ship.size) = "small":
            {return true}
        }
    }
    {return false}
}

{set HasService(service) to:
    {if CheckPad(current_station):
        {if find(current_station.stationservices, service) > -1:
            {return true}
        |else:
            {return false}
        }
    |else: {return false}
    }
}

{if state.eddi_context_body_type = 'Station' && station.name && station.name != "":
    {set current_station to StationDetails(station.name)}
    {set system_claim_records to []}
    {set system_claims to 0}
    {set system_fine_records to []}
    {set system_fines to 0}
    {set system_bounty_records to []}
    {set system_bounties to 0}
    {set interstellar_fine_records to []}
    {set interstellar_fines to 0}
    {set interstellar_bounty_records to []}
    {set interstellar_bounties to 0}    

    {for record in criminalrecord:
        {set factionMatched to false}
        {for faction in system.factions:
            {if record.faction = faction.name:

                {_ This record can be connected to a local faction }
                {set factionMatched to true}

                {if record.claims > 0:
                    {set system_claim_records to cat(system_claim_records, [record.faction])}
                    {set system_claims to system_claims + record.claims}
                }

                {set reports to record.finesIncurred}
                {if len(reports) > 0:
                    {for report in reports:
                        {if report.shipId = ship.LocalId:
                            {set system_fines to system_fines + report.amount}
                            {if find(system_fine_records, record.faction) < 0:
                                {set system_fine_records to cat(system_fine_records, [record.faction])}
                            }
                        }
                    }
                }

                {set reports to record.bountiesIncurred}
                {if len(reports) > 0:
                    {for report in reports:
                        {if report.shipId = ship.LocalId:
                            {set system_bounties to system_bounties + report.amount}
                            {if find(system_bounty_records, record.faction) < 0:
                                {set system_bounty_records to cat(system_bounty_records, [record.faction])}
                            }
                        }
                    }
                }
            }
        }
        {if !factionMatched:
        
            {_ This record requires interstellar factors }
            
                {set reports to record.finesIncurred}
                {if len(reports) > 0:
                    {for report in reports:
                        {if report.shipId = ship.LocalId:
                            {set interstellar_fines to interstellar_fines + report.amount}
                            {if find(interstellar_fine_records, record.faction) < 0:
                                {set interstellar_fine_records to cat(interstellar_fine_records, [record.faction])}
                            }
                        }
                    }
                }

                {set reports to record.bountiesIncurred}
                {if len(reports) > 0:
                    {for report in reports:
                        {if report.shipId = ship.LocalId:
                            {set interstellar_bounties to interstellar_bounties + report.amount}
                            {if find(interstellar_bounty_records, record.faction) < 0:
                                {set interstellar_bounty_records to cat(interstellar_bounty_records, [record.faction])}
                            }
                        }
                    }
                }
        }
    }

    {set canPaySystemBounties to system_bounties > 0 && suggestSurrender && HasService("Contacts")}
    {set canPaySystemFines to system_fines > 0 && HasService("Contacts")}
    {set canPayInterstellarBounties to interstellar_bounties > 0 && HasService("Interstellar Factors Contact")}
    {set canPayInterstellarFines to interstellar_fines > 0 && HasService("Interstellar Factors Contact")}
    {set canRedeemClaims to system_claims > 0 && HasService("Contacts")}
    
    {if canPaySystemBounties || canPaySystemFines || canRedeemClaims || canPayInterstellarBounties || canPayInterstellarFines:
        {set actions to []}
        {if canPaySystemBounties || canPayInterstellarBounties:
            {set action to:
                clear your pending
                {if canPaySystemBounties: local }
                {if canPaySystemBounties && canPayInterstellarBounties: and}
                {if canPayInterstellarBounties: interstellar }  
                {if len(system_bounty_records) + len(interstellar_bounty_records) = 1: bounty |else: bounties }
            }
            {set actions to cat(actions, [action])}            
        }
        {if canPaySystemFines || canPayInterstellarFines: 
            {set action to:
                clear your pending
                {if canPaySystemFines: local }
                {if canPaySystemFines && canPayInterstellarFines: and}
                {if canPayInterstellarFines: interstellar }                
                {if len(system_fine_records) + len(interstellar_fine_records) = 1: fine |else: fines }
            }
            {set actions to cat(actions, [action])}            
        }
        {if canRedeemClaims:
            {set action to:
                claim your pending
                {if len(system_claim_records) = 1: reward |else: rewards }
                for hunting services rendered
            }
            {set actions to cat(actions, [action])}
        }
        There are facilities at this station to {List(actions)}.
    }
}

Crime check system.script:
{Log("Fktn.: Crime check system")}

{_ Crime check system }
{_ Check claims, fines & bounties related to the current system }

{set CheckPad(station) to:
    {set largest_pad to lcase(station.largestpad)}
    {if largest_pad != "none" || largest_pad != "Kein":
        {if largest_pad = "large" || largest_pad != "Groß": {return true} }
        |elif largest_pad = "medium" || largest_pad != "Mittel":
            {if lcase(ship.size) = "large" || lcase(ship.size) = "Groß": {return false}
            |else: {return true}
			}
			{if lcase(ship.size) = "small" || lcase(ship.size) = "Klein": {return true}
		}
		{return false}
	}

	{set NearestStation(service) to:
		{set distance to 0}
		{set station_count to 0}
		{set orbital_count to 0}

    {_ If preferred & available, use orbital stations }
		{if orbitalpriority:
			{for station in system.orbitalstations:
				{if find(station.stationservices, service) > -1 :
					{set orbital_count to orbital_count + 1}
				}
			}
		}

    {_ Force orbital stations when game version is not 'Horizons' }
		{if orbital_count > 0 || !horizons : {set stations to system.orbitalstations}
		|else:	{set stations to system.stations}
		}

    {_ Get the total count and nearest station }
		{for station in stations:
			{if CheckPad(station):
				{if find(station.stationservices, service) > -1 :
					{set station_count to station_count + 1}
					{if distance = 0 || station.distancefromstar < distance:
						{set distance to station.distancefromstar}
						{set nearest_station to station.name}
					}
				}
			}
		}
	}

	{set factions_claims to []}
	{set factions_fines to []}
	{set factions_bounties to []}
	{set station_report to false}
	{set system_claims to 0}
	{set system_fines to 0}
	{set system_bounties to 0}
	{set wanted_count to 0}

	{for record in criminalrecord:
		{for faction in system.factions:
			{if record.faction = faction.name:
				{if record.claims > 0 :
					{set factions_claims to cat(factions_claims, [record.faction])}
					{set system_claims to system_claims + record.claims}
				}

				{set reports to record.finesIncurred}
				{if len(reports) > 0 :
					{for report in reports:
						{if report.shipId = ship.LocalId:
							{set system_fines to system_fines + report.amount}
							{if find(factions_fines, record.faction) < 0 :
								{set factions_fines to cat(factions_fines, [record.faction])}
								{if find(factions_bounties, record.faction) < 0 :
									{set wanted_count to wanted_count + 1}
								}
							}
						}
					}
				}

				{set reports to record.bountiesIncurred}
				{if len(reports) > 0 :
					{for report in reports:
						{if report.shipId = ship.LocalId:
							{set system_bounties to system_bounties + report.amount}
							{if find(factions_bounties, record.faction) < 0 :
								{set factions_bounties to cat(factions_bounties, [record.faction])}
								{if find(factions_fines, record.faction) < 0 :
									{set wanted_count to wanted_count + 1}
								}
							}
						}
					}
				}
			}
		}
	}

	{set claim_count to len(factions_claims)}
	{set fine_count to len(factions_fines)}
	{set bounty_count to len(factions_bounties)}

	{if wanted_count > 0 :
		Warnung: {ShipName()} wird gesucht von {wanted_count}
		{if wanted_count = 1 : Fraktion |else: Fraktionen},
		für eine Gesammtsumme von {Humanise(system_fines + system_bounties)} credditts.
	}

{_ Report nearest station to clear bounties }
	{if system_bounties > 0 :
		{NearestStation("Interstellar Factors Contact")}
		{if station_count > 0 :
			In diesem system gibt es {if station_count = 1 : Eine |else: {station_count}} 
			{if orbitalpriority: orbital}
			{if station_count = 1 : station |else: stationen} um die offenen Kopfgelder zu bezahlen.
			{nearest_station} ist die mit der geringsten Entfernung. Sie ist cirka
			{round(distance, 1)} Lichtsekunden vom Hauptstern entfernt.
			{set station_report to true}
		}
	}

	{if system_fines > 0 && !station_report:
		{NearestStation("Contacts")}
		{if station_count > 0 :
			Hier gibt es {if station_count = 1 : Eine |else: {station_count}} 
			{if orbitalpriority: orbital}
			{if station_count = 1 : station |else: stationen} um die offenen Geldbußen zu bezahlen.
			{nearest_station} ist die mit der geringsten Entfernung. Sie ist cirka
			{round(distance, 1)} Lichtsekunden vom Hauptstern entfernt.
			{set station_report to true}
		}
	}

{_ Report nearest station for pending claims }
	{if system_claims > 0 :
		Wir haben Kopfgeldansprüche in diesem System bei {if claim_count = 1 : einer |else: {claim_count}}
		{if claim_count = 1 : Fraktion |else: Fraktionen}, über cirka {Humanise(system_claims)} credditts.

		{if !station_report:
			{if system_fines = 0 : {NearestStation("Contacts")}}
			{if station_count > 0 :
				Hier gibt es {if station_count = 1 : Eine |else: {station_count}} 
				{if orbitalpriority: orbital}
				{if station_count = 1 : station |else: stationen} um die offenen {OneOf("Ansprüche einzulösen", "Belohnungen abzuholen")}.
				{nearest_station} ist die mit der geringsten Entfernung. Sie ist cirka
				{round(distance, 1)} Lichtsekunden vom Hauptstern entfernt.
				{set station_report to true}
			}
		}
	}
}

Crime check system.defaultValue:
{set system_claim_records to []}
{set system_claims to 0}
{set system_fine_records to []}
{set system_fines to 0}
{set system_bounty_records to []}
{set system_bounties to 0}
{set interstellar_fine_records to []}
{set interstellar_fines to 0}
{set interstellar_bounty_records to []}
{set interstellar_bounties to 0}
{set wanted_faction_count to 0}

{for record in criminalrecord:
    {set factionMatched to false}
    {for faction in system.factions:
        {if record.faction = faction.name:

            {_ This record pertains to a faction which is present in the system }
            {set factionMatched to true}
                         
            {if record.claims > 0:
                {set system_claim_records to cat(system_claim_records, [record.faction])}
                {set system_claims to system_claims + record.claims}
            }

            {set reports to record.finesIncurred}
            {if len(reports) > 0:
                {for report in reports:
                    {set system_fines to system_fines + report.amount}
                    {if find(system_fine_records, record.faction) < 0:
                        {set system_fine_records to cat(system_fine_records, [record.faction])}
                        {if find(system_bounty_records, record.faction) < 0:
                            {set wanted_faction_count to wanted_faction_count + 1}
                        }
                    }
                }
            }

            {set reports to record.bountiesIncurred}
            {if len(reports) > 0:
                {for report in reports:
                    {set system_bounties to system_bounties + report.amount}
                    {if find(system_bounty_records, record.faction) < 0:
                        {set system_bounty_records to cat(system_bounty_records, [record.faction])}
                        {if find(system_fine_records, record.faction) < 0:
                            {set wanted_faction_count to wanted_faction_count + 1}
                        }
                    }
                }
            }
        }
    }
    
    {if !factionMatched:

        {_ This record pertains to a faction not present in the system }
        {set reports to record.finesIncurred}
        {if len(reports) > 0:
            {for report in reports:
                {set interstellar_fines to interstellar_fines + report.amount}
                {if find(system_fine_records, record.faction) < 0:
                    {set interstellar_fine_records to cat(interstellar_fine_records, [record.faction])}
                }
            }
        }
        {set reports to record.bountiesIncurred}
        {if len(reports) > 0:
            {for report in reports:
                {set interstellar_bounties to interstellar_bounties + report.amount}
                {if find(system_bounty_records, record.faction) < 0:
                    {set interstellar_bounty_records to cat(interstellar_bounty_records, [record.faction])}
                }
            }
        }
    }    
}

{if wanted_faction_count > 0:
    {if system_bounties > 0:
        {OneOf("Warning", "Caution", "Alert")}:
        You are wanted in this system by {wanted_faction_count}
        {if wanted_faction_count = 1: faction |else: factions},
        for total
        {if system_fines > 0:
            fines and bounties
        |else:
            bounties
        }
    |else:
        Alert: You owe {wanted_faction_count}
        {if wanted_faction_count = 1: faction |else: factions} in this system,
        for fines 
    }
    of {Humanise(system_fines + system_bounties)} credits
}
{if len(system_claim_records) > 0:
    {if wanted_faction_count > 0: and |else: You}
    have earned {if len(system_claim_records) = 1: a reward |else: rewards} 
    of {Humanise(system_claims)} credits
    for local hunting services rendered
}

