Damage check.script:
{Log("Fktn.: Damage check")}

{_ Track if there is any damage_ }
{set damaged to 0}

Zustandsbericht unseres Schiffes,

{Pause(1000)}

{_ Announce hull health}
{if ship.health < 98:
    Schiffs Hülle bei {round(ship.health, 0)}%.
    {set damaged to 1}
}

{_ Announce critical module health _}

{set minordamage to []}
{set majordamage to []}

{  if ship.powerplant.health <  60: {set majordamage to cat(majordamage, [ship.powerplant])}
|elif ship.powerplant.health < 100: {set minordamage to cat(minordamage, [ship.powerplant])}
}
{  if ship.thrusters.health <  60: {set majordamage to cat(majordamage, [ship.thrusters])}
|elif ship.thrusters.health < 100: {set minordamage to cat(minordamage, [ship.thrusters])}
}
{  if ship.frameshiftdrive.health <  60: {set majordamage to cat(majordamage, [ship.frameshiftdrive])}
|elif ship.frameshiftdrive.health < 100: {set minordamage to cat(minordamage, [ship.frameshiftdrive])}
}
{  if ship.powerdistributor.health <  60: {set majordamage to cat(majordamage, [ship.powerdistributor])}
|elif ship.powerdistributor.health < 100: {set minordamage to cat(minordamage, [ship.powerdistributor])}
}
{  if ship.lifesupport.health <  60: {set majordamage to cat(majordamage, [ship.lifesupport])}
|elif ship.lifesupport.health < 100: {set minordamage to cat(minordamage, [ship.lifesupport])}
}
{  if ship.sensors.health <  60: {set majordamage to cat(majordamage, [ship.sensors])}
|elif ship.sensors.health < 100: {set minordamage to cat(minordamage, [ship.sensors])}
}

{if len(majordamage) > 0: {set damaged to 1} Beträchtlicher Schaden an
    {  if len(majordamage) = 1: {majordamage[0].name}
    |elif len(majordamage) = 2: {majordamage[0].name} und {majordamage[1].name}
    |else                     : {set cur to 0}
        {while cur < len(majordamage):
            {if cur = 0                     :       {majordamage[cur].name}
            |elif cur < len(majordamage) - 1:     , {majordamage[cur].name}
            |else                           : , und {majordamage[cur].name}
            }
            {set cur to cur + 1}
        }
    }.
}

{if len(minordamage) > 0: {set damaged to 1} Geringer Schaden an
    {  if len(minordamage) = 1: {minordamage[0].name}
    |elif len(minordamage) = 2: {minordamage[0].name} und {minordamage[1].name}
    |else                     : {set cur to 0}
        {while cur < len(minordamage):
            {if cur = 0                     :       {minordamage[cur].name}
            |elif cur < len(minordamage) - 1:     , {minordamage[cur].name}
            |else                           : , und {minordamage[cur].name}
            }
            {set cur to cur + 1}
        }
    }.
}

{_ Announce other module health}

{set minordamage to []}
{set majordamage to []}

{for compartment in ship.compartments:
    {  if compartment.module.health <  60: {set majordamage to cat(majordamage, [compartment.module.name])}
    |elif hardpoint.module.health   < 100: {set minordamage to cat(minordamage, [compartment.module.name])}
    }
}

{for hardpoint in ship.hardpoints:
    {  if hardpoint.module.health <  60: {set majordamage to cat(majordamage, [hardpoint.module.name])}
    |elif hardpoint.module.health < 100: {set minordamage to cat(minordamage, [hardpiont.module.name])}
    }.
}

{if len(majordamage) > 0: {set damaged to 1}
    {  if len(majordamage) = 1: Eine weitere Schiffs Komponente mit beträchtlichem Schaden.
    |elif len(majordamage) > 1: und {len(majordamage)} weitere Schiffs Komponenten mit beträchtlichem Schaden.
    }
