Destination arrived.script:
{Log("EVENT: Destination arrived / SuperCruiseDestinationDrop")}

{SetState('eddi_context_last_action', 'destination_arrived')}

{if event.name = carrier.name :
    Willkommen zurück auf {OneOf("unserem Flottenträger", carrier.name)}, 
    {F("Honorific")}.
|else:
    {set reportSystem to SystemDetails(system.systemname)}
    {if find(reportSystem.carriersignalsources, event.name) > -1:
        {Occasionally(2, "Der")} Flottenträger {event.name}
        {OneOf(
            "{OneOf('befindet sich', 'kreist')} hier",
            "{OneOf('kreist', 'befindet sich')} an dieser Position",
            "{OneOf('ist hier', 'ist auf dieser Position')}
            im Orbit")}.
    |else:
        {set entering to OneOf("Ankunft", "Eintritt in den Normalraum,")}
        {set warninglevel to:
            {if event.threat < 2:
                {Pause(100)} {entering}
            |elif event.threat = 2:
                Achtung: {entering} in der Nähe von einer durchschnittlichen Bedrohung,
            |elif event.threat = 3:
                Warnung: {entering} in der Nähe von einer hohen Bedrohung,
            |elif event.threat = 4:
                Gefahr: {entering} in der Nähe von einer sehr hohen Bedrohung,
            |elif event.threat = 5:
                Extreme Gefahr: sehr hohe Bedrohung {Occasionally(2, "in der Nähe")},
            |elif event.threat = 6:
                Extreme Gefahr: extrem hohen Bedrohung {Occasionally(2, "in der Nähe")},
            |elif event.threat = 7:
                Extreme Gefahr: ultra hohen Bedrohung {Occasionally(2, "in der Nähe")},
            |else:
                Extreme Gefahr: einer noch nie dagewesenen Bedrohung {Occasionally(2, "in der Nähe")}, Level {event.threat}, 
            }
        }
        
        {warninglevel} {_ event.name}
        
        {if event.isSignalSource:  
            {if find(event.name, "Rescue") > -1 : {set translate to event.name} {F("Translate Stationsnamen")} {state.station_name}
            |elif find(event.invariantName, "Colonisation") > -1 : in der Nähe vom Kolonisationsschiff, 
            |else: bei der Signalquelle {event.name}
            }       
        |else: 
            {if find(event.invariantName, "Colonisation") >-1  : nahe dem Kolo-schiff,
            |elif event.name = "Jameson Memorial": Dschejmessen Mimorriel
            {_ Abfrage auf Kolonisationsschif nur so lange das Kolo.-schiff in EDDI noch nicht implementiert is}
            |elif find(system.signalsources, "Besiedelungs-Boje") >-1 && event.name = "Signalquelle": in der Nähe vom Kolonisationsschiff,
            |elif find(event.invariantName, "Orbital Construction Site:") > -1 :
                {OneOf("nahe der", "in der Nähe der")}
                {OneOf("orbitalen Bau-Stelle", "Orbital-Bau-stelle")} {token(event.invariantName, "Site:", 1)}
            |elif find(event.invariantName, "Planetary Construction Site:") > -1 :
                {OneOf("nahe der", "in der Nähe der")}
                {OneOf("planetaren Bau-Stelle", "Planeten-Bau-stelle")} {token(event.invariantName, "Site:", 1)}            
            |else:
                {if state.eddi_context_landing_pad_station:
                    {if find(event.name, "Station") > -1 :
                        {if event.name = "Jameson Memorial": Dschejmessen Mimorriel
                        |else:
                            bei {set translate to event.name} {F("Translate Stationsnamen")} {event.station.model} {state.station_name}.
                        }
                    |else:
                        bei der Station {event.name}
                    }
                |else:
                    bei der Station {event.name}
                }    
            }
        }
        {if system.state = "Anarchy" || system.state = "Anarchie" &&
        event.name = "Degraded Emissions" || event.name = "Degradierte Emissionen" && system.population > 0: 
            {Pause(2000)}
            {set local to OneOf('lokalen', 'nahen')}
            {set chatter to OneOf('Geschwätz', 'Klatsch', 'Gerüchte', 'Tratsch')}
            {set indicate to OneOf('suggerieren', 'implizieren', 'berichten')}
            {Occasionally(2, "Vorsicht:
                {OneOf('Übertragungen {indicate}', '{chatter} in {local} Übertragungen {indicate}')} erhöhte
                {OneOf('Aktivitäten', 'Präsens')} von
                {OneOf('kriminellen Syndikaten', 'Piraten', 'kriminellen Banden', 'Clans')} in
                {OneOf('diesem Bereich', 'diesem Areal', 'diesem Sektor', 'diesem System')}.
                Bleiben Sie {OneOf('wachsam', 'aufmerksam', 'cool')} 
                {Occasionally(3,' {F(\'Honorific\')}' )}.
           ")}
        }
    }
}

{_ für Tests 
char(10) Name: event.name
char(10) Invariant: event.invariantName
char(10) is SignalSource: if event.isSignalSource: event.isSignalSource else: leer
char(10) Stationsmodell: if event.station.model: event.station.model else: leer }

{_
=Event-Variablen=
invariantName   - The invariant name of the destination location 
isSignalSource  - True if the destination is a signal source 
name            - The name of the destination location, localized when applicable 
threat          - The threat level at the destination location (0 is lowest) (typically only used for unidentified signal sources) 
_}

Destination arrived.defaultValue:
{if event.name = carrier.name:
    Welcome back to 
    {OneOf("your carrier", carrier.name)}, 
    {F("Honorific")}.
|else:

    {set reportSystem to SystemDetails(system.systemname)}
    {if find(reportSystem.carriersignalsources, event.name) > -1:
        {Occasionally(2, "The")} Fleet Carrier
        {event.name} is
        {OneOf("{OneOf('located', 'orbiting', 'stationed')} here",
            "{OneOf('orbiting', 'stationed')} at this location",
            "in orbit {OneOf('here', 'at this location')}")}.
    |else:
        {set entering to cat(OneOf("Arriving ", "Dropping "), OneOf("near ", "at "))}
        
        {set warninglevel to:
            {if event.threat < 2:
                {entering}
            |elif event.threat = 2:
                Caution: {entering} medium threat
            |elif event.threat = 3:
                Warning: {entering} high-threat
            |elif event.threat = 4:
                Danger: {entering} very high-threat
            |elif event.threat = 5:
                Extreme Danger: {Occasionally(2, entering)} very high-threat
            |elif event.threat = 6:
                Extreme Danger: {Occasionally(2, entering)} extremely high-threat
            |elif event.threat = 7:
                Extreme Danger: {Occasionally(2, entering)} ultra high-threat
            |else:
                Extreme Danger: {Occasionally(2, entering)} Unprecedented level {event.threat} threat
            }
        }
        
        {warninglevel} {event.name}
        {if event.isSignalSource:
            signal source
        }.
        
        {if system.state = "Anarchy" && event.name = "Degraded Emissions" && system.population > 0: 
            {Pause(2000)}
            {Occasionally(2, "
