                {OneOf('activity', 'presence')}
                in {OneOf('the area', 'the vicinity', 'this sector', 'this system')}.
                {OneOf('Remain', 'Stay', 'Keep')} {OneOf('vigilant', 'alert', 'frosty', 'sharp')} 
                {Occasionally(3,' {F(\"Honorific\")}' )}.
            ")}
        }
    }
}

Died.script:
{Log("EVENT: Died")}

{if len(event.ships) > 0:

    {set commanders to []}
    {for cmdr in event.commanders:
        {if find(cmdr, "$ShipName") >= 0:
           {_ Ignore NPC ships _}        
        |else: 
           {set commanders to cat(commanders, [cmdr])}
        }
    }

    {ShipName()} wurde
    {Occasionally(2, "gerade")}
    {OneOf("weggeblasen", "terminiert", "zerstört")}
    
    {if len(commanders) = 1:
        von {commanders[0]} 
        in einem Schif vom Typ {event.ships[0]} 
        mit einem Kampfrang von {event.ratings[0]}.
    |elif len(commanders) = 2:
        von {commanders[0]} 
        in einem Schif vom Typ {event.ships[0]} 
        mit einem Kampfrang von {event.ratings[0]}, 
        und von {commanders[1]} 
        in einem Schif vom Typ {event.ships[1]}
        mit einem Kampfrang von {event.ratings[1]}.
    |elif len(commanders) > 2:
        {set cur to 0}
        von
        {while cur < len(commanders):
            {if cur = 0:
                {commanders[cur]} 
                in einem Schif vom Typ {event.ships[cur]} 
                mit einem Kampfrang von {event.ratings[cur]}
            |elif cur < len(commanders) - 1:
                , {commanders[cur]} 
                in einem Schif vom Typ {event.ships[cur]} 
                mit einem Kampfrang von {event.ratings[cur]}
            |else:
                , und {commanders[cur]} 
                in einem Schif vom Typ {event.ships[cur]} 
                mit einem Kampfrang von {event.ratings[cur]}.
            }
            {set cur to cur + 1}
        }
    }
}

{_
- commanders  The names of the commanders who killed you
- ships       The ships the commanders who killed you were flying
- ratings     The ratings of the commanders who killed you
neu
killers - A list of objects describing your killers 
killers[<index>].equipment 
killers[<index>].name 
killers[<index>].rating
_}

