    {set scanned_bodies to 0}
    {set body_stern to 0}
    {set stern_scanned to 0}
    {set alreadydiscovered to 0}
    {set already_stern to 0}
        
    {for body in SystemDetails(system.systemname).bodies: {_ zählt nur schon entdeckte Körper ! }
        
        {if body.bodytype = "Stern":
            {set body_stern to body_stern + 1}
            {if body.alreadydiscovered: {set already_stern to already_stern + 1}}
            {if body.scanned:
                {set stern_scanned to stern_scanned +1}}
            {if body.mainstar:
               {if !body.alreadydiscovered && state.eddi_context_star_scanned = "Autoscan":
                   {set system_entdeckt to 0} {_ system unentdeckt }
               |else:
                   {set system_entdeckt to 1} {_ system bekannt }
               }
            }       
        |else:
            {if body.scanned: {set scanned_bodies to scanned_bodies + 1}}
            {if body.alreadydiscovered: {set alreadydiscovered to alreadydiscovered + 1}}
        }    
    }    
}

{Pause(500)}

{if event.totalbodies = body_stern: Wir haben alle Körper entdeckt, {SetState('system_nur_sonnen', true)}
    {if body_stern = 1  : Nur ein Stern im System.
    |else               : Nur {body_stern} Sterne im System.}
|elif event.totalbodies = body_stern + scanned_bodies :
    Alle Himmelskörper sind bekannt. {F("System scan complete")}
|else:

    {if alreadydiscovered = event.totalbodies: {_ set system_entdeckt to 2} {_ system komplett erforscht }
        {Occasionally(2, 'Das')} System wurde schon {Occasionally(5, 'von einem anderen Commander')} komplett entdeckt,}

    {if len(reportSystem.bodies) < event.totalbodies: {_ set system_entdeckt to 1} {_ system bekannt und nur teilweise erforscht }
    |elif len(reportSystem.bodies) = body_stern && len(reportSystem.bodies) < event.totalbodies:
    es können noch {event.totalbodies - len(reportSystem.bodies)} Körper gescannt werden,
    }

    {if body_stern >= 10 :
        Anzahl Signalquellen = {event.nonbodies}
        system totalboddies  = {if !event.totalbodies : {system.totalbodies} |else: {event.totalbodies} },
        schon gescannte Körper = {system.scannedbodies},
        {_ Anzahl Körper über L E N = len(reportSystem.bodies)}
        Anzahl Sterne = {body_stern},
        Anzahl schon entdeckter Sterne = {already_stern},
        Anzahl gescannter Sterne = {stern_scanned},
        Anzahl gescannter Körper = {scanned_bodies},
        schon entdeckte Körper = {alreadydiscovered},
        {_ zu entdeckende Körper = system.totalbodies - body_stern - alreadydiscovered }
    }

    {set bodiesToScan to (system.totalbodies - already_stern - scanned_bodies)}
    {_ len(SystemDetails(system.name).bodies)}

    {if event.totalbodies = body_stern :
        {if body_stern = 1  : Nur ein Stern im System.
        |else               : Nur {body_stern} Sterne im System.}
    }

    {_ Alte Ansagen ab hier,}

    {if system_entdeckt = 0 : {OneOf("Achtung! Wir haben ein System entdeckt, dass bei 
        Juniversl, Carto graphiks noch nicht gelistet ist!", 
        "Aber Hallo! Wir haben ein neues System entdeckt!")}}

    {if event.totalbodies > 0 :
        {if event.totalbodies = body_stern :
            {if event.totalbodies = 1 : Es wurde ein stellares Signal 
            |else: Es wurden {event.totalbodies} Signale von stellaren Objekten
            } im System {OneOf("ge ortet,", "gefunden,", "entdeckt,", "aufgespürt,","ausfindig gemacht,","aufgeklärt,","sondiert,","erkundet,", "ermittelt,")}
        |else: 
            Wir haben {event.totalbodies} Signale von stellaren und planetaren Objekten im System 
            {OneOf("entdeckt,","aufgespürt,","ausfindig gemacht,","aufgeklärt,","sondiert,","erkundet,", "ermittelt,", "ge ortet,", "gefunden,")}
        }
    }

    {if system_entdeckt = 2 :
        {if reportSystem.visits > 10 :
        |else: 
            {OneOf("das System ist allgemein bekannt,", 
                   "das System ist bei Juniversl, Carto graphiks eingetragen,",
                   "das System ist bei UC gelistet,")} 
            {if reportSystem.population < 1 :
                {OneOf("eigentlich ab zur NAV-Boje, aber hier wohnt ja niemand!",
                       "aber unbewohnt, daher keine NAV-Boje zum Datenabruf vorhanden")} 
            |else:
                {Occasionally(2, "hier hilft der VSS nicht,")} 
                {OneOf("ab zur NAV-Boje", "Datenabruf an der NAV-Boje,",
                       "für Informationen über das System bitte die Nav-Boje scannen")} }            
        }
    |else:
        {if system_entdeckt = 0: 
            {if   bodiesToScan = 0 :                                         {_ -zu entdecken- rausgenommen}
            |elif bodiesToScan = 1 : Wir können noch {OneOf("einen Körper", "ein Objekt")} 
            |elif bodiesToScan > 1 : Wir können noch {bodiesToScan} {OneOf("Körper", "Objekte")} 
            }
            als Erster {OneOf("entdecken,", "scannen,")}
            
        |elif  system_entdeckt = 1 :
            {if bodiesToScan = 1 : Wir können noch {OneOf("einen Körper", "ein Objekt")} im System
                                                     {OneOf("scannen,", "entdecken,")}
            |elif bodiesToScan > 1 : Wir können noch {bodiesToScan} {OneOf("Körper", "Objekte")}
                                                     {OneOf("scannen,", "entdecken,")}}
        }
    }
}

{SetState('bodies_to_scan', bodiesToScan)}
{SetState('bodies_to_count', bodiesToScan - 1 )}

{_ erst nachdem FSS/VSS sind alle "Bodies" (Körper) bekannt, vorher nur ihre Anzahl !!! }

{_ if body_stern = system.totalbodies:
   else:
    if system.totalbodies >= body_stern + ScannedBodies : F("Bodies to map")
}

{_
nonbodies - the number of non-body signals !!! gibt es scheinbar nicht mehr !!! 
totalbodies - the total number of discoverable bodies within the system
}

Discovery scan.defaultValue:
{set reportMaterials to true}   {_ If true, recommends material collection locations using the `System materials report` after we have data on all system bodies and when the commander has an SRV _}

{_ Fetch from Context, if a context is set _}
{if state.eddi_context_system_address:
    {set reportSystem to SystemDetails(state.eddi_context_system_address)}
|else:
    {set reportSystem to SystemDetails(system.systemAddress)}
}

{if !reportSystem.name || reportSystem.name = "":
    I'm not sure which system you are asking about.
|else:
    {for compartment in ship.compartments:
        {if compartment.module.invariantName = "Planetary Vehicle Hangar":
            {set hasSRV to true}
        }
    }
    {if reportMaterials && hasSRV && reportSystem && reportSystem.totalbodies <= len(reportSystem.bodies):
        {set materialsReport to F("System materials report")}
        {if materialsReport:
            {materialsReport}
        }
    }
    {if ship.Role.edname = "Exploration" || 
        (ship.Role.edname = "Multipurpose" && len(reportSystem.factions) = 0):

        {if reportSystem:
        	{set reportedBodies to len(reportSystem.bodies)}
            {if 0 < reportedBodies && reportedBodies < event.totalbodies:
                {Pause(1000)}

                {set scannedBodies to 0}
                {for body in reportSystem.bodies:
                    {if body.scanned:
                        {set scannedBodies to scannedBodies + 1}
                    }
                }
                {set bodiesToScan to (event.totalbodies- scannedBodies)}

                {if bodiesToScan = 1:
                	One body remains to be scanned
                |elif scannedBodies < event.totalbodies:
                    {OneOf(
                        "{scannedBodies} out of {event.totalbodies} bodies {if scannedBodies != 1: have been scanned |else: has been scanned}", 
                        "{bodiesToScan} bodies remain to be scanned"
                    )}
                    {Occasionally(3, OneOf("here", "in this system"))}.
                }
            |else:
                {set bodiesToMap to F("Bodies to map")}
                {if bodiesToMap:
                    {bodiesToMap}
                }
            }
        }
    }
}

Disembark.script:
{Log("EVENT: Disembark")}

{if event.fromship          : {SetState('eddi_context_last_action', 'disembark from ship')} {_ zu Fuß vom Schiff }
|elif event.fromsrv         : {SetState('eddi_context_last_action', 'disembark from srv')}
|elif event.onplanet        : {SetState('eddi_context_last_action', 'disembark on planet')}
|elif event.onstation       : {SetState('eddi_context_last_action', 'disembark on station')}
|elif event.fromtransport   : {SetState('eddi_context_last_action', 'disembark from transport')}
|elif event.frommulticrew   : {SetState('eddi_context_last_action', 'disembark from multicrew')}
|else                       : {SetState('eddi_context_last_action', 'disembark')}
}

{set reportTemperature to 1}   {_ if truthy, report the external temperature (for on-foot expeditions) _}
{set reportTempScale to 1}     {_ Set to 0 for temperatures in Kelvin _}
                               {_ Set to 1 for temperatures in Celsius _}
                               {_ Set to 2 for temperatures in Fahrenheit _}
                               
{set reportBody to BodyDetails(state.eddi_context_body_name, state.eddi_context_system_name)}
{if !reportBody: {set reportBody to BodyDetails(event.bodyname, event.systemname)}}
{_ set reportBody to BodyDetails(event.bodyname, event.systemname) _}

{set surfacetemp to reportBody.temperature}

{if reportTempScale = 0:
    {set surfTemp to round(surfacetemp, 0)}
    {set tempScale to " Kelvin "}
|elif reportTempScale = 1:
    {set surfTemp to round(surfacetemp - 273.15 , 0)}
    {set tempScale to " Grad Celsius "}
|elif reportTempScale = 2:
    {set surfTemp to round((surfacetemp - 273.15) * 9 / 5 + 32 , 0)}
    {set tempScale to " Grad Fahrenheit "}
}

{_ Wir befinden uns auf reportBody.bodytype state.eddi_context_body_name im System state.eddi_context_system_name _}

{Pause(4000)}

{if reportTemperature && odyssey && state.eddi_context_touchdown_body_name != event.bodyname:
    {_ Convert to desired temperature scale _}


    {_ Report dangerous surface temperatures for on-foot expeditions _}
    {set hazardousHighTemp to surfacetemp > 426}
    {set hazardousLowTemp to surfacetemp < 150}
    {_ if hazardousHighTemp  hazardousLowTemp
        Warnung Dieser reportBody.bodytype hat eine OneOf("gefährlich", "bedrohlich", "bedenklich")
        if hazardousHighTemp hohe
        else niedrige  Occasionally(3, "durchschnittliche")
        OneOf("Oberflächen-", "Umgebungs-") Temperatur von surfTemp tempScale.
    _}
|else: }

{_ Temperatur= surfacetemp -zum Test-_}

{if event.onplanet:
    {if reportBody.temperature = "void" || !reportBody.temperature: keine Temperaturwerte bekannt !
    |else:
        {if surfacetemp < 173 :
            Warnung: {OneOf("gefährlich", "bedrohlich", "bedenklich")} niedrige
            {OneOf("Oberflächen-", "Umgebungs-")}Temperatur von {surfTemp} {tempScale}
            {OneOf("festgestellt", "erkannt", "gemessen", "ermittelt", "registriert")}.
            Umweltsysteme auf Maximum einstellen. Minimale Aufenthaltsdauer empfohlen!
        |elif surfacetemp > 173 && surfacetemp < 273 :
            Vorsicht: Niedrige {OneOf("Oberflächen-", "Umgebungs-")}Temperatur von {surfTemp} {tempScale}
            {OneOf("festgestellt", "erkannt", "gemessen", "ermittelt", "registriert")}. 
            Anzugsysteme kompensieren.
        |elif surfacetemp > 273 && surfacetemp < 323 :
            Normale {OneOf("Oberflächen-", "Umgebungs-")}Temperatur von {surfTemp} {tempScale} 
            {OneOf("festgestellt", "erkannt", "gemessen", "ermittelt", "registriert")}.
            Anzugsysteme auf Minimum stellen.        
        |elif surfacetemp > 323 && surfacetemp < 423 :
            Vorsicht: Hohe {OneOf("Oberflächen-", "Umgebungs-")}Temperatur von {surfTemp} {tempScale} 
            {OneOf("festgestellt", "erkannt", "gemessen", "ermittelt", "registriert")}.
            Anzugsysteme kompensieren.
        |elif surfacetemp > 423 :
    	   Warnung! {OneOf("gefährlich", "bedrohlich", "bedenklich")} hohe
            {OneOf("Oberflächen-", "Umgebungs-")}Temperatur von {surfTemp} {tempScale}
            {OneOf("festgestellt", "erkannt", "gemessen", "ermittelt", "registriert")}.
 		 Schutzanzugsysteme auf Maximum. Minimale Aufenthaltsdauer empfohlen!
        }
    }
}

