Entered normal space.script:
{Log("EVENT: Entered normal space / SupercruiseExit")} 

{_ Context }
{SetState('eddi_context_last_subject', 'fsd')}
{SetState('eddi_context_last_action', 'disengage')}
{SetState('eddi_context_system_name', event.systemname )}
{SetState('eddi_context_body_name', event.bodyname)}
{SetState('eddi_context_body_type', event.bodytype)}
{SetState('eddi_context_body_system', event.systemname)}
{SetState('station_model_normalspace', 'no station')}

{set reportBody to BodyDetails(event.bodyname, event.systemname)}

{set station_notifications to:
    {set station_missions to F("Mission check station")}
    {set station_crimes to F("Crime check station")}
    {if station_missions || station_crimes:
        {_ Pause for station ATC before speaking again _}
        {Pause(5000)}
        Information:
        {if station_missions:
            {Pause(500)}
            {station_missions}
        }
        {if station_crimes:
            {Pause(500)}
            {station_crimes}
        }
    }
}

{Pause(500)}

{if event.taxi = true :
	{if state.booked_type = "Dropship":
		{if state.booked_return_flight = true :	
			{OneOf("Kampfschiff hat den Supercruise verlassen", "Das Kampfschiff ist in den Normalraum eingetreten")}
		|elif state.booked_return_flight = false :
			{OneOf("Fertig machen für den Kampfeinsatz", "Achtung, der Einsatz startet in wenigen Augenblicken!")}
		|else:
		}
	|else:
		{OneOf("\{OneOf(\"Das Taxi hat\",\"Unser Taxi hat\",\"Wir haben\")\} den Supercruise verlassen,", 
		"\{OneOf(\"Das Taxi ist\",\"Unser Taxi ist\",\"Wir sind\")\} in den Normalraum eingetreten,")}		
	}
|else: 
	{OneOf("\{OneOf(\"{ShipName()} hat\",\"Wir haben\")\} den Supercruise verlassen,", 
			"\{OneOf(\"{ShipName()} ist\",\"Wir sind\")\} in den Normalraum eingetreten,")}
}

{if event.bodytype = 'Planet' || event.bodytype = "Mond" || event.bodytype = "Moon":
    {OneOf("nahe", "in der Nähe von", "in Sichtweite von")}    
    {set time_a to SecondsSince(0) - state.eddi_context_approach_time} 
    {set time_b to SecondsSince(0) - state.eddi_context_granted_time}
    {if !time_b || time_a < time_b : {set time to time_a} {set settlement to state.eddi_context_last_settlement}
    |else: {set time to time_b} {set settlement to state.eddi_context_last_docking}
    }
    {if time < 60:
		{set station to StationDetails(settlement, event.systemname)}
        {if station:
			{SetState('eddi_context_body_name', settlement)}
            {if station.model = "Carrier" || station.model = "FleetCarrier": 
            	{if station.name = state.eigener_carrier : von unserem Träger,
            	|else: von Träger {station.name},
            	}
            	{settlement} {station_notifications}
			|else:
                {if find(settlement, "Planetary Construction Site:") > -1:
                    {OneOf('der planetaren ', 'der Planeten-')}Bau-Stelle {token(settlement, "Site:", 1)}
                |else:
                    {set reportStation to StationDetails(state.eddi_context_nextdest, event.systemname)} 
                    {reportStation.model} {settlement}}  {_ Ansage der Station}
            }
		|else:
            {if find(event.bodyname, "Planetary Construction Site:") > -1:
                {OneOf('der planetaren ', 'der Planeten-')}Bau-Stelle {token(settlement, "Site:", 1)}
            |else:
                von {reportBody.bodytype} {set shortname to reportBody.shortname} {F("Translate Himmelskörper")}
                {station_notifications}}
		}
	|else:
	   {if find(event.bodyname, "Planetary Construction Site:") > -1:
	       {OneOf('der planetaren ', 'der Planeten-')}Bau-Stelle {token(event.bodyname, "Site:", 1)}   
	   |else:
	       {set reportBody to BodyDetails(event.bodyname, event.systemname)}
	       {reportBody.bodytype} {set shortname to reportBody.shortname} {F("Translate Himmelskörper")}}
	}
|elif event.bodytype = 'Star' || event.bodytype = 'Stern':
    {set reportBody to BodyDetails(event.bodyname, event.systemname)}
    {OneOf("nahe", "in der Nähe von")} {reportBody.bodytype} {reportBody.bodyname}

|elif event.bodytype = 'Station':
    {set reportStation to StationDetails(event.bodyname, event.systemname)}
    {OneOf("nahe", "in der Nähe von")}
    {if station.model = "Unbekannte Station" : der Station {event.bodyname}   
    |else:
        {if find(event.bodyname, "Station") >= 0 : {set translate to event.bodyname} {F("Translate Stationsnamen")} {state.station_name}     
        |elif find(event.bodyname, "Rescue") >= 0 : {set translate to event.bodyname} {F("Translate Stationsnamen")} {state.station_name}
        |elif event.bodyname = "Jameson Memorial": Dschejmessen Mimorriel 
        |else:
            {OneOf('der Station,','{reportStation.model}')} {event.bodyname},     
        }
    }
    {SetState('station_model_normalspace', station.model)}
    {station_notifications}

|elif event.bodytype_invariant = 'Planetary Ring' || event.bodytype = 'Stellar Ring' :
    {OneOf("nahe", "in der Nähe von", "in Sichtweite von")} 
    {event.bodytype} {token(event.bodyname," Ring",0)}
|else:
    {OneOf("nahe", "in der Nähe von")}
    {if find(event.bodyname, "Station") >= 0 : {set translate to event.bodyname} {F("Translate Stationsnamen")}
    {state.station_name}
    |elif find(event.bodyname, "Rescue") >= 0 : {set translate to event.bodyname} {F("Translate Stationsnamen")}
    {state.station_name}
    |elif event.bodyname = "Jameson Memorial": Dschejmessen Mimorriel 
    |else:
        {event.bodyname},     
    }
}  
.

{_ für Tests
char(10) bodyname: event.bodyname 
char(10) bodytype: event.bodytype
char(10) bodytype_invariant: event.bodytype_invariant _}

{_
bodyId              - The numeric ID of the nearest body to the commander when entering normal space (if any) 
bodyname            - The nearest body to the commander when entering normal space (if any) 
bodytype            - The localized type of the nearest body to the commander when entering normal space 
bodytype_invariant  - The invariant type of the nearest body to the commander when entering normal space 
multicrew           - True if the ship is belongs to another player 
systemAddress       - The numeric system address of the star system at which the commander has entered normal space 
systemname          - The system at which the commander has entered normal space 
taxi                - True if the ship is an transport (e.g. taxi or dropship) 
_}

Entered normal space.defaultValue:
{_ Context }
{SetState('eddi_context_last_subject', 'fsd')}
{SetState('eddi_context_last_action', 'disengage')}
{SetState('eddi_context_system_name', event.systemname)}
{SetState('eddi_context_system_address', event.systemAddress)}
{SetState('eddi_context_body_type', event.bodytype)}
{SetState('eddi_context_body_system', event.systemname)}

{set station_notifications to:
    {set station_missions to F("Mission check station")}
    {set station_crimes to F("Crime check station")}
    {if station_missions || station_crimes:
        {_ Pause for station ATC before speaking again _}
        {Pause(5000)}
        Information:
        {if station_missions:
            {Pause(500)}
            {station_missions}
        }
        {if station_crimes:
            {Pause(500)}
            {station_crimes}
        }
    }
}

{Pause(500)}

{OneOf("{ShipName()} has", "")} 
{OneOf("left supercruise", "{OneOf('entered', 'returned to', 'dropped to')} normal space")}

{if event.bodytype_invariant = 'Station':
    {SetState('eddi_context_station_name', event.body)}
    {set reportStation to StationDetails(event.body, event.system)}
    {OneOf("near", "close to", "in the vicinity of")}
    {P(reportStation.model, "station")}, {P(event.body, "body")}
    {station_notifications}
|else:
    {SetState('eddi_context_body_name', event.body)}
    {if event.bodytype_invariant = 'Planet':
        {OneOf("near", "close to", "in the vicinity of")}
    {set time to SecondsSince(0) - state.eddi_context_approach_time}
        {if time < 60:
            {set settlement to state.eddi_context_last_settlement}
            {set reportStation to StationDetails(settlement, event.system)}
            {if reportStation:
                {SetState('eddi_context_body_name', settlement)}
                {if reportStation.Model.invariantName != "Unknown Station":
                    {P(reportStation.model, "station")},
                } 
                {P(settlement, "station")}
                {station_notifications}
            |else:
                {P(settlement, "station")}
            }
        |else:
            {set reportBody to BodyDetails(event.body, event.system)}
            {reportBody.bodytype)} {P(reportBody.name, "body")}
        }
    |elif event.bodytype_invariant = 'Star':
        {set reportBody to BodyDetails(event.body, event.system)}
        {OneOf("near", "close to", "in the vicinity of")} {reportBody.bodytype)} {P(reportBody.name, "body")}

    |elif event.bodytype_invariant = 'Planetary Ring':
        {OneOf("near", "close to", "in the vicinity of")} 
        {event.bodytype} {P(token(event.body," Ring",0), "body")}
    }.
}

