Exobiology Report.script:
{Log("Fktn.: Exobiology Report")} 

{_ Exobiology Report _}
{_ Function to report on possible exobiological species on a body. _}
{_ Data Sources: Deep Space Network, Spansh, EDSM, ED Wiki _}

{_ NOTE: Only lists *possible* species.  It does NOT say what is actually on a body. _}

{_ NOTE: Bark Mounds require a Nebula (<150Ly from Nebula centre). _}
{_ The Journal/API does not provide this data. While possible to calculate, _}
{_ it would take too long and disrupt gameplay. Therefore I have left them out of this list. _}

{_ Test samples _}
{_ set reportBody to BodyDetails("Asgarage A 4", "Asgarage")}
{_ set reportBody to BodyDetails("Saktsak AB 2 f", "Saktsak")}
{_ set reportBody to BodyDetails("LHS 1918 B 3", "LHS 1918")}
{_ set reportBody to BodyDetails("LP 292-66 E 3", "LP 292-66")}
{_ set reportBody to BodyDetails("Caph 5 C", "Caph")}
{_ set reportBody to BodyDetails("Heart Sector DL-Y e7 7", "Heart Sector DL-Y e7")} {_ Anemone _}
{_ set reportBody to BodyDetails("Nyeakua DX-A d1-27 B 5", "Nyeakua DX-A d1-27")} {_ Brain Tree & Sinuous Tubers _}
{_ set reportBody to BodyDetails("Gludgoe FQ-F b53-1 6 b", "Gludgoe FQ-F b53-1")} {_ Fumerola _}


{_ Options _}
{set value_threshold to 10000000}  {_ Minimum Vista Genomics sale value. 1M to 20M _}
{set report to 1}   {_ Set to 0 for no ExoBiology reporting _}
                    {_ Set to 1 to only report possible species above value_threshold _}
                    {_ Set to 2 for full report of all possible species of all values _}
{set reportValue to 1}  {_ Set to 0 for no value reporting _}
                        {_ Set to 1 to report maximum value if all reported species exist _}


{_ Only calculate if report is wanted _}
{if report > 0:

    {if !reportBody:
        {set reportBody to BodyDetails(GetState('eddi_context_body_name'),
                                       GetState('eddi_context_body_system'))}
    }
    {set reportSystem to SystemDetails(reportBody.systemname)}

    {_ Create system body types array _}
    {set bodyTypes to []}
    {for body in reportSystem.bodies:
        {if find(bodyTypes, body.planettype) = -1:
            {set bodyTypes to cat(bodyTypes, [body.planettype])}
        }
    }
    {set bodyTypes to flip(bodyTypes)}

    {_ Create body type checklist _}
    {set checkList to ["Ammonia world":0, "Earth-like world":1, "Gas giant with water based life":2,
                       "Gas giant with ammonia based life":3, "Water giant":4]}

    {_ Find main star class & luminosity _}
    {set starClass to ""}
    {set starLumin to ""}
    {set main to slice(reportBody.shortname, 0, 1)}
    {if ord(main) > 64 && ord(main) < 91:
        {set main to cat(reportBody.systemname, " A")}
    |else:
        {set main to reportBody.systemname}
    }
    {set starClass to BodyDetails(main, reportBody.systemname).stellarclass}
    {set starLumin to BodyDetails(main, reportBody.systemname).luminosityclass}
    {set starLumin to match(starLumin, "(.*[I|V])")[0]}

    {set volcanismComp to split(reportBody.volcanism.composition, " ")[0]}
    {set species to []}


    {_ Compile all possible species for the body_}
    {if reportBody.atmosphere = "No atmosphere" || reportBody.atmosphere = "Sulphur dioxide":
        {if starClass = "O":
            {if reportBody.planettype = "Metal-rich body" || reportBody.planettype = "Rocky body"
              || reportBody.planettype = "High metal content world":
                {set species to cat(species, ["Anemone Prasinum Bioluminescent"])}
            |elif reportBody.planettype = "Rocky ice world" || reportBody.planettype = "Icy body":
                {set species to cat(species, ["Anemone Puniceum"])}
            }
        |elif starClass = "B":
            {if find(["I", "II", "III"], starLumin) > -1:
                {if reportBody.planettype = "Metal-rich body"
                  || reportBody.planettype = "High metal content world":
                    {set species to cat(species, ["Anemone Roseum Bioluminesecent"])}
                |elif reportBody.planettype = "Rocky body":
                    {set species to cat(species, ["Anemone Roseum"])}
                }
            |elif find(["IV", "V"], starLumin) > -1:
                {if reportBody.planettype = "Metal-rich body"
                  || reportBody.planettype = "High metal content world":
                    {set species to cat(species, ["Anemone Blatteum Bioluminesecent"])}
                |elif reportBody.planettype = "Rocky body":
                    {set species to cat(species, ["Anemone Luteolum"])}
                }
            |elif starLumin = "VI":
                {if reportBody.planettype = "Metal-rich body"
                  || reportBody.planettype = "High metal content world":
                    {set species to cat(species, ["Anemone Rubeum Bioluminesecent"])}
                |elif reportBody.planettype = "Rocky body":
                    {set species to cat(species, ["Anemone Croceum"])}
                }
            }
        |elif starClass = "A" && starLumin = "III":
            {if reportBody.planettype = "Metal-rich body"
              || reportBody.planettype = "High metal content world":
                {set species to cat(species, ["Anemone Rubeum Bioluminesecent"])}
            |elif reportBody.planettype = "Rocky body":
                {set species to cat(species, ["Anemone Croceum"])}
            }        
        }
    }

    {if reportBody.atmosphere = "No atmosphere":
        {if starClass = "A":
            {if reportBody.planettype = "Metal-rich body":
                {set bodyTypes to cross(bodyTypes, checkList)}
                {if len(bodyTypes) > 0: {set species to cat(species, ["Amphora Plant"])}}
            }
        }

        {if find(["A", "F", "G", "K", "M", "S"], starClass) > - 1:
            {if reportBody.temperature <= 237:
                {if reportBody.distance > 12000:
                    {set bodyTypes to cross(bodyTypes, checkList)}
                    {if len(bodyTypes) > 0:
                        {set species to cat(species, ["Crystalline Shards"])}
                    }
                }
            }
        }

        {if reportBody.volcanism:
            {set checkList2 to ["Earth-like world":0, "Gas giant with water based life":1]}
            {if len(cross(bodyTypes, checkList2)) > 0:
                {if reportBody.planettype = "Metal-rich body"
                  || reportBody.planettype = "High metal content world":
                    {set species to cat(species, ["Brain Tree Ostrinum", "Brain Tree Puniceum"])}
                    {if reportBody.temperature > 300 && reportBody.temperature < 500:
                        {set species to cat(species, ["Brain Tree Aureum"])}
                        {if reportBody.planettype = "High metal content world":
                            {set species to cat(species, ["Brain Tree Lindigoticum"])}
                        }
                    }
                |elif reportBody.planettype = "Rocky body":
                    {if reportBody.temperature > 200 && reportBody.temperature <= 300:
                        {set species to cat(species, ["Brain Tree Gypseeum"])}
                    |elif reportBody.temperature > 300 && reportBody.temperature <= 500:
                        {set species to cat(species, ["Brain Tree Lindigoticum", "Brain Tree Lividum"])}
                    }
                |elif reportBody.planettype = "Rocky ice world":
                    {if reportBody.temperature > 100 && reportBody.temperature <= 270:
                        {set species to cat(species, ["Brain Tree Viride"])}
                    }
                }
            }
            {if reportBody.temperature > 200 && reportBody.temperature < 500:
                {set species to cat(species, ["Brain Tree Roseus"])}
            }


            {if reportBody.planettype = "Metal-rich body"
              || reportBody.planettype = "High metal content world":
                {set species to cat(species, ["Sinuous Tubers Blatteum", "Sinuous Tubers Prasinium",
                                              "Sinuous Tubers Violaceum", "Sinuous Tubers Viride"])}
            |elif reportBody.planettype = "Rocky body":
                {set species to cat(species, ["Sinuous Tubers Albidum", "Sinuous Tubers Caeruleum",
                                              "Sinuous Tubers Lindigoticum"])}
            }
            {if volcanismComp = "Silicate" && reportBody.volcanism.type = "Magma":
                {set species to cat(species, ["Sinuous Tubers Roseum"])}
            }
        }
    }


    {_ All gravities _}
    {_ Vista Genomics Exobiology _}
    {if volcanismComp = "Helium" || volcanismComp = "Iron" || volcanismComp = "Silicate"
      || volcanismComp = "Ammonia":
        {set species to cat(species, ["Bacterium Tela"])}
    }

    {if reportBody.atmosphere = "Helium":
        {set species to cat(species, ["Bacterium Nebulus"])}
    }

    {if find(reportBody.atmosphere, "Neon") > -1:
        {set species to cat(species, ["Bacterium Acies"])}
        {if volcanismComp = "Water":
            {set species to cat(species, ["Bacterium Verrata"])}
        |elif volcanismComp = "Ammonia" || volcanismComp = "Nitrogen":
            {set species to cat(species, ["Bacterium Omentum"])}
        |elif volcanismComp = "Carbon" || volcanismComp = "Methane":
            {set species to cat(species, ["Bacterium Scopulum"])}
        }
    }

    {if find(reportBody.atmosphere, "Argon") > -1:
        {set species to cat(species, ["Bacterium Vesicula"])}
    }

    {if find(reportBody.atmosphere, "Methane") > -1:
        {set species to cat(species, ["Bacterium Bullaris"])}
    }

    {if reportBody.atmosphere = "Nitrogen":
        {set species to cat(species, ["Bacterium Informem"])}
    }

    {if reportBody.atmosphere = "Oxygen":
        {set species to cat(species, ["Bacterium Volu"])}
        {if reportBody.planettype ="High metal content world" && reportBody.temperature > 164.9:
            {set species to cat(species, ["Stratum Tectonicas"])}
        }
    }

    {if reportBody.atmosphere = "Ammonia":
        {if reportBody.planettype = "Rocky body":
            {set species to cat(species, ["Bacterium Alcyoneum"])}
            {if reportBody.temperature >= 165:
                {set species to cat(species, ["Stratum Paleas", "Stratum Laminamus"])}
            }
        |elif reportBody.planettype = "High metal content world":
            {set species to cat(species, ["Bacterium Alcyoneum"])}
            {if reportBody.temperature >= 165:
                {set species to cat(species, ["Stratum Tectonicas"])}
            }
        }
    }

    {if find(reportBody.atmosphere, "Carbon dioxide") > -1:
        {if reportBody.planettype = "Rocky body":
            {set species to cat(species, ["Bacterium Aurasus"])}
            {if reportBody.temperature >= 165:
                {set species to cat(species, ["Stratum Paleas"])}
            }
            {if reportBody.temperature >= 165 && reportBody.temperature <= 190:
                {set species to cat(species, ["Stratum Excutitus", "Stratum Limaxus"])}
            |elif reportBody.temperature > 190:
                {set species to cat(species, ["Stratum Frigus", "Stratum Cucumisis"])}
            }
        |elif reportBody.planettype = "High metal content world":
            {set species to cat(species, ["Bacterium Aurasus"])}
            {if reportBody.temperature >= 165:
                {set species to cat(species, ["Stratum Tectonicas"])}
            }
        }
    }

    {if find(reportBody.atmosphere, "Water") > -1:
        {if reportBody.planettype = "Rocky body":
            {set species to cat(species, ["Bacterium Cerbrus"])}
            {if reportBody.temperature >= 165:
                {set species to cat(species, ["Stratum Paleas"])}
            }
        |elif reportBody.planettype = "High metal content world":
            {set species to cat(species, ["Bacterium Cerbrus"])}
            {if reportBody.temperature >= 165:
                {set species to cat(species, ["Stratum Tectonicas"])}
            }
        }
    }

    {if reportBody.atmosphere = "Sulphur dioxide":
        {if reportBody.planettype = "Rocky body":
            {set species to cat(species, ["Bacterium Cerbrus"])}
            {if reportBody.temperature >= 165:
                {set species to cat(species, ["Stratum Araneamus"])}
            }
            {if reportBody.temperature >= 165 && reportBody.temperature <= 190:
                {set species to cat(species, ["Stratum Excutitus", "Stratum Limaxus"])}
            }
            {if reportBody.temperature > 190:
                {set species to cat(species, ["Stratum Frigus", "Stratum Cucumisis"])}
            }
        |elif reportBody.planettype = "High metal content world":
            {set species to cat(species, ["Bacterium Cerbrus"])}
            {if reportBody.temperature >= 165:
                {set species to cat(species, ["Stratum Tectonicas"])}
            }
        |elif reportBody.planettype = "Rocky ice world" || reportBody.planettype = "Icy body":
            {set species to cat(species, ["Bacterium Cerbrus"])}
        }
    }

    {_ Low gravity bodies <= 0.27G _}
    {if round(reportBody.gravity, 2) <= 0.27:
        {set starLumin to slice(starLumin, 0, 1)} {_ Need only first letter in this section _}

        {if reportBody.planettype = "High metal content world" || reportBody.planettype = "Rocky body":
            {if volcanismComp = "Silicate" || volcanismComp = "Iron" || volcanismComp = "Rocky":
                {set species to cat(species, ["Fumerola Extremus"])}
            }
        |elif reportBody.planettype = "Icy body" || reportBody.planettype = "Rocky ice world":
            {if volcanismComp = "Water":
                {set species to cat(species, ["Fumerola Aquatis"])}
            |elif volcanismComp = "Carbon dioxide" || volcanismComp = "Methane":
                {set species to cat(species, ["Fumerola Carbosis"])}
            |elif volcanismComp = "Ammonia" || volcanismComp = "Nitrogen":
                {set species to cat(species, ["Fumerola Nitris"])}
            }
        }
        {if reportBody.atmosphere = "Helium" && reportBody.planettype = "Icy body":
            {set species to cat(species, ["Electricae Radialem"])} {_ Only near Nebulae? _}
            {if (starClass = "A" && starLumin = "V") || starClass = "Neutron":
                {set species to cat(species, ["Electricae Pluma"])}
            }
        }

        {if find(reportBody.atmosphere, "Neon") > -1:
            {set species to cat(species, ["Fonticulua Segmentatus"])}
            {if reportBody.planettype = "Icy body":
                {set species to cat(species, ["Electricae Radialem"])} {_ Only near Nebulae? _}
                {if (starClass = "A" && starLumin = "V") || starClass = "Neutron":
                    {set species to cat(species, ["Electricae Pluma"])}
                }
            }
        }

        {if find(reportBody.atmosphere, "Argon") > -1:
            {set species to cat(species, ["Fungoida Bullarum", "Osseus Pumice"])}
            {if reportBody.planettype = "Icy body":
                {set species to cat(species, ["Electricae Radialem"])} {_ Only near Nebulae? _}
                {if (starClass = "A" && starLumin = "V") || starClass = "Neutron":
                    {set species to cat(species, ["Electricae Pluma"])}
                }
            }
            {if reportBody.planettype = "Icy body" || reportBody.planettype = "Rocky ice world":
                {if reportBody.atmosphere = "Argon":
                    {set species to cat(species, ["Fonticulua Campestris"])}
                |else:
                    {set species to cat(species, ["Fonticulua Upupam"])}
                }
            |elif reportBody.planettype = "Rocky body":
                {set species to cat(species, ["Tussock Capillum"])}
            }
        }

        {if find(reportBody.atmosphere, "Methane") > -1:
            {set species to cat(species, ["Fungoida Setisis", "Osseus Pumice"])}
            {if reportBody.planettype = "Icy body" || reportBody.planettype = "Rocky ice world":
                {set species to cat(species, ["Fonticulua Digitos"])}
            |elif reportBody.planettype = "Rocky body":
                {set species to cat(species, ["Tussock Capillum"])}
            }
        }

        {if reportBody.atmosphere = "Nitrogen":
            {set species to cat(species, ["Concha Bioncavis"])}
            {if reportBody.planettype = "Icy body" || reportBody.planettype = "Rocky ice world":
                {set species to cat(species, ["Fonticulua Lapida"])}
            }
        }

        {if reportBody.atmosphere = "Oxygen":
            {if reportBody.planettype = "Icy body" || reportBody.planettype = "Rocky ice world":
                {set species to cat(species, ["Fonticulua Fluctus"])}
            }
        }

        {if reportBody.atmosphere = "Ammonia":
            {if reportBody.planettype = "Rocky body"
              || reportBody.planettype = "High metal content world":
                {set species to cat(species, ["Aleoida Laminiae", "Aleoida Spica", "Cactoida Lapis",
                    "Cactoida Peperatis", "Concha Aureolas", "Fungoida Setisis", "Osseus Spiralis"])}
            }
            {if reportBody.planettype = "Rocky body":
                {set species to cat(species, ["Frutexa Flabellum", "Frutexa Flammasis",
                    "Tussock Catena", "Tussock Cultro", "Tussock Divisa"])}
                {if reportBody.gravity < 0.15:
                    {set species to cat(species, ["Tubus Rosarium"])}
                }
            |elif reportBody.planettype = "High metal content world":
                {set species to cat(species, ["Frutexa Metallicum"])}
                {if reportBody.gravity < 0.15:
                    {set species to cat(species, ["Tubus Sororibus"])}
                }
            }
        }

        {if find(reportBody.atmosphere, "Carbon dioxide") > -1:
            {if reportBody.planettype = "Rocky body"
              || reportBody.planettype = "High metal content world":

                {if reportBody.temperature >= 175 && reportBody.temperature < 180:
                    {set species to cat(species, ["Aleoida Arcus"])}
                |elif reportBody.temperature < 190:
                    {set species to cat(species, ["Aleoida Coronamus", "Osseus Fractus"])}
                |elif reportBody.temperature < 195:
                    {set species to cat(species, ["Aleoida Gravis", "Osseus Pellebantus"])}
                }
                {if reportBody.temperature >= 180 && reportBody.temperature < 195:
                    {set species to cat(species, ["Cactoida Cortexum", "Cactoida Pullulanta",
                        "Concha Renibus", "Fungoida Gelata", "Fungoida Stabitis", "Osseus Cornibus"])}
                }
                {if reportBody.temperature >= 190:
                    {set species to cat(species, ["Clypeus Lacrimam", "Clypeus Margaritus"])}
                    {if reportBody.distance > 2500:
                        {set species to cat(species, ["Clypeus Speculumi"])}
                    }
                }    
            }

            {if reportBody.planettype = "Rocky body":
                {set species to cat(species, ["Concha Labiata", "Frutexa Acus", "Frutexa Fera",
                    "Tussock Propagito", "Tussock Pennatis"])}

                {if reportBody.temperature >= 145 && reportBody.temperature < 155:
                    {set species to cat(species, ["Tussock Pennata"])}
                |elif reportBody.temperature < 160:
                    {set species to cat(species, ["Tussock Ventusa"])}
                |elif reportBody.temperature < 170:
                    {set species to cat(species, ["Tussock Ignis"])}
                |elif reportBody.temperature < 175:
                    {set species to cat(species, ["Tussock Serrati"])}
                |elif reportBody.temperature < 180:
                    {set species to cat(species, ["Tussock Albata"])}
                |elif reportBody.temperature < 190:
                    {set species to cat(species, ["Tussock Caputus"])}
                |elif reportBody.temperature < 195:
                    {set species to cat(species, ["Tussock Triticum"])}
                }

                {if reportBody.temperature >= 160 && reportBody.temperature < 190
                  && reportBody.gravity <= 0.15:
                    {set species to cat(species, ["Tubus Cavas", "Tubus Compagibus", "Tubus Conifer"])}
                }

            |elif reportBodyplanettype = "High metal content world":
                {set species to cat(species, ["Frutexa Metallicum"])}
                {if reportBody.temperature >= 160 && reportBody.temperature < 190
                  && reportBody.gravity <= 0.15:
                    {set species to cat(species, ["Tubus Sororibus"])}
                }
                {if reportBody.temperature < 190:
                    {set species to cat(species, ["Concha Labiata"])}
                }
            }
        }

        {if find(reportBody.atmosphere, "Water") > -1:
            {if reportBody.planettype = "Rocky body"
              || reportBody.planettype = "High metal content world":
                {set species to cat(species, ["Cactoida Vermis", "Concha Renibus",
                    "Fungoida Gelata", "Fungoida Stabitis", "Osseus Discus"])}
                {if reportBody.temperature >= 190:
                    {set species to cat(species, ["Clypeus Lacrimam", "Clypeus Margaritus"])} 
                    {if reportBody.distance > 2500:
                        {set species to cat(species, ["Clypeus Speculumi"])}
                    }
                }
            }
            {if reportBody.planettype = "Rocky body":
                {set species to cat(species, ["Frutexa Sponsae", "Tussock Virgam"])}
            }
        }

        {if reportBody.atmosphere = "Sulphur dioxide":
            {if reportBody.planettype = "Rocky body":
                {set species to cat(species, ["Frutexa Collum", "Recepta Deltahedronix",
                    "Recepta Umbrux", "Tussock Stigmasis"])}
            |elif reportBody.planettype = "High metal content world":
                {set species to cat(species, ["Recepta Deltahedronix", "Recepta Umbrux"])}
            |elif reportBody.planettype = "Rocky ice world" || reportBody.planettype = "Icy body":
                {set species to cat(species, ["Recepta Conditivus", "Recepta Umbrux"])}
            }
        }
    }


    {_ Sort species alphabetically _}
    {if len(species) > 0:
        {set species2 to []}
        {for bio in sort(species):
            {set species2 to cat(species2, [bio])}
        }
        {set species to species2}
    }

    {_ Set up species values _}
    {_ Some are not ExoBiology samples, ie. Brain Trees _}
    {set species_value to [
        "Aleoida Arcus" : 7252500,
        "Aleoida Coronamus" : 6284600,
        "Aleoida Gravis" : 12934900,
        "Aleoida Laminiae" : 3385200,
        "Aleoida Spica" : 3385200,
        "Amphora Plant" : 1628800,
        "Anemone Blatteum Bioluminescent" : 1499900,
        "Anemone Croceum" : 3399800,
        "Anemone Luteolum" : 1499900,
        "Anemone Prasinum Bioluminescent" : 1499900,
        "Anemone Puniceum" : 1499900,
        "Anemone Roseum" : 1499900,
        "Anemone Roseum Bioluminescent" : 1499900,
        "Anemone Rubeum Bioluminescent" : 1499900,
        "Bacterium Acies" : 1000000,
        "Bacterium Alcyoneum" : 1658500,
        "Bacterium Aurasus" : 1000000,
        "Bacterium Bullaris" : 1152500,
        "Bacterium Cerbrus" : 1689800,
        "Bacterium Informem" : 8418000,
        "Bacterium Nebulus" : 5289900,
        "Bacterium Omentum" : 4638900,
        "Bacterium Scopulum" : 4934500,
        "Bacterium Tela" : 1949000,
        "Bacterium Verrata" : 3897000,
        "Bacterium Vesicula" : 1000000,
        "Bacterium Volu" : 7774700,
        "Bark Mounds" : 1471900,
        "Brain Tree Aureum" : 3565100,
        "Brain Tree Gypseeum" : 3565100,
        "Brain Tree Lindigoticum" : 3565100,
        "Brain Tree Lividum" : 1593700,
        "Brain Tree Ostrinum" : 3565100,
        "Brain Tree Puniceum" : 3565100,
        "Brain Tree Roseum" : 1593700,
        "Brain Tree Viride" : 1593700,
        "Cactoida Cortexum" : 3667600,
        "Cactoida Lapis" : 2483600,
        "Cactoida Peperatis" : 2483600,
        "Cactoida Pullulanta" : 3667600,
        "Cactoida Vermis" : 16202800,
        "Clypeus Lacrimam" : 8418000,
        "Clypeus Margaritus" : 11873200,
        "Clypeus Speculumi" : 16202800,
        "Concha Aureolas" : 7774700,
        "Concha Biconcavis" : 19010800,
        "Concha Labiata" : 2352400,
        "Concha Renibus" : 4572400,
