{Log("Fktn.: Fahrzeug-Hangar check")}

{set vehicles_count to 0}
{set garage_count to 0}
{set hangar_gefunden to false}

{for compartment in ship.compartments:
    {if find(compartment.module.name, "Planetenfahrzeug-Hangar") > -1 : {set hangar_gefunden to true}
    |else: }
}

{if hangar_gefunden = true:
    {for compartment in ship.compartments:
        {if compartment.module.name = "Planetenfahrzeug-Hangar":
            {set garage_count to garage_count + 1}
            {if capi_active :                           {_ len(ship.launchbays) > 0 : }
                {for launchbay in ship.launchbays:
                    {if len(launchbay.vehicles) > 0 :
                        {set vehicles_count to vehicles_count + 1}}
