FSD engaged.script:
{Log("EVENT: FSD engaged")}

{_ Context _}
{SetState('eddi_context_last_subject', 'fsd')}
{SetState('eddi_context_last_action', 'engage')}
{SetState('eddi_context_system_name', event.systemname )}
{SetState('eddi_context_system_name_report', event.systemname )}
{SetState('eddi_context_system_system', event.systemname)}
{SetState('eddi_context_fsd_target', event.target)}
{SetState('eddi_context_system_lastvisit', system.lastVisitSeconds)}

{SetState('first_visit', false)}
{SetState('eddi_honk_starten', false)}

{_ Zero the 'remaining jump' context to only use values following this event _}
{SetState('eddi_context_remaining_jumps', 0)}

{_ Set variable for the 'relog trick' _}
{SetState('eddi_relog', true)}

{if event.target = 'Hyperspace':
	{_ Rewrite context to represent a jump _}
   	{SetState('eddi_context_last_subject', 'jump')}
    {SetState('eddi_context_last_action', 'start')}
    {SetState('eddi_context_supercruise', "yes")}

    {set reportsystem to SystemDetails(event.systemname)}
	{set arrivalsystem to SystemDetails(event.systemname)}

    {_ Wait for the FSD countdown and initial jump _}
	{Pause(700)}
    
    {if state.say_countdown = 1:
        {Pause(100)} 3 {Pause(300)}, 2 {Pause(300)}, 1 {Pause(300)}, Sprung.
	|else: }
	
	{Pause(5000)}
	
	Wir {OneOf("sind", "befinden uns")} {OneOf("auf Kurs", "auf dem Weg", "auf dem Sprung")} zum System 
	{if (reportsystem.name) = "Shinrarta Dezhra": Shinrarte dessra, |else: {reportsystem.name},}
	
	{_ Achtung Variable für SecondsSince() geändert}
	{if reportsystem.name = "" || !reportsystem.name:
	|elif reportsystem.name = homesystem.name: 
		{if SecondsSince(reportsystem.lastVisitSeconds) > 3600 : Willkommen im Heimatsystem, {F("Honorific")} {cmdr.name}}
	|else: {_ Log("lastsystem_allegiance:")  Log(lastsystem.allegiance) Log("system_allegiance:") Log(system.allegiance) _}

		{if lastsystem.allegiance && lastsystem.allegiance != "None" && lastsystem.allegiance != "Keine" &&
            lastsystem.allegiance != "Independent" && lastsystem.allegiance != "Unabhängig" && 
            (!system.allegiance || system.allegiance = "None" || system.allegiance = "Keine" || 
            system.allegiance = "Independent" || system.allegiance = "Unabhängig"):
				und verlassen {OneOf("jetzt", "gerade", "hiermit", "somit", "derzeit")} {set allegiance to lastsystem.allegiance}
        |elif lastsystem.allegiance && lastsystem.allegiance != system.allegiance && system.allegiance 
				&& system.allegiance != "None" && system.allegiance != "Keine":
				und betreten {OneOf("jetzt", "gerade", "hiermit", "somit", "derzeit")} {set allegiance to system.allegiance}         
			{if cmdr.title != "Commander" && cmdr.title != "Kommandant" : {set welcome to cat('Willkommen zurück ', F('Honorific'))}}
        }

		{if allegiance:
			{ if  allegiance = "Empire"       	|| allegiance = "Imperium"   : 
				{OneOf("immperiales Gebiet", "immperiales Hoheits Gebiet", "immperiales Herrschafts Gebiet", "immperialen Raum")}
			|elif  allegiance = "Federation"  	|| allegiance = "Föderation" : 
				{OneOf("föderales Gebiet", "Föderations Gebiet", "Föderations Raum", "föderalen Raum")}
			|elif  allegiance = "Alliance"    	|| allegiance = "Allianz"    : 
				{OneOf("Allianz Gebiet", "alli iertes Gebiet", "Allianz Raum", "alli ierten Raum")}
			|elif  allegiance = "Independent" 	|| allegiance = "Unabhängig" : 
				{OneOf("neutrales Gebiet", "neutralen Raum")}
			|elif  allegiance = "Thargoids"		|| allegiance = "Thargoiden" : 
                {OneOf("Thargoiden Gebiet", "Thargoiden Raum", "Gebiet der Außerirdischen")}   
			|else: 
				{allegiance}
			}.
		}

		{if welcome: {welcome}.}

		{set scoopables to ["O", "B", "A", "F", "G", "K", "M"]}
		{if slice(event.stellarclass, 0, 1) = "D" 		: Vorsicht: Weissen Zwerg entdeckt!
		|elif event.stellarclass = "N" 					: Vorsicht: Neutrohnen,Stern entdeckt!
		|elif event.stellarclass = "H"					: Vorsicht: Schwarzes Loch entdeckt!
		|elif find(scoopables, event.stellarclass) = -1	: Achtung: Zielstern zum Auftanken nicht geeignet!
		}		
		
		{char(10)}{char(13)}
		{if reportsystem.visits = 0 : {OneOf("Dies ist unser erster Besuch,", "Wir sind das erste mal in diesem System,", 
			"Oh, hier waren wir noch nicht,", "Das ist unser erster Besuch in diesem System,",
			"Das ist ein {OneOf('neues', 'unbekanntes')} System für uns, {Occasionally(4,\"gleich nicht mehr,\")}",
			"Ein {OneOf('unbekanntes', 'neues')} System für uns, {Occasionally(4,\"gleich nicht mehr,\")}")} 
			{SetState('first_visit', true)}
		|elif reportsystem.visits = 1 :
            {OneOf("Wir haben dieses System bereits einmal besucht,","Das ist unser zweiter Besuch,")}
		|elif reportsystem.visits = 2 :
            {OneOf("Das ist unser dritter Besuch dieses Systems,","Wir sind zum dritten Mal in diesem System,")}
		|elif reportsystem.visits = 3 || reportsystem.visits = 4 || reportsystem.visits = 5 :
            Wir haben dieses System {OneOf("bereits", "schon")} {reportsystem.visits} mal besucht,
		|elif reportsystem.visits = 6 || reportsystem.visits = 7 || reportsystem.visits = 8 : 
            Wir haben dieses System {OneOf("bereits", "schon")} {OneOf("mehrmals", "häufiger", "öfter")} besucht,
        |elif reportsystem.visits > 8 : {_ keine Ansage mehr oder... } {Occasionally(5, reportsystem.visits'ter Besuch')}
 		|else:
            {OneOf("{Occasionally(2, 'Das')} System ist 
            {Occasionally(2, 'uns')} bekannt", 'Wir kennen das System', 'Ein bekanntes System')},
		}
		
		{Pause(500)}
		
		{if state.ansage_home = 1:
			{if homesystem.name = "" || !homesystem.name : {_ Occasionally(5, "Es wurde kein Heimatsystem eingetragen !") _}
			|else:
			
				{if homesystem.name = "Shinrarta Dezhra": {set homesystem to SystemDetails("Shinrarte dessra")}}
			
				{if reportsystem.distancefromhome > 500 :
					{Occasionally(7, "{OneOf("Wir befinden uns", "Aktuelle Position ist", "Wir sind nun")}")} 
					{set value to (reportsystem.distancefromhome)} {F("Humanise ValueGer")} Lichtjahre von 
					{OneOf("unserem Heimatsystem", "zu Hause", homesystem.name )} entfernt.
					
					{if reportsystem.comment: Wir haben {OneOf("eine Notiz", "einen Vermerk", "Einen Kommentar")} 
						{OneOf("über", "für", "zu")} diesem System.  
						{OneOf("Mit dem Inhalt:", "Der Besagt,", "lese vor:", " Der Inhalt lautet?")} {reportsystem.comment}.
					}
				}
			}
		|else: {_ Heimatsystem wird nicht angesagt _}
		}
	}	
}

{_
stellarclass    - The class of the destination primary star (only if type is Hyperspace) 
systemAddress   - The numeric system address of the destination star system (only if type is Hyperspace) 
systemname      - The destination system (only if type is Hyperspace) 
target          - The target frame (Supercruise/Hyperspace) 
taxijump        - True if traveling via taxi 
_}

