    {SetState('eddi_context_last_subject', 'jump')}
    {SetState('eddi_context_last_action', 'start')}
    {SetState('eddi_context_system_name', event.systemname)}
    {SetState('eddi_context_system_address', event.systemAddress)}
    
    {set reportsystem to SystemDetails(event.systemAddress)}

    {_ Wait for the FSD countdown and initial jump}
    {Pause(5000)}

    {OneOf("Destination confirmed","Jump in progress","Telemetry obtained")}.
    {ShipName()} is {OneOf("heading to", "on route to", "travelling to", "in transit to")} the {P(reportsystem.name, "starsystem")} system.

    {if (lastsystem.allegiance != reportsystem.allegiance) &&
        ((lastsystem.allegiance != "None" && lastsystem.allegiance != "Independent") && 
        (reportsystem.allegiance = "None" || reportsystem.allegiance = "Independent")):
        You {OneOf("are leaving", "have left", "are no longer in")} {lastsystem.allegiance} space.
    }
           
    {if lastsystem.allegiance && reportsystem.allegiance && 
        lastsystem.allegiance != reportsystem.allegiance && 
        reportsystem.allegiance != "None":
        You {OneOf("are now in", "have entered", "are entering")} {reportsystem.allegiance} space.
        {if cmdr.title != "Commander":
            Welcome back {F("Honorific")}.
        }
    }
    
    {if !reportsystem.population && lastsystem.population:
        {OneOf("This system is not populated","There is no human presence here", "Humans have yet to colonise this system")}.
    |elif reportsystem.population:
        {if SecondsSince(reportsystem.lastVisitSeconds) > 3600:
            {F("System report")}
        }
    }     

    {if reportsystem.visits = 0:
        This is your first visit to this system.
    |elif reportsystem.visits = 1:
        This is your second visit to this system.
    |elif reportsystem.visits = 2:
        {Occasionally(2, "This is your third visit to this system.")}
    |elif reportsystem.visits = 3:
        {Occasionally(2, "This is your fourth visit to this system.")}
    |else:
        {Occasionally(3, "You have visited this system {reportsystem.visits} times.")}
    }
    
    {if reportsystem.comment:
        You made a {OneOf("note", "comment", "remark")} {OneOf("about", "for", "on")} this system.  It {OneOf("is as follows", "says", "reads", "is")} {reportsystem.comment}.
    }    
}

Fuel check.script:
{Log("Fktn.: Fuel check")}

{_ Fuel Check _}
{_ Report fuel level _}

{if ship.name = GetState('eddi_fuelcheck_shipname') :
    {if state.eddi_context_fuel_used > GetState('eddi_fuelcheck_maxfuel') :
        {SetState('eddi_fuelcheck_maxfuel', state.eddi_context_fuel_used)}
    }
|else:
    {SetState('eddi_fuelcheck_maxfuel', state.eddi_context_fuel_used)}
    {SetState('eddi_fuelcheck_shipname', ship.name)}
}

{set maxfuel to GetState('eddi_fuelcheck_maxfuel')}
{set fuelremaining to state.eddi_context_fuel_remaining}
{set maxjump to fuelremaining / maxfuel}
{set currentfuel to round(fuelremaining / ship.fueltanktotalcapacity * 100, 0)}
{set shipsfuel to:
   {Occasionally(2, "{ShipName()}s" )} Treibstoff
   {OneOf("level", "tank", "reserve")} {Occasionally(2, "ist")} {Occasionally(2, "jetzt")}
}
{set currentpercent to: 
   bei {currentfuel} Prozent {Occasionally(2, "Kapazität")}
}
{set title to Occasionally(3,", {F('Honorific')}")}

{if maxjump < 0.5: Gefahr! {shipsfuel} erschöpft!
   {Pause(500)} Wir Laufen auf Reserve. Die Abschaltung nicht lebenswichtiger Systeme wird empfohlen!
   {Pause(500)} Notfalltransponder in Bereitschaft, {title}.
|elif maxjump < 1.25:
   {OneOf("Warnung:", "Gefahr:", "Achtung:")}
   {shipsfuel} {OneOf("gefährlich tief", "fast aufgebraucht", "fast alle")}.
   {OneOf("Bitte auftanken", "Auftanken wird unbedingt empfohlen")}{title}.
|elif currentfuel < 25:
   {OneOf("Warnung:", "Gefahr:", "Achtung:")}
   {shipsfuel} {OneOf("unter 25%", "{currentpercent}" )}{title}.
|elif currentfuel < 50:
   {shipsfuel} {OneOf("unter 50%", "{currentpercent}" )}{title}.
|elif currentfuel < 75:
   {Occasionally(2, "{shipsfuel} {currentpercent}{title}.")}
|elif currentfuel < 100:
   {Occasionally(3, "{shipsfuel} {currentpercent}{title}.")}
|else:
   {shipsfuel} {OneOf(currentpercent, "beim Maximum")}{title}.
}

Fuel check.defaultValue:
{_ Fuel Check _}
{_ Report fuel level _}

{_ Might be a taxi or multicrew session. We ignore those. We only want this when we're in our own ship. }
{if vehicle = "Ship" && ship && status:

    {if len(ship.compartments) > 0:
        {for compartment in ship.compartments:
            {if compartment.module && compartment.module.basename = "FuelScoop":    
                {set scoop_present to true}
            }
        }
    }

    {set shipsfuel to:
        Fuel {OneOf("levels", "tanks", "reserves")}
        {Occasionally(2, "are")} {Occasionally(2, "now")}
    }

    {set currentfuel to round(status.fuel_percent, 0)}
    {set currentpercent to:
        at {currentfuel} percent {Occasionally(2, "capacity")}
    }

    {set title to Occasionally(3,", {F('Honorific')}")}

    {set maxjump to status.fuel/ship.maxfuel}

    {if state.eddi_context_last_subject = "jump" && state.eddi_context_last_action = "complete" && maxjump < 1.25:
        Warning: {shipsfuel} almost depleted.
        {Pause(500)}
        {if scoop_present && !nextsystem.scoopable:
            {_ Invoke a RouteDetails() query to identify the nearest scoopable system }
            {set nearest_scoopable_system to RouteDetails('scoop')}
        |else:
            {if maxjump < 0.5:
                {OneOf("Refuel now", "Refuelling is extremely urgent")} {title}.
            |else:
                {OneOf("Please refuel", "Refuelling is strongly recommended")} {title}.
            }
        }
    |elif currentfuel < 25:
        {OneOf("Warning:", "Caution:", "Attention:")}
        {shipsfuel} {OneOf("below 25%", "{currentpercent}" )} {title}.
    |elif currentfuel < 50:
        {shipsfuel} {OneOf("below 50%", "{currentpercent}" )} {title}.
    |elif currentfuel < 75:
        {Occasionally(2, "{shipsfuel} {currentpercent} {title}.")}
    |elif currentfuel < 100:
        {Occasionally(3, "{shipsfuel} {currentpercent} {title}.")}
    |else:
        {ShipName()} {shipsfuel} {OneOf(currentpercent, "at maximum")} {title}.
    }
}

Fuel check2.script:
