Glide.script:
{Log("EVENT: Glide")}

{_ Context }
{SetState('eddi_context_last_subject', 'fsd')}
{SetState('eddi_context_last_action', 'glide')}
{SetState('eddi_context_system_name', event.system)}
{SetState('eddi_context_body_name', event.body)}
{SetState('eddi_context_body_system', event.system)}
{SetState('eddi_gravity', 0)}
{SetState("eddi_glide_end", false)}

{_ Fetch from context _}
{set reportbody to BodyDetails(event.body, event.system)} {_ oder event.bodyname und event.systemname _}
{set gravity to round(reportbody.gravity,2)}
{SetState('eddi_gravity', round(reportbody.gravity,2))}
{SetState('eddi_gravity_surface', round(reportbody.gravity,2))}

{if event.gliding:
    {Pause(3000)}
    Gleitflug {Occasionally(2, "jetzt")} {OneOf("eingeleitett.", "gestarted.")}
    {if !reportbody.gravity:
    	Achtung ! {Occasionally(2, "Ich habe")} keine Angaben zur {OneOf("Gravitation", "Anziehung")} !
    |else:
    	{if state.gravitation_ansagen = 1:
    		Zur Erinnerung! Eine
    		{if reportbody.gravity < 0.5 : geringe
    		|elif reportbody.gravity < 2  : mittlere
    		|elif reportbody.gravity < 3  : hohe
    		|elif reportbody.gravity < 4  : sehr hohe
    		|else                         : extrem hohe
    		}
    
    		Gravitation von {set value to (gravity)} {F("Humanise ValueGer")} G erwartet uns.
    		{if gravity > 3:
    			{OneOf("Achtung! Kommander, die Gravitation könnte bei der Landung unser Schiff beschädigen.
    			Erhöte Vorsicht ist geboten !", "Mutig mutig Kommander! Hier landen? Na wenn das mal gut geht")}
    		}
		|else:
		}    
    }	
|else: {SetState("eddi_glide_end", true)} Gleitflug beendet.
}

{_
- gliding 		The glide status (either true or false, true if entering a glide)
- systemname 	The system at which the commander is currently located
- bodyname 		The nearest body to the commander
- bodytype 		The type of the nearest body to the commander
_}

Glide.defaultValue:
{_ Context }
{SetState('eddi_context_last_subject', 'fsd')}
{SetState('eddi_context_last_action', 'glide')}
{SetState('eddi_context_system_name', event.systemname)}
{SetState('eddi_context_system_address', event.systemAddress)}
{SetState('eddi_context_body_name', event.bodyname)}
{SetState('eddi_context_body_system', event.systemname)}
{SetState('eddi_context_body_system_address', event.systemAddress)}

{if event.gliding:
    Glide {Occasionally(2, "mode")} 
    {OneOf("engaged.", "initiated.")}

    {Pause(1000)}
    {set body to BodyDetails(event.body, event.system)}
    {if !body.name || body.name = "" || !body.gravity:
        Planetary approach data not available.
    |else:
        {if body.landable:
            {set gravity to round(body.gravity,2)}
            {if gravity > 4:
                {if vehicle = "Ship": Danger! }
                {set gravityDescription to "an extremely high gravity world"}
            |elif gravity > 2:
                {if vehicle = "Ship": Caution! }
                {set gravityDescription to "a high gravity world"}
            |elif gravity > 0.5:
                {if vehicle = "Ship": Warning! }
                {set gravityDescription to "a medium gravity world"}
            |else:
                {set gravityDescription to "a low gravity world"}
            }
            {body.bodytype} {P(body.shortname, "body")} is {gravityDescription}, at {gravity} G.
        }
    }
|else:
    Glide completed.
}

Habitable zone.script:
{Log("Fktn.: Habitable zone")}

{_ Fetch from context }

{if event: {set reportbody to event} }
{if !event: {set reportbody to BodyDetails(state.eddi_context_star_star, state.eddi_context_star_system)}}

{_ Distance for target black-body temperature}
{_ From Jackie Silver's Hab-Zone Calculator https://forums.frontier.co.uk/showthread.php?p=5452081}

{set targetmin to 286}
{set targetmax to 225}

{set LS to 300000000}
{set rad to (pow((event.radius*1000), 2))}
{set temp to (pow(event.temperature, 4))}
{set zielmin to (pow(targetmin, 4))}
{set zielmax to (pow(targetmax, 4))}

{set hzmin1 to (rad / (4*zielmin))}
{set hzmin2 to (hzmin1 * temp)}
{set hzmin to (pow(hzmin2, 0.5)/ LS)}

{set hzmax1 to (rad / (4*zielmax))}
{set hzmax2 to (hzmax1 * temp)}
{set hzmax to (pow(hzmax2, 0.5)/ LS)}


{if hzmin > 10:
   Bewohnbare Zone
   {Occasionally(2, OneOf("wird berechnet mit", "fällt in den Bereich von"))}: 
   {set value to (round (hzmin))} {F("Humanise ValueGer")}
   bis {set value to (round (hzmax))} {F("Humanise ValueGer")}
   Lichtsekunden.
}

Hangar check.script:
{Log("Fktn.: Hangar check")}

{SetState('fighter_inst', false)}
{SetState('fighter_en', false)}
{SetState('srv_installed', false)}
{SetState('srv_enabled', false)}
{SetState('vehicles_count', 0)}
{set hangar_zwei to 0}
{set hangar_vier to 0}
{set hangar_six to 0}
{set hangar_gesamt to 0}
{SetState('hangar_gesamt_state', 0)}
{set hangar_zwei_en to false}
{set hangar_vier_en to false}
{set hangar_six_en to false}

{if GetState('eddi_liftoff') = 1 : {SetState('eddi_liftoff', 0)}
|else:
	{_ Check shipmodel _}
	{if ship.model = "Orca"							: {SetState('no_fighting', true)}
		|elif ship.model = "Asp"					: {SetState('no_fighting', true)}
		|elif ship.model = "Adder"					: {SetState('no_fighting', true)}
		|elif ship.model = "Beluga liner"			: {SetState('no_fighting', true)}
		|elif ship.model = "Diamondback explorer"	: {SetState('no_fighting', true)}
		|elif ship.model = "Diamondback scout"		: {SetState('no_fighting', true)}
		|elif ship.model = "Dolphin"				: {SetState('no_fighting', true)}
		|elif ship.model = "Hauler"					: {SetState('no_fighting', true)}
		|elif ship.model = "Type-7 Transporter"		: {SetState('no_fighting', true)}
		|elif ship.model = "Type-9 Heavy"			: {SetState('no_fighting', true)}
    										  			{SetState('slow_ship', true)}
	|else											: {SetState('no_fighting', false)}
	}
    
    {for compartment in ship.compartments:
        {if find(compartment.module.name,"Hangar") > -1: Hangarbelegung:}}
	{for compartment in ship.compartments:
    	{if compartment.module.name = "Fighter-Hangar": {SetState('fighter_inst', true)}
        	{if compartment.module.enabled	: {SetState('fighter_en', true)} Ein Jäger kann uns unterstützen.
        	|else: Es ist kein Jäger an Bord.}
        |elif compartment.module.name = "Planetenfahrzeug-Hangar":
			{if compartment.size = 2:
				{set hangar_zwei to hangar_zwei + 1}
        		{if compartment.module.enabled: {set hangar_zwei_en to true}
        		|else:							{set hangar_zwei_en to false}}
        	}
        	{if compartment.size = 4:
        		{set hangar_vier to hangar_vier + 1}
        		{if compartment.module.enabled	:	{set hangar_vier_en to true}
        		|else:								{set hangar_vier_en to false}}
        	}
        	{if compartment.size = 6:
        		{set hangar_vier to hangar_six + 1}
        		{if compartment.module.enabled	:	{set hangar_six_en to true}
        		|else:								{set hangar_six_en to false}}
        	}
        	{set hangar_gesamt to hangar_zwei + hangar_vier + hangar_six}
    	}
	}
    {if capi_active: {F("Launchbay report new")}
    |else:
        {if hangar_gesamt > 0:
            {if hangar_zwei > 0 && hangar_zwei_en = true:
                {if hangar_zwei = 1:{char(10)}{char(13)}Es ist ein |else: {hangar_zwei}}
                Fahrzeug-Hangar der Größe 2}
            {if hangar_vier > 0 && hangar_vier_en = true:
