JumpFuel.script:
{Log("Fktn.: JumpFuel")}

{_ Report fuel level for max range jumps remaining}
{Pause(1000)}

{set fuellevel to status.fuel_percent}
{set fuelremaining to round(status.fuel,1)}
{set maxfuel to ship.maxfuelperjump}

{set maxjump to round(status.fuel,1)/ship.maxfuelperjump}

{if fuellevel < 25      : Warnung,
|elif fuellevel < 50    : Achtung,}

{set trim to fuellevel} {F("ZeroTrim")} {set fuellevel to trim}

{if fuellevel < 50: Treibstoff{OneOf("vorrat","reserven")} bei {fuellevel}% {Occasionally("Kapazität")},}

{if maxjump < 1:
    {OneOf(
    "Wir haben nicht genug Treibstoff für unsere maximale Sprungreichweite.",
    "Zu wenig Treibstoff!",
    "Treibstoff ist fast alle!")}
    Auftanken {OneOf("so schnell wie möglich","bei der {OneOf('ersten','nächsten')} Gelegenheit,")}
|elif maxjump < 2:
    {OneOf(
    "Wir haben nur noch Treibstoff für einen weiteren Sprung mit maximaler Reichweite,",
    "Nur noch ein weiterer Sprung mit maximaler Reichweite möglich,")}
}

