Limpet check.script:
{Log("Fktn.: Limpet check")}

{if ship.model = "panthermkii" || ship.model = "Panther Clipper Mk. II" || ship.model = "PantherMkII":
    {set ship_cargocapacity to state.panther_clipper_cap}
|else:
    {set ship_cargocapacity to ship.cargocapacity}
}

{set  controllers to 0}
{for compartment in ship.compartments:
    {if find(compartment.module.name, "Steuerung") != -1			: {set controllers to controllers + 1}
    |elif find(compartment.module.name, "Steuereinheit") != -1		: {set controllers to controllers + 1}
    |elif find(compartment.module.name, "Drohnensteuerung") != -1	: {set controllers to controllers + 1}
    }
}

{set limpets to 0}
{for cargo in inventory:
    {if cargo.edname = "Drohne"		: {set limpets to cargo.total}
    |elif cargo.edname = "Drohnen"	: {set limpets to cargo.total}
    |elif cargo.edname = "Drones"	: {set limpets to cargo.total}
    }
}

{set tenpercent to ship_cargocapacity / 10 - 1}

{if ship.role = "Exploration" || ship.role = "Erkundung":
|else:
	{if controllers > 0 && limpets = 0:
        {if GetState('eddi_context_last_subject') = 'undocked':
            {OneOf("Zur Erinnerung: Wir haben einen Drohnen Kontroler verbaut, aber keine Drohnen geladen.",
            "ey, Du hast Dronen vergessen!", "Wir haben keine Dronen mit! Käpptn!",
            "Willst Du alles mit der Hand einsammeln? Wir haben keine Dronen mit! Käpptn!",
            "Dronen! Dronen nicht vergessen, mein Käpptn!", "Mist, wir haben vergessen die Drohnen aufzufüllen!")}
        |else:
            {OneOf("Zur Erinnerung: Wir haben einen Drohnen Kontroler verbaut, aber keine Drohnen geladen.",
            "äh, Dronen sind alle, Käpptn!", "Wir haben keine Dronen mehr! Käpptn!",
            "Willst Du alles mit der Hand einsammeln? Wir haben keine Dronen mit! Käpptn!",
            "Dronen! Dronen nicht vergessen, mein Käpptn!")}}
	|elif controllers = 0 && limpets > 0:
	       {OneOf("Wir haben Drohnen geladen, jedoch keinen Drohnen Kontroler verbaut.", 
	       "Achtung! Frachtraum wird von Dronen belegt, Käpptn!", "Drohnen? Brauchen wir denn welche, Käpptn?",
            "Käpptn, wir brauchen keine Dronen!")}
	|elif controllers > 0 && limpets < tenpercent:
            Es sind nur noch {set value to limpets} {F("Humanise ValueGer")} Drohnen an Bord.
            {OneOf("Sie sollten eine Aufstockung des Drohnen Vorrats in Erwägung ziehen.", "Käpptn, die Dronen
            sind fast alle.", "Wir haben fast alle Drohnen verbraucht, Käpptn!", 
            "Verdammt, die Drohnen sind fast alle!")}
	}
}

Limpet check.defaultValue:
{set  controllers to 0}
{for compartment in ship.compartments:
    {if find(compartment.module.name, "Controller") != -1:
        {set controllers to controllers + 1}
    }
}

{set limpets to 0}
{for cargo in inventory:
    {if cargo.edname = "Drones":
        {set limpets to cargo.total}
    }
}

{set tenpercent to ship.cargocapacity / 10 - 1}
{if controllers > 0 && limpets = 0:
    Reminder: you have a limpet controller but are not carrying any limpets.
|elif controllers = 0 && limpets > 0:
    You are carrying limpets but no limpet controller.
|elif controllers > 0 && limpets < tenpercent:
    You only have {limpets} {if limpets = 1: limpet |else: limpets} on-board; you might want to consider re-stocking.
}

Limpet launched.script:
{Log("EVENT: Limpet launched")}

{if ship.role != "Bergbau":
	{if event.kind = "Collection"          : Sammeldrohne
	|elif event.kind = "Hatchbreaker"      : Lukenöffnerdrohne
	|elif event.kind = "FuelTransfer"      : Treibstofftransferdrohne
	|elif event.kind = "Prospector"        : Erzsucherdrohne
	|elif event.kind = "Repair"            : Reparaturdrohne
	|elif event.kind = "Decontamination"   : Entseuchungsdrohne
	|elif event.kind = "Research"          : Forschungsdrohne
	|else: {OneOf('Drohne','Roboter')}
	} gestartet.
}

{_ inventory[0].name}

{_ - kind 	The kind of limpet launched,
			"Hatchbreaker", "FuelTransfer", "Collection", "Prospector", "Repair", 
			"Research", "Decontamination"
_}

Limpet purchased.script:
{Log("EVENT: Limpet purchased")}

{if event.amount = 1: Eine |else: {event.amount}} Drohne{if event.amount > 1:n}
{if event.amount = 1: wurde |else: {Occasionally(2, "wurden")}}

{OneOf("unserem", "{ShipName()}s")}
{OneOf("Frachtraum", "Inventar")} hinzugefügt.

