Location.script:
{Log("EVENT: Location")} 

{_ Context _}
{SetState('eddi_context_last_subject', 'location')}
{SetState('eddi_context_system_name', event.systemname)}
{SetState('eddi_context_body_name', event.bodyname)}
{SetState('eddi_context_body_type', event.bodytype)}
{SetState('eddi_context_body_system', event.systemname)}
{SetState('eddi_spielstart', true)}

{F("A_Einstellungen")}

{set reportBody to BodyDetails(event.bodyname, event.systemname)}

Wir befinden uns {_ "On Foot" in der Station oder auf dem Planeten _} 
{if vehicle = "On Foot" : zu Fuß
    {if event.bodytype = "Planet" : auf dem Planeten {event.bodyname}
    |elif event.bodytype = "Mond" : auf dem Mohnd {event.bodyname}
    |elif event.bodytype = "Station" : 
       {set station to StationDetails(event.station, event.systemname)}
       {set station_missions to F("Mission check station")}
       {set station_crimes to F("Crime check station")}
       {if event.stationtype = "AsteroidBase"  || event.stationtype = "Asteroidenbasis"          : in der {event.stationtype}
       |elif event.stationtype = "Bernal"  || event.stationtype = "Ocellus Sternenhafen"         : in dem {event.stationtype}
	   |elif event.stationtype = "Coriolis"  || event.stationtype = "Coriolis Sternenhafen"      : in dem {event.stationtype}
	   |elif event.stationtype = "Dodec"  || event.stationtype = "Dodec-Sternenhafen"            : in dem {event.stationtype}
	   |elif event.stationtype = "Megaship"  || event.stationtype = "Megaschiff"                 : auf dem {event.stationtype}
	   |elif event.stationtype = "MegaShipCivilian"  || event.stationtype = "Ziviles Megaschiff" : auf dem {event.stationtype}
	   |elif event.stationtype = "Ocellus"  || event.stationtype = "Ocellus Sternenhafen"        : in dem {event.stationtype} 
	   |elif event.stationtype = "Orbis"  || event.stationtype = "Orbis Sternenhafen"            : in dem {event.stationtype}
	   |elif event.stationtype = "Outpost"  || event.stationtype = "Außenposten"                 : auf dem {event.stationtype}
	   |elif event.stationtype = "SurfaceStation"  || event.stationtype = "Bodenstation"         : in der {event.stationtype} {_ nicht länger genutzt _}
	   |elif event.stationtype = "CraterOutpost"  || event.stationtype = "Planetarer Außenposten": auf dem planetaren Außenposten
	   |elif event.stationtype = "CraterPort"  || event.stationtype = "Raumhafen"                : in dem {event.stationtype}
	   |elif event.stationtype = "OutpostScientific"  || event.stationtype = "Wissenschaftlicher Außenposten": auf dem wissenschaftlichen Außenposten
	   |elif event.stationtype = "OnFootSettlement" || event.stationtype = "Siedlung"            : in der {event.stationtype} 
       |elif event.stationtype = "Carrier" || event.stationtype = "FleetCarrier":
        {if station.name = state.eigener_carrier :
                auf unserem {OneOf("Carrier", "Träger", "Flottenträger")}
        |else:  auf dem {OneOf("Carrier", "Träger", "Flottenträger")}
                {set translate to (station.name)}
                {F('Translate FleetCarrier')} {_ oder event.station _}
        }
    |else: in der Station {set translate to event.bodyname} {F("Translate Stationsnamen")} {state.station_name},
    }
    {set system_missions to F("Mission check system")}
    {set system_crimes to F("Crime check system")}
	} im System {event.systemname},
	
|elif vehicle = "SRV" : {SetState("use_srv", true)}
	im {OneOf("Buggy", "SRV")} auf dem
	{if event.bodytype = "Planet" : Planeten
	|elif event.bodytype = "Mond" : Mohnd
	|else: auf {event.bodytype}
	} {event.bodyname} im System {event.systemname}
	
|elif vehicle = "Ship" :
	in unserem Schiff {ship.name} vom Typ {set translate to ship.model} {F("Translate Ship Names")},
    {SetState("use_ship", true)}
    {if event.docked : {char(10)}angedockt
        {set station to StationDetails(event.station, event.system)}
		{set station_missions to F("Mission check station")}
		{set station_crimes to F("Crime check station")}
		
		{if find(event.station, "ColonisationShip") >-1 : auf dem Kolonisationsschiff
		|elif event.stationtype = "None" || event.stationtype = "Unbekannte Station":
            auf einer unbekannten Station {set translate to event.station} {F("Translate Stationsnamen")} {state.station_name}
        |elif event.stationtype = "AsteroidBase"  || event.stationtype = "Asteroidenbasis":
            auf der {event.stationtype} {set translate to event.station} {F("Translate Stationsnamen")} {state.station_name}
		|elif event.stationtype = "Bernal"  || event.stationtype = "Ocellus Sternenhafen":
            auf dem {event.stationtype} {set translate to event.station} {F("Translate Stationsnamen")} {state.station_name}
		|elif event.stationtype = "Coriolis"  || event.stationtype = "Coriolis Sternenhafen":
            auf dem {event.stationtype} {set translate to event.station} {F("Translate Stationsnamen")} {state.station_name}
		|elif event.stationtype = "Megaship"  || event.stationtype = "Megaschiff":
            auf dem {event.stationtype} {set translate to event.station} {F("Translate Stationsnamen")} {state.station_name}
		|elif event.stationtype = "MegaShipCivilian"  || event.stationtype = "Ziviles Megaschiff":
            auf dem {event.stationtype} {set translate to event.station} {F("Translate Stationsnamen")} {state.station_name}
		|elif event.stationtype = "Ocellus"  || event.stationtype = "Ocellus Sternenhafen":
            auf dem {event.stationtype} {set translate to event.station} {F("Translate Stationsnamen")} {state.station_name}
		|elif event.stationtype = "Orbis"  || event.stationtype = "Orbis Sternenhafen":
            auf dem {event.stationtype} {set translate to event.station} {F("Translate Stationsnamen")} {state.station_name}
        |elif event.stationtype = "Dodec"  || event.stationtype = "Dodec-Sternenhafen":
            auf dem {event.stationtype} {set translate to event.station} {F("Translate Stationsnamen")} {state.station_name}
		|elif event.stationtype = "Outpost"  || event.stationtype = "Außenposten":
            auf dem {event.stationtype} {set translate to event.station} {F("Translate Stationsnamen")} {state.station_name}
		|elif event.stationtype = "OutpostScientific"  || event.stationtype = "Wissenschaftlicher Außenposten":
            auf dem wissenschaftlichen Außenposten {event.stationtype} 
            {set translate to event.station} {F("Translate Stationsnamen")} {state.station_name} auf
            {set shortname to reportBody.shortname} {F("Translate Himmelskörper")}
		|elif event.stationtype = "SurfaceStation"  || event.stationtype = "Bodenstation": {_ nicht länger genutzt _}
            auf der {event.stationtype} {set translate to event.station} {F("Translate Stationsnamen")} {state.station_name} auf
            {reportBody.bodytype} {set shortname to reportBody.shortname} {F("Translate Himmelskörper")}
		|elif event.stationtype = "CraterOutpost"  || event.stationtype = "Planetarer Außenposten":
            auf dem planetaren Außenposten {set translate to event.station} {F("Translate Stationsnamen")} {state.station_name} auf 
            {reportBody.bodytype} {set shortname to reportBody.shortname} {F("Translate Himmelskörper")}
		|elif event.stationtype = "CraterPort"  || event.stationtype = "Raumhafen": 
            in dem {event.stationtype} {set translate to event.station} {F("Translate Stationsnamen")} {state.station_name} auf 
            {reportBody.bodytype} {set shortname to reportBody.shortname} {F("Translate Himmelskörper")}
		|elif event.stationtype = "Carrier" || event.stationtype = "FleetCarrier": 
    		{if station.name = state.eigener_carrier : auf unserem {OneOf("Carrier", "Träger", "Flottenträger")}
    		|else: dem {OneOf("Carrier", "Träger", "Flottenträger")} {set translate to (station.name)} 
    		      {F('Translate FleetCarrier')} {_ oder event.station _}
    		}
		|else:
            in der Station {_ event.station} 
            {if find(event.station, "Station") >= 0 : {set translate to event.station} {F("Translate Stationsnamen")} {state.station_name}
            |else:
                {if event.station = "Jameson Memorial": Dschejmessen Mimorriel |else: {event.station}}
            }auf {reportBody.bodytype} {set shortname to reportBody.shortname} {F("Translate Himmelskörper")}
		}	
	{_ im Schiff auf dem Planeten _}	
	|elif event.bodytype = "Planet" || event.bodytype = "Mond" :
		{set system_missions to F("Mission check system")}
		{set system_crimes to F("Crime check system")}
		{if event.longitude || event.latitude:
		      auf {reportBody.bodytype} {set shortname to reportBody.shortname} {F("Translate Himmelskörper")},
		|else: {OneOf("in der Nähe", "in Sichtweite")}
			{if reportBody.shortname = event.systemname: des Primärgestirns,
			|elif reportBody.bodytype:
		      vom {reportBody.bodytype} {set shortname to reportBody.shortname} {F("Translate Himmelskörper")},
			|else: vom {event.bodyname}
			}
		}
	|else:
		im deep space,
		{if event.bodytype = "Star": in der Nähe von Stern {event.bodyname}
		|elif event.bodytype = "Planet" || event.bodytype = "Mond": in der Nähe von 
		  {set shortname to reportBody.shortname} {F("Translate Himmelskörper")}
		}
		{set system_missions to F("Mission check system")}
		{_ powerplay_report to F "Powerplay report")}
		{_ state_report to F "System state report")}
		{_ system_engineer to F "Engineer report")}
		{set system_crimes to F("Crime check system")}
	}
	im System {event.systemname}
}

{if powerplay_report || state_report || system_engineer || station_missions || station_crimes || system_missions || system_crimes:
    {Pause(500)}
    Infos:
    {if powerplay_report:
        {Pause(500)}
        {powerplay_report}
    }
    {if state_report:
        {Pause(500)}
        {state_report}
    }
    {if system_engineer:
        {Pause(500)}
        {system_engineer}
    }
    {if station_missions:
        {Pause(500)}
        {station_missions}
    }
    {if station_crimes:
        {Pause(500)}
        {station_crimes}
    }
    {if system_missions:
        {Pause(500)}
        {system_missions}
    }
    {if system_crimes:
        {Pause(500)}
        {system_crimes}
    }
}

{if (ship.Role.edname = "Exploration" || ship.Role.edname = "Multipurpose") && !event.docked:
    {Pause(500)}
    {F("Bodies Exploration Report")}
}

{_
bodyId              - The numeric ID of the body nearest to the commander (if any) 
bodyname            - The nearest body to the commander 
bodytype            - The type of the nearest body to the commander 
conflicts           - A list of conflict objects describing any conflicts between factions in the system, if any 
conflicts[<index>].conflictdays 
conflicts[<index>].faction1 
conflicts[<index>].faction1dayswon 
conflicts[<index>].faction2 
conflicts[<index>].faction2dayswon 
conflicts[<index>].margin 
conflicts[<index>].stake 
conflicts[<index>].state 
conflicts[<index>].status 
contestingpowers    - The localized names of powerplay powers with at least 30% progress towards acquiring control 
                        of an uncontrolled star system, if any, in descending order 
ContestingPowers    - Powerplay powers with at least 30% progress towards acquiring control of an uncontrolled star system, 
                        if any, as objects, in descending order 
contestingpowers[<index>] 
ContestingPowers[<index>].invariantName 
ContestingPowers[<index>].name 
distancefromstar    - The distance of the nearest body (when close) from the main star 
docked              - True if the commander is docked 
economy             - The economy of the system in which the commander resides 
economy2            - The secondary economy of the system in which the commander resides, if any 
factions            - A list of faction objects describing the factions in the system, if any 
factions[<index>].allegiance    - The localized allegiance of the faction, if known 
factions[<index>].Allegiance    - The allegiance of the faction, if known, as an object 
factions[<index>].Allegiance.invariantName 
factions[<index>].Allegiance.name 
factions[<index>].government    - The localized government of the faction, if known 
factions[<index>].Government    - The government of the faction, if known, as an object 
factions[<index>].Government.invariantName 
factions[<index>].Government.name 
factions[<index>].myreputation  - Your reputation with the faction, out of 100% 
factions[<index>].name          - The name of the faction 
factions[<index>].presences     - A list of FactionPresence objects. Unless called from the FactionDetails() function, 
                                    only details from the current system will be included here 
factions[<index>].presences[<index>].ActiveStates - (For recently visited systems only) A list of FactionState objects 
factions[<index>].presences[<index>].ActiveStates[<index>].invariantName 
factions[<index>].presences[<index>].ActiveStates[<index>].name 
factions[<index>].presences[<index>].FactionState - The faction's current dominant state in this system, as an object 
factions[<index>].presences[<index>].FactionState.invariantName 
factions[<index>].presences[<index>].FactionState.name 
factions[<index>].presences[<index>].happiness - (For recently visited systems only) The current localized happiness level of the faction within the system 
factions[<index>].presences[<index>].Happiness - (For recently visited systems only) The current happiness level of the faction within the system, as an object 
factions[<index>].presences[<index>].Happiness.invariantName 
factions[<index>].presences[<index>].Happiness.name 
factions[<index>].presences[<index>].influence - The faction's influence level within the system, as a percentage 
factions[<index>].presences[<index>].PendingStates - (For recently visited systems only) A list of pending FactionState objects and trend values 
factions[<index>].presences[<index>].PendingStates[<index>].factionState 
factions[<index>].presences[<index>].PendingStates[<index>].factionState.invariantName 
factions[<index>].presences[<index>].PendingStates[<index>].factionState.name 
factions[<index>].presences[<index>].PendingStates[<index>].trend 
factions[<index>].presences[<index>].RecoveringStates - (For recently visited systems only) A list of recent prior FactionState objects and trend values 
factions[<index>].presences[<index>].RecoveringStates[<index>].factionState 
factions[<index>].presences[<index>].RecoveringStates[<index>].factionState.invariantName 
factions[<index>].presences[<index>].RecoveringStates[<index>].factionState.name 
factions[<index>].presences[<index>].RecoveringStates[<index>].trend 
factions[<index>].presences[<index>].squadronhomesystem - (For recently visited systems only) True if the faction is the pilot's current squadron faction 
factions[<index>].presences[<index>].state - The faction's current (localized) dominant state in this system 
factions[<index>].presences[<index>].systemAddress - The unique 64 bit system address where this faction has a presence 
factions[<index>].presences[<index>].systemName - The system name where this faction hasa presence 
factions[<index>].squadronfaction - True if the faction is the pilot's current squadron faction 
latitude                - The latitude of the commander (if on the ground) 
longitude               - The longitude of the commander (if on the ground) 
marketId                - The market ID of the station at which the commander is docked 
nearbypowers            - The localized names of powerplay powers having star systems with acquisition radii which overlap the star system, 
                            less the controlling power, if any 
NearbyPowers            - Powerplay powers having star systems with acquisition radii which overlap the star system, 
                            less the controlling power, if any, as objects 
nearbypowers[<index>] 
NearbyPowers[<index>].invariantName 
NearbyPowers[<index>].name 
population              - The population of the system to which the commander has jumped 
power                   - The localized powerplay power controlling the star system, if any. If the star system is Unoccupied or Contested 
                            then this will be empty. 
Power                   - The powerplay power controlling the star system, if any, as an object. 
Power.invariantName 
Power.name 
powerAcquisitionProgress - The progress of nearby powerplay powers towards obtaining control of the star system, as a list of objects
                            with keys Power (as an object) and progress (as a percent) 
powerControlProgress    - The percent progress of the controlling power, if any, in consolidating control over the star system. 
                            Values below 0% indicate a reduction in the control level at the end of the cycle while values above 100% 
                            indicate an increase in the control level at the end of the cycle (if the current control state is less than 'Stronghold') 
powerReinforcementControlPoints - The control points accumulated by the controlling power via powerplay reinforcement activities during the current cycle 
powerstate                      - The localized state of powerplay efforts within the star system 
PowerState                      - The state of powerplay efforts within the star system, as an object 
PowerState.invariantName 
PowerState.name 
powerUnderminingControlPoints   - The control points lost by the controlling power via powerplay undermining activities during the current cycle 
security                        - The security of the system in which the commander resides 
station                         - The name of the station at which the commander is docked 
stationallegiance               - The super power allegiance of the station, if the commander is docked 
stationfaction                  - The faction controlling the station, if the commander is docked 
stationgovernment               - The government of the station, if the commander is docked 
stationstate                    - The state of the faction controlling the station, if the commander is docked 
stationtype                     - The type of the station at which the commander is docked 
systemAddress                   - The numeric system address of the star system in which the commander is located 
systemallegiance                - The super power allegiance of the star system in which the commander is located 
systemfaction                   - The faction controlling the system in which the commander is located 
systemgovernment                - The government of the system in which the commander is located 
systemname                      - The name of the system in which the commander is located 
systemstate                     - The state of the faction controlling the system in which the commander is located 
ThargoidWar                     - Thargoid war data, when applicable 
ThargoidWar.CurrentState        - The current phase of the Thargoid war, as a localizable object. 
ThargoidWar.CurrentState.invariantName 
ThargoidWar.CurrentState.name 
ThargoidWar.FailureState        - The next phase of the Thargoid war, if success is not achieved, as a localizable object. 
ThargoidWar.FailureState.invariantName 
ThargoidWar.FailureState.name 
ThargoidWar.progress            - Percent progress in achieving a success state in the current phase of the Thargoid war. 
                                    Progress can decrease if success is not reached before the weekly 'tick'. 
