    {set material to MaterialDetails(material)}

    {_ Context }
    {SetState('eddi_context_last_subject', 'material')}
    {SetState('eddi_context_material_name', material.name)}
}

{if !material:
    {if len(state.eddi_context_material_name) > 0:
        {_ Fetch from context }
        {set material to MaterialDetails(state.eddi_context_material_name)}
    }
}

{if material && material.name:
    {set uses to len(material.blueprints)}
    {if slice(material.name, len(material.name)-1) = 's':
        {set plural to 'wird'}
    |else:
        {set plural to 'ist'}
    }
    {if uses = 0:
      {material.name} wird für keinen Bauplan benötigt
    |elif uses = 1:
        {set name to cat(material.blueprints[0].name, material.blueprints[0].modulename, ' Grad ', material.blueprints[0].grade)}
        {material.name} {plural} für den Bauplan {name} benötigt
    |elif uses = 2:
        {set name0 to cat(material.blueprints[0].name, material.blueprints[0].modulename, ' Grad ', material.blueprints[0].grade)}
        {set name1 to cat(material.blueprints[1].name, material.blueprints[1].modulename, ' Grad ', material.blueprints[1].grade)}
        {material.name} {plural} für den Bauplan {name0} und den Bauplan {name1} benötigt
    |else:
        {material.name} {plural} genutzt für die Baupläne {len(material.blueprints)}. Diese sind
        {set cur to 0}
        {while cur < len(material.blueprints):
            {set name to cat(material.blueprints[cur].name, material.blueprints[cur].modulename, ' Grad ', material.blueprints[cur].grade)}
            {if cur = 0:
                {name}
            |elif cur < len(material.blueprints) - 1:
                , {name}
            |else:
                , und {name}
            }
            {set cur to cur + 1}
        }
    }
|else:
    Ich habe keine Informationen über {if material = " " : {material} |else: dieses Material}.
}.

