Materials discard report.script:
{Log("Fktn.: Materials discard report")}

{_ Find those that are above maximum, desired and minimum}
{set shoulddiscardmaterials to []}
{set coulddiscardmaterials to []}
{set lastdiscardmaterials to []}

{for material in materials:
    {if material.maximum && material.amount > material.maximum:
      {set shoulddiscardmaterials to cat(shoulddiscardmaterials, [material])}
    |elif material.desired && material.amount > material.desired:
      {set coulddiscardmaterials to cat(coulddiscardmaterials, [material])}
    |elif material.minimum && material.amount > material.minimum:
      {set lastdiscardmaterials to cat(lastdiscardmaterials, [material])}
    }
}

{if len(shoulddiscardmaterials) > 0:
    Du bist über dem Maximum Level für 
    {if len(shoulddiscardmaterials) = 1: ein Material |else: {len(shoulddiscardmaterials)} Materiale}. Du kannst
    {if len(shoulddiscardmaterials) = 1:
        {if shoulddiscardmaterials[0].amount - shoulddiscardmaterials[0].maximum = 1 : ein Mal
        |else: {shoulddiscardmaterials[0].amount - shoulddiscardmaterials[0].maximum} 
        }
        {shoulddiscardmaterials[0].material}
    |elif len(shoulddiscardmaterials) = 2:
        {shoulddiscardmaterials[0].amount - shoulddiscardmaterials[0].maximum} {shoulddiscardmaterials[0].material} und 
        {shoulddiscardmaterials[1].amount - shoulddiscardmaterials[1].maximum} {shoulddiscardmaterials[1].material}
    |else:
        {set cur to 0}
        {while cur < len(shoulddiscardmaterials):
            {if cur = 0:
                {shoulddiscardmaterials[cur].amount - shoulddiscardmaterials[cur].maximum} {shoulddiscardmaterials[cur].material}
            |elif cur < len(shoulddiscardmaterials) - 1:
                , {shoulddiscardmaterials[cur].amount - shoulddiscardmaterials[cur].maximum} {shoulddiscardmaterials[cur].material}
            |else:
                , und {shoulddiscardmaterials[cur].amount - shoulddiscardmaterials[cur].maximum} {shoulddiscardmaterials[cur].material}
            }
            {set cur to cur + 1}
        }
    } entsorgen.
|elif len(coulddiscardmaterials) > 0:
    Du bist über dem gewünschten Level für 
    {if len(coulddiscardmaterials) = 1: ein Material |else: {len(coulddiscardmaterials)} Materiale}. Du kannst
    {if len(coulddiscardmaterials) = 1:
        {if coulddiscardmaterials[0].amount - coulddiscardmaterials[0].desired = 1 : ein Mal 
        |else: {coulddiscardmaterials[0].amount - coulddiscardmaterials[0].desired}
        } 
        {coulddiscardmaterials[0].material}
    |elif len(coulddiscardmaterials) = 2:
        {coulddiscardmaterials[0].amount - coulddiscardmaterials[0].desired} {coulddiscardmaterials[0].material} und 
        {coulddiscardmaterials[1].amount - coulddiscardmaterials[1].desired} {coulddiscardmaterials[1].material}
    |else:
        {set cur to 0}
        {while cur < len(coulddiscardmaterials):
            {if cur = 0:
                {coulddiscardmaterials[cur].amount - coulddiscardmaterials[cur].desired} {coulddiscardmaterials[cur].material}
            |elif cur < len(coulddiscardmaterials) - 1:
                , {coulddiscardmaterials[cur].amount - coulddiscardmaterials[cur].desired} {coulddiscardmaterials[cur].material}
            |else:
                , und {coulddiscardmaterials[cur].amount - coulddiscardmaterials[cur].desired} {coulddiscardmaterials[cur].material}
            }
            {set cur to cur + 1}
        }
    } entsorgen.
|elif len(lastdiscardmaterials) > 0:
    Du bist über dem Minimum Level für 
    {if len(lastdiscardmaterials) = 1: ein Material |else: {len(lastdiscardmaterials)} materiale}. Du kannst
    {if len(lastdiscardmaterials) = 1:
        {if lastdiscardmaterials[0].amount - lastdiscardmaterials[0].minimum = 1 : ein Mal 
        |else: {lastdiscardmaterials[0].amount - lastdiscardmaterials[0].minimum} {lastdiscardmaterials[0].material}
        }
    |elif len(lastdiscardmaterials) = 2:
        {lastdiscardmaterials[0].amount - lastdiscardmaterials[0].minimum} {lastdiscardmaterials[0].material} und 
        {lastdiscardmaterials[1].amount - lastdiscardmaterials[1].minimum} {lastdiscardmaterials[1].material}
    |else:
        {set cur to 0}
        {while cur < len(lastdiscardmaterials):
            {if cur = 0:
                {lastdiscardmaterials[cur].amount - lastdiscardmaterials[cur].minimum} {lastdiscardmaterials[cur].material}
            |elif cur < len(lastdiscardmaterials) - 1:
                , {lastdiscardmaterials[cur].amount - lastdiscardmaterials[cur].minimum} {lastdiscardmaterials[cur].material}
            |else:
                , und {lastdiscardmaterials[cur].amount - lastdiscardmaterials[cur].minimum} {lastdiscardmaterials[cur].material}
            }
            {set cur to cur + 1}
        }
    } entsorgen.
|else:
    Du kannst nichts wegwerfen ohne dein Minimum Level zu unterschreiten.
}

