Mission abandoned.script:
{Log("EVENT: Mission abandoned")}

{_ Context }
{SetState('eddi_context_last_subject', 'mission')}
{SetState('eddi_context_last_action', 'abandon')}

{set mission to MissionDetails(event.missionid)}

{Pause(500)}
{if event.communal:
    Sie haben das Gemeinschaftsziel {mission.localisedname} abgebrochen.
|else:
    {  if find(mission.invariantTags, "Rescue") > -1        :{set type to "Rettungsmission"}
    |elif find(mission.invariantTags, "assassinate") > -1   :{set type to "Mordauftrag"}
    |elif find(mission.invariantTags, "collect") > -1       :{set type to "Beschaffungsmission"}
    |elif find(mission.invariantTags, "Courier") > -1       :{set type to "Kuriermission"}
    |elif find(mission.invariantTags, "Delivery") > -1      :{set type to "Liefermission"}
    |elif find(mission.invariantTags, "Disable") > -1       :{set type to "Oberflächeneinrichtung abschalten"}
    |elif find(mission.invariantTags, "Hack") > -1          :{set type to "Datenbeschaffung"}
    |elif find(mission.invariantTags, "Expedition") > -1    :{set type to "Entdeckungsmission"}
    |elif find(mission.invariantTags, "Massacre") > -1      :{set type to "Massakermission"}
    |elif find(mission.invariantTags, "Massaker") > -1      :{set type to "Massakermission"}    
    |elif find(mission.invariantTags, "Mining") > -1        :{set type to "Bergbaumission"}
    |elif find(mission.invariantTags, "On Foot") > -1       :{set type to "Bodenmission"}    
    |elif find(mission.invariantTags, "Passenger") > -1     :{set type to "Passagiertransport"}
    |elif find(mission.invariantTags, "Transport") > -1     :{set type to "Passagiertransport"}
    |elif find(mission.invariantTags, "Salvage") > -1       :{set type to "Bergungsmission"}
    |elif find(mission.invariantTags, "Scan") > -1          :{set type to "Oberflächen-Scann"}
    |elif find(mission.invariantTags, "Sightseeing") > -1   :{set type to "Besichtigungsmission"}
    |elif find(mission.invariantTags, "Smuggle") > -1       :{set type to "Schmuggelmission"}
    |elif find(mission.invariantTags, "Credits") > -1       :{set type to "Spendenmission"}
    |elif find(mission.invariantTags, "assassination") > -1 :{set type to "Mordanschlag"}
    |elif find(mission.invariantTags, "Bulk") > -1          :{set type to "Gruppentransport"}
    |elif find(mission.invariantTags, "Chain") > -1         :{set type to "Folgemission"}
    |elif find(mission.invariantTags, "Evakuation") > -1    :{set type to "Evakuierungmission"}
    |elif find(mission.invariantTags, "Offline") > -1       :{set type to "Siedlung ausschalten"}
    |elif find(mission.invariantTags, "Onslaught") > -1     :{set type to "Siedlung überfallen"}
    |elif find(mission.invariantTags, "Sabotage") > -1      :{set type to "Siedlung sabotieren"}
    |elif find(mission.invariantTags, "Planetary") > -1     :{set type to OneOf("Oberflächenmission", "Planetenmission")}
    |elif find(mission.invariantTags, "Special") > -1       :{set type to "Spezialmission"}
    |elif find(mission.invariantTags, "Skimmer") > -1       :{set type to "Skimmer zerstören"}
    |elif find(mission.invariantTags, "Power") > -1         :{set type to "Siedlung herunterfahren"}
    |elif find(mission.invariantTags, "Thargoid") > -1      :{set type to "Thargoiden zerstören"}
    |elif find(mission.invariantTags, "Scout") > -1         :{set type to "Thargoiden zerstören"}
    |elif find(mission.invariantTags, "RefugeeBulk") > -1   :
        {set type to: {OneOf("Flüchtlingsgruppe ", "Flüchtlinge ")} {OneOf("ausfliegen", "evakuieren")}}
    |elif find(mission.name, "Flüchtlingsgruppe") > -1      :
        {set type to: {OneOf("Flüchtlingsgruppe ", "Flüchtlinge ")} {OneOf("ausfliegen", "evakuieren")}}
	|else:
    	{set type to mission.tags[0]}
    	{_ if !type : set type to "Mission" else: }
	}
    {_ set type to cat(type, "")}
    
    {if find(type, "mission") >-1 : {type} |else: Mission {type}}
    {if mission.faction: für die Fraktion {mission.faction}} abgebrochen,
}

{if event.fine:
    Du hast eine Geldstrafe von {Humanise(event.fine)} credditts
    {if mission.commodity: 
        für den Diebstahl von {mission.commodity}
    |elif mission.microresource:
        für den Diebstahl von {mission.microresource}
    }erhalten.
}

{SetState('missions_abandoned', true)}
{SetState('missions_recalc', true)}
{SetState('routedetails_count', missionsCount)}

{if environment != "Docked":
    {F("Missions Route Update")}
}

{_
- missionid 	The ID of the mission
- name 			The name of the mission
- fine 			The fine levied
_}

