Mission check galaxy.script:
{Log("Fktn.: Mission check galaxy")} 

{_ Mission check galaxy }
{_ Check what systems you have missions in }
{_ Setup allmissions function _}

{set allmissions(system) to:
    {set here to find(systems, system)}
    {if here > -1:
        {if mission.statusEDName = "Active":       
            {set active to systems_active[here] + 1}
            {set systems_active to union(systems_active, [here:active])}
        |elif mission.statusEDName = "Claim":
            {set claim to systems_claim[here] + 1}
            {set systems_claim to union(systems_claim, [here:claim])}
            {set active to systems_active[here] + 1}
            {set systems_active to union(systems_active, [here:active])}
        |elif mission.statusEDName = "Complete":
            {set complete to systems_complete[here] + 1}
            {set systems_complete to union(systems_complete, [here:complete])}
            {set active to systems_active[here] + 1}
            {set systems_active to union(systems_active, [here:active])}
        }
    |else:
        {set systems to cat(systems, [system])}
        {if mission.statusEDName = "Active":
            {set systems_active to cat(systems_active, [1])}
            {set systems_claim to cat(systems_claim, [0])}
            {set systems_complete to cat(systems_complete, [0])}
        |elif mission.statusEDName = "Claim":
            {set systems_active to cat(systems_active, [1])}
            {set systems_claim to cat(systems_claim, [1])}
            {set systems_complete to cat(systems_complete, [0])}
        |elif mission.statusEDName = "Complete":
            {set systems_active to cat(systems_active, [1])}
            {set systems_claim to cat(systems_claim, [0])}
            {set systems_complete to cat(systems_complete, [1])}
         }
    }
}

{if missionsCount > 0:
    {set failed to 0}
    {set multi to 0}
    {set pending to 0}
    {set systems to []}
    {set systems_active to []}
    {set systems_claim to []}
    {set systems_complete to []}

    {for mission in missions:
        {if mission.statusEDName = "Failed":
            {set failed to failed + 1}
        |else:
            {set pending to pending + 1}
            {if mission.destinationsystems:
                {set multi to multi + 1}
                {for destination in mission.destinationsystems:
                    {allmissions(destination.name)}
                }
            |elif mission.destinationsystem && mission.destinationsystem != "":
                {allmissions(mission.destinationsystem)}
            }
        }
    }
    {set systemsCount to len(systems)}

    {char(10)}{char(13)}Wir haben
    {if failed > 0:
        {if failed = 1 : eine |else: {failed}} gescheiterte 
        {if pending > 0: und
        |elif missionsCount = 1: Mission.
        |else: Missionen.
        }
    }

    {if pending > 0 :
        {if mission_report && systemsCount > 0 :
            {set cur to 0}
            {while cur < systemsCount:
                {set active to systems_active[cur]}
                {set claim to systems_claim[cur]}
                {set complete to systems_complete[cur]}
                {set subtotal to complete + claim}

                {if active > 0 && active > subtotal:
                    {if active = 1 : eine |else: {active}} laufende
                    {if claim > 0 || complete > 0 : , einschließlich }
                }
                {if complete > 0 :
                    {if complete = 1 : eine |else: {complete}} beendete 
                    {if claim > 0 : und }
                }
                {if claim > 0 :
                    {if claim = 1 : eine |else: {claim}} einlösbare
                }
                {if subtotal > 0 :
                    {if subtotal = 1 : Mission |else: Missionen }
                }
                im System {systems[cur]}
                {set cur to cur + 1}
                {if cur = systemsCount - 1 : und |elif cur < systemsCount: , }
            }.
        |else:
            {if pending = 1: eine |else: {pending} } offene
            {if missionsCount = void || missionsCount = 1 : Mission. |else: Missionen,} 
            {if systemsCount = 0 :
                {if missionsCount = 1 : {_ mit keinem bestimmten Ziel System} |else: {_ mit keinen bestimmten Ziel Systemen} }
            |else: in 
                {if systemsCount = 1 : einem Sternsystem
                |else: {systemsCount} {Occasionally(2,"Stern")}systemen 
                } 
            }.
            {if multi > 0 :
                {if multi = pending: Alle |else: {multi} } diese {if multi = 1 : ist eine |else: sind }
                Mehr-Ziel- {if multi = 1: Mission |else: Missionen }.
            }        
        }
    }
|else:
    {char(10)}{char(13)}Wir haben {OneOf("im Moment","zur Zeit")} keine offenen Missionen.
}

