Mission check system.defaultValue:
{_ Setup addstation function }
{set addstation(stationName, mission) to:
    {set missions_here to missions_here + 1}
    {set station_based to station_based + 1}
    {set here to find(stations_here, stationName)}
    {if here > -1:
        {if mission.statusEDName = "Active":
            {set active to stations_active[here] + 1}
            {set stations_active to union(stations_active, [here:active])}
        |elif mission.statusEDName = "Claim":
            {set claim to stations_claim[here] + 1}
            {set stations_claim to union(stations_claim, [here:claim])}
        }
    |else:
        {set stations_here to cat(stations_here, [stationName])}
        {if mission.statusEDName = "Active":
            {set stations_active to cat(stations_active, [1])}
            {set stations_claim to cat(stations_claim , [0])}
        |elif mission.statusEDName = "Claim":
            {set stations_active to cat(stations_active, [0])}
            {set stations_claim to cat(stations_claim , [1])}
        }
    }
}

{if missionsCount + goalsCount > 0:
    {set missions_here to 0}
    {set station_based to 0}
    {set stations_active to []}
    {set stations_claim to []}
    {set stations_here to []}
    {set non_stations to []}

    {_ Find all missions in the current system and identify all the stations }
    {for mission in missions:
        {if mission.status != "Failed":
            {if mission.destinationsystems:
                {for destination in mission.destinationsystems:
                    {if destination.name = system.name && !destination.visited:
                        {set missions_here to missions_here + 1}
                        {if find(mission.invariantTags, "Transport") > -1:
                            {set type to "Transport "}
                            {set type to cat(type, mission.passengertype)}
                        |else:
                            {set type to mission.tags[0]}
                        }

                        {if find(non_stations, type) = -1:
                            {set non_stations to cat(non_stations, [type])}
                        }
                    }
                }
            |elif mission.destinationsystem = system.name:
                {if mission.destinationstation && mission.destinationstation != "":
                    {addstation(mission.destinationstation, mission)}
                |else:
                    {if mission.status = "Active":
                        {set missions_here to missions_here + 1}
                        {if find(mission.invariantTags, "Transport") > -1:
                            {set type to "Transport "}
                            {set type to cat(type, mission.passengertype)}
                        |else:
                            {set type to mission.tags[0]}
                        }
                        {if find(non_stations, type) = -1:
                            {set non_stations to cat(non_stations, [type])}
                        }
                    }
                }
            |elif mission.originsystem = system.name && find(mission.invariantTags, "Delivery") > -1:
                {if mission.originstation && mission.originstation != "":
                    {addstation(mission.originstation, mission)}
                }
            }
        }
    }

    {if missions_here > 0:
        {set missionSummaries to []}
        {set cur to 0}
        {for station in stations_here:
            {set station to StationDetails(station)}
            {set active to stations_active[cur]}
            {set claim to stations_claim[cur]}
            {set depot to stations_depot[cur]}

            {set summaries to []}
            {if active > 0:
                {set activeSummary to: 
                    {active} {OneOf("active", "pending")} {if active = 1: mission |else: missions}
                }
                {set summaries to cat(summaries, [activeSummary])}
            }
            {if claim > 0:
                {set claimSummary to: 
                    {claim} claimable {if claim = 1: mission |else: missions}
                }
                {set summaries to cat(summaries, [claimSummary])}
            }
            {if len(summaries) > 0:
                {set summary to:
                    {List(summaries)} here
                    {if station:
                        at 
                        {set stationModel to P(station.model, 'station')}
                        {if stationModel: {Occasionally(2, "the {stationModel}, ")} }
                        {station.name}
                    }
                }
                {set missionSummaries to cat(missionSummaries, [summary])}
            }
            {set cur to cur + 1}
        }
        
        {set remaining to missions_here - station_based}
        {if remaining > 0:
            {set otherMissions to:
                {remaining} {if len(missionSummaries) > 0: other}
                {if remaining > 1: missions |else: mission}
                here
            }
            {set missionSummaries to cat(missionSummaries, [otherMissions])}
        }

        {if len(missionSummaries) > 0:
            {Pause(500)}
            You have
            {List(missionSummaries)}.
        }
    }
}

