Mission redirected.defaultValue:
{_ Mission redirected }
{_ Triggered when a mission is redirected }

{set redirected_from to cat(event.olddestinationsystem,"_",event.olddestinationstation)}
{set redirected_to to cat(event.newdestinationsystem,"_",event.newdestinationstation)}

{if state.eddi_context_last_subject = 'mission' 
    && state.eddi_context_last_action = 'redirected' 
    && state.eddi_context_redirected_mission = event.name
    && state.eddi_context_redirected_from = redirected_from 
    && state.eddi_context_redirected_to = redirected_to:
    {_ Duplicate from stacked similar mission, do nothing }
|else:
    {_ Context }
    {SetState('eddi_context_last_subject', 'mission')}
    {SetState('eddi_context_last_action', 'redirected')}
    {SetState('eddi_context_redirected_mission', event.name)}
    {SetState('eddi_context_redirected_from', cat(event.olddestinationsystem,"_",event.olddestinationstation))}
    {SetState('eddi_context_redirected_to', cat(event.newdestinationsystem,"_",event.newdestinationstation))}
    
    {set mission to MissionDetails(event.missionid)}

    {if find(mission.invariantTags, "Transport") > -1:
        {set type to "Transport "}
        {set type to cat(type, mission.passengertype)}
    |else:
        {set type to mission.tags[0]}
    }
    {set type to cat(type, " mission")}

    {if mission.status = "Claim" && mission.destinationsystem = mission.originsystem:
        {type} {Occasionally(2, "requirements")} completed.
        Redirected to {Occasionally(2, "mission")} origin.
    |elif mission.destinations:
        Chained {type} redirected to the {P(event.newdestinationsystem, "starsystem")} system.
    |else:
        {type} redirected to 
        {if event.newdestinationstation:
            {P(event.newdestinationstation, "station")} in the {P(event.newdestinationsystem, "starsystem")} system.
        |else:
            the {P(event.newdestinationsystem, "starsystem")} system.
        }
    }
    
    {_ Update to next system in route if conditions met _}
    {Pause(1000)}
    {set next_system to RouteDetails("update")}
}

