		{_ set type to cat(type, "")}

        {if find(type, "mission") >-1 : {type} |else: Mission {type}}
        {if mission.faction: für {mission.faction}}
        {if mission.destinationsystem: im
            {if mission.destinationsystem = curr.name: aktuellen System
            |else: System {mission.destinationsystem}
            }
        } läuft als nächstes ab.
    |else: Keine aktiven Missionen gefunden.
    }
|elif event.routetype = "farthest":
    {if event.distance > 0:
        Unser {if missionsCount = 1: einziges |else: weitestes}
        Missionsziel ist im
        {if event.system = curr.name: aktuellen System
        |else: System {event.system}, {round(event.distance, 1)} Lichtjahre {OneOf("von hier", "entfernt")}.
        }.
    |else: Keine Mission mit einem festgelegten Ziel gefunden.
    }
|elif event.routetype = "most":
    {if missionsCount = 1:
        Unser einziges Missionsziel befindet sich 
        {if missions[0].destinationsystem = "": in einem unbekannten System.
        |elif missions[0].destinationsystem = curr.name: im aktuellen System.
        |else: im System {missions[0].destinationsystem}.
        }
    |else:
        {set systems to split(event.systems, "_")}
        {set system_most to event.system}
        {set systems_count to len(systems)}

        {if systems_count = 1:
            {if system_most = curr.name: {set system_most to " aktuellen System"}
            |else: {set system_most to cat("diesem ", system_most, " system")}
            }
            {OneOf("Most of your missions are {Occasionally(2,'located')} in {system_most}",
                "{system_most} has {OneOf('the most','most of your')} missions",
                "{system_most} has the most missions for you to complete")}.
        |elif systems_count > 1:
            Wir haben {event.count} Missionsziele{if event.count > 1:s} each, in the

            {set cur to 0}
            {while cur < len(equal):
                {systems[cur]}
                {set cur to cur + 1}
                {if cur = systems_count-1: und 
                |elif cur < systems_count: ,
                }
            } systems.
        }
    }
|elif event.routetype = "nearest":
    {if event.distance > 0:
        Unser {if missionsCount = 1: einziges |else: am nächsten gelegenes}
        Missionsziel befindet sich 
        {if event.system = curr.name: im aktuellem System
        |else: {event.system} System, cirka {round(event.distance, 1)} Lichtjahre entfernt
        }.
    |else: Die Mission hat kein bestimmtes Zielsystem.
    }
|elif event.routetype = "route":
    {if event.routedistance > 0:
        Die Missionsroute wurde für {event.count} Systeme berechnet.
        Die Länge der Route beträgt {round(event.routedistance, 1)} Lichtjahre.
        Das erste Missionsziel befindet sich {event.system}, {round(event.distance, 1)} Lichtjahre entfernt.
    |else: Das Berechnen der Missionsroute ist nicht möglich
    }
|elif event.routetype = "update":
    {if event.routedistance > 0 : Das nächste Missionsziel befindet sich im System {event.system}, 
    								{round(event.distance, 1)} Lichtjahre entfernt.
    |else: Ein Routen Update zum nächsten Missionsziel ist nicht möglich.
    }
}

