    {OneOf ("Kurs", "Rute")} {OneOf("berechnet", "gesetzt", "gelockd", "eingetragen")}
    {if reportDestination:
        für {event.route[event.jumps].systemname} {if len(details) > 0:,}
    }
    {if len(details) > 0:
        , {List(details)} {OneOf("zu absolvieren", "durchzuführen", "bis ins System")}
    }.
|elif !event.route: Route {OneOf("gelöscht", "entfernt")}
}

