Near surface.script:
{Log("EVENT: Near surface")}

{_ Context }
{SetState('eddi_context_body_system', event.systemname)}
{SetState('eddi_context_body_name', event.bodyname)}
{SetState('eddi_gravity_surface', 0)}

{_ SetState('time_near_surface', SecondsSince(A))}
{_ SecondsSince(state.time_near_surface)}

{_ Fetch from context _}
{set reportbody to BodyDetails(event.bodyname, event.systemname)} {_ oder event.body und event.system _}
{set gravity to round(reportbody.gravity,2)}
{SetState('eddi_gravity_surface', round(reportbody.gravity,2))}

{if state.gravitation_ansagen = 1:
	{if SecondsSince(state.time_near_surface) < 50 || SecondsSince(state.time_near_surface) > 3600:
		{_ zum Testen: if-Zweig, SecondsSince(state.time_near_surface) Sekunden _}
		{Pause(2000)}
		{if event.approaching_surface = true : {OneOf("Orbital eintritt", "Eintritt in die Umlaufbahn")}
			{SetState('time_near_surface', SecondsSince(A))}
			{if event.bodyname: bei {event.bodyname};}
	    	{Pause(500)}
	   		{if !reportbody.gravity:
	   			Achtung ! {Occasionally(2, "Ich habe")} keine Angaben zur Gravitation !
			|else:
			    Das Objekt hat eine
			    {if reportbody.gravity < 0.5 : geringe
			    |elif reportbody.gravity < 2  : mittlere
			    |elif reportbody.gravity < 4  : hohe
			    |else                         : extrem hohe
			    }
			    Gravitation von {set value to (gravity)} {F("Humanise ValueGer")} G.
			}
			{Pause(500)}
	        {F("Fahrzeug-Hangar check")}		
		}
	|else: {_ zum Testen: else-Zweig, SecondsSince(state.time_near_surface) Sekunden _}
	}

	{if event.approaching_surface = false :
		{OneOf(
		"Orbital austritt", 
		"Verlassen {OneOf('der Umlaufbahn','des Orbitts')} {if event.bodyname: von {event.bodyname}}", 
   		"Wir verlassen jetzt {OneOf('die Umlaufbahn','den Orbitt')} {if event.bodyname: von {event.bodyname}}")}
	}
|else:
}

{_
- approaching_surface	A boolean value. True if you are entering the gravity well
						and false if you are leaving
- systemname 			The name of the starsystem
- bodyname 				The name of the body
- shortname 			The short name of the body, less the system name
_}

