    {set reportBody to BodyDetails(event.name)} {set prep to "zum "} {set artikel to "der "}
    {if reportBody.bodytype = "Stern" : {set Ziel to cat("Stern, ", reportBody.shortname)} 
    |else:
        {if reportBody.type = "Mond": {set reportBody_type to "Moond"} |else: {set reportBody_type to reportBody.type"en"}}
        {set Ziel to cat(reportBody_type," ",reportBody.shortname)}}   {_ set shortname to reportBody.shortname F("Translate Himmelskörper"))}

|elif event.isSignalSource: 
    {if find(system.signalsources, "Besiedelungs-Boje") >-1 && event.localizedName = "Signalquelle":
        {set prep to "zum"} {set artikel to "das"}
        {set Ziel to "Kolonisationsschiff"}
    |elif find(event.name, "Orbital Construction Site:") > -1: {set artikel to "die"} {set prep to "zur"}
        {set Ziel to cat('Orbital-Bau-Stelle', token(event.name, "Site:", 1))}
