            |else:      
                {if find([
                    "J2Z-9QZ","J4F-77J","KLG-23Z","VHZ-BQZ","K7M-2KQ","XXX-XXX","K1L-58Z","X6Z-L4F","LFN-9QZ","Q3P-49L",
                    "K3M-WVG","KHZ-6XW","K4F-6XL","K8L-34Q","Q2T-7HG","L0X-2XZ","K7V-L0Y","X2N-NKX","XFZ-47B","V3J-NQY",
                    "XLH-LTW","Q6Q-52T","V1Q-G0G","X0M-72L","K6Y-8HG","T0V-T6Z","T8V-WTZ","JFJ-W5Q","Q5Q-63Z","KNN-G3Z",
                    "V5G-B6W","XBJ-4QZ","L7G-95Z","JBX-84X","K6M-25B","XFK-5XJ","V2K-9VB","X5Z-TKW","QLX-5QF","K7F-78Q",
                    "JNL-87Z","K9G-72Z","KHN-L6J","XFK-NVB","V5K-G7M","V0N-89K","J0F-09Q","T7N-487","QFZ-2QH","Q9H-87V",
                    "K7F-34F","K1Z-G4T","JBY-30W","ABC-123"], event.name) > -1 :
                    {set artikel to cat("der ", OneOf('Carrier','Träger','Flottenträger','Fleetcarrier'))}
                    {set prep to cat("zum ", OneOf('Carrier','Träger','Flottenträger','Fleetcarrier'))}
