            {set artikel to "die"} {set prep to "zur"} {set Ziel to event.station.model}
            {set Ziel to cat(event.station.model," ",event.name)}
        |elif event.station.model = "Ocellus Sternenhafen" || event.station.model = "Coriolis Sternenhafen"
            || event.station.model = "Orbis Sternenhafen"  || event.station.model = "Raumhafen"
            || event.station.model = "Planetenhafen"       || event.station.model = "Dodec-Sternenhafen"
