Next jump.script:
{Log("EVENT: FSDTarget / Next jump")}

{_ Context }

{SetState('eddi_context_nextsystem_name', event.system)}
{SetState('eddi_context_nextsystem_nextsystem', event.system)}
{SetState('eddi_context_remaining_jumps', event.remainingjumpsinroute)}
{SetState('eddi_context_next_route_destination', event.system)}
{_ SetState('eddi_context_jump_target', event.target)}

{if state.eddi_context_last_action = 'start': |else: {_ Nächstes Ziel ist das System event.system,} }

{for compartment in ship.compartments :
    {if compartment.module.name = "Treibstoffsammler": {set ts_vorhanden to true}}} 

{if (status.gui_focus = "galaxy map" || status.gui_focus = "system map" || status.gui_focus = "orrery") && next_jump = 1: 
|else:
	{SetState('next_jump', 1)}
	{_ Nächstes Ziel RouteDetails("set", event.system) _}
	{set scoopables to ["O", "B", "A", "F", "G", "K", "M"]}

	{if vehicle = "Ship":
        {if (status.gui_focus = "galaxy map" || status.gui_focus = "system map" || status.gui_focus = "orrery"): 		
            {if find(event.starclass, "A_") = 0		: {set starclass to "A"}
			|elif find(event.starclass, "F_") = 0	: {set starclass to "F"}
			|elif find(event.starclass, "M_") = 0	: {set starclass to "M"}
			|elif find(event.starclass, "K_") = 0	: {set starclass to "K"}
			|elif find(event.starclass, "DA") = 0	: {set starclass to "D,A"}
			|else									: {set starclass to event.starclass}
			}
				
			{Pause(100)}
			{if state.eddi_context_last_subject = "navroute" && state.navroute_ziel:
			     {if event.remainingjumpsinroute = 1:
			     |else:
                    Der erste Sprung geht ins System, {event.system},
                 }   
			|else:
                System {event.system} {OneOf("angewählt,", "ausgewählt,")}
			}
			
			{if slice(event.starclass, 0, 1) = "D"          : Vorsicht: Weissen Zwerg entdeckt.
            |elif event.starclass = "N"                     : Vorsicht: Neutrohnen,Stern entdeckt. 
            |elif event.starclass = "H"                     : Vorsicht: Schwarzes Loch entdeckt.
            |elif find(scoopables, event.starclass) = -1    : Achtung: Zielstern zum Auftanken nicht geeignet!
            |else:
                {if ts_vorhanden:
                    {OneOf('{Occasionally(2,"Ziel-")}stern zum Auftanken geeignet.', 'Auftanken am Stern möglich.',
                    'Treibstoffgewinnung möglich.')}
                }
            }
		} 
	|else:
	}
}

{SetState('eddi_context_last_subject', 'FSDTarget')}

{_ Test
1 state.navroute_ziel
2 event.system
SetState('navroute', 0)
_}

