Powerplay report.script:
{Log("Fktn.: Powerplay report")}

{_ Fetch from Context, if a context is set _}
{if state.eddi_context_system_name:
    {set reportSystem to SystemDetails(state.eddi_context_system_name)}
|else:
    {set reportSystem to SystemDetails(system.systemname)}
}

{set contestingPowers to []}
{for contestingPower in reportSystem.contestingpowers:
    {set contestingPowers to cat(contestingPowers, [P(contestingPower, "power")])}
}
    
{if reportSystem.powerState.invariantName = "Contested":
    {reportSystem.systemname} wird bestritten von {List(contestingPowers)}.
|elif reportSystem.power && reportSystem.power != "Keine":
    {reportSystem.systemname} wird
    {if reportSystem.powerState.invariantName = "Exploited":
        {OneOf("bedingt kontrolliert", "ausgenutzt")} von
    |elif reportSystem.powerState.invariantName = "Fortified":
        {OneOf("kontrolliert", "gestärkt")} von
    |elif reportSystem.powerState.invariantName = "Stronghold":
        einer Hochburg von
    |elif reportSystem.powerState.invariantName = "Headquarters":
        dem Hauptquartier von
    }
    {reportSystem.power}
    {if len(contestingPowers) > 0:
        und wird unterwandert von {List(contestingPowers)}
    }.
}

Powerplay report.defaultValue:
{_ Fetch from Context, if a context is set _}
{if state.eddi_context_system_address:
    {set reportSystem to SystemDetails(state.eddi_context_system_address)}
|else:
    {set reportSystem to SystemDetails(system.systemAddress)}
}
    
{set powerName to cmdr.power}
{set thisSystem to OneOf(P(reportSystem.systemname, "starsystem"), "this star system")}
{if reportSystem.powerState.invariantName = "Unoccupied":
    {set leadingPower to true}
    {for powerProgress in reportSystem.powerAcquisitionProgress:
        {if powerProgress.Power.localizedName = powerName && powerProgress.progress >= 5:
            {P(powerName, "power")} is 
            {if leadingPower: leading |else: trailing }
            in the 
            {OneOf("contest", "battle", "struggle", "dispute")}
            for {Occasionally(2, "control of")}
            {thisSystem}.
        }
        {set leadingPower to false}
    }
|elif reportSystem.powerState.invariantName = "Contested":
    {set contestingPowers to []}
    {for contestingPower in reportSystem.contestingpowers:
        {set contestingPowers to cat(contestingPowers, [P(contestingPower, "power")])}
    }
    {List(contestingPowers)} are 
    {OneOf("contesting", "in dispute", "vying")}
    for {Occasionally(2, "control of")}
    {thisSystem}.
|elif reportSystem.power && reportSystem.power != "None":
    {set systemPower to P(reportSystem.power, 'power')}
    {if reportSystem.powerState.invariantName = "Exploited":
        {OneOf(
            "{thisSystem} is tenuously controlled by {systemPower}.", 
            "{thisSystem} is exploited by {systemPower}."
            "{thisSystem} is within the influence of {systemPower}."
            "{systemPower} is consolidating control within {thisSystem}."
         )}
    |elif reportSystem.powerState.invariantName = "Fortified":
        {OneOf(
            "{thisSystem} is securely within the control of {systemPower}.", 
            "{thisSystem} is being fortified by {systemPower}."
            "{systemPower} is reinforcing their control within {thisSystem}."
        )}
    |elif reportSystem.powerState.invariantName = "Stronghold":
        {OneOf(
            "{thisSystem} is a stronghold of {systemPower}."
            "{systemPower} has consolidated their power and made a stronghold in {thisSystem}."
        )}
    |elif reportSystem.powerState.invariantName = "Headquarters":
        {OneOf(
            "{thisSystem} is the headquarters of {systemPower}."
            "{systemPower} has established their headquarters within {thisSystem}."
        )}
    }
}

