{Log("EVENT: Hotspots detected")}

{Pause(500)}
{_ Welche drei Materialien in VA angezeigt werden sollen, bitte hier eintragen _}
{set commodity1 to "Tieftemperaturdiamanten"}
{set commodity2 to "Tritium"}
{set commodity3 to "Leerenopal"}

{if !SecondsSince(system.lastVisitSeconds) < 3600 : 
	{if ship.Role.invariantName = "Mining" || ship.Role.invariantName = "Multipurpose" || ship.Role.invariantName = "Exploration":    
	    {set ringhotspots to event}
	    {set hotspots to []}
	   	{set AnzahlHotspots to 0}
	   	{SetState("ring_hotspots_body", "")}
	   	{SetState("ring_hotspots_commodity1", "")}
	   	{SetState("ring_hotspots_commodity2", "")}
	   	{SetState("ring_hotspots_commodity3", "")}
	   	{for hotspot in event.hotspots:
	   		{if hotspot.commodity = commodity1 || hotspot.commodity = commodity2 || hotspot.commodity = commodity3 :
	   			{SetState("ring_hotspots_body", event.bodyname)}
