{if ship.Role.edname = "Exploration" || ship.Role.edname = "Multipurpose":

    {Pause(3000)}

    {OneOf("Das Meppen", "Die Erkundung", "Das Scannen")} des
    {if event.composition = "MetalRich" || event.composition = "metallreich": erzreichen  
    |elif event.composition = "Icy" || event.composition = "eisig"          : eisigen
    |elif event.composition = "Metallic" || event.composition = "metallisch": metallischen
    |elif event.composition = "Rocky" || event.composition = "felsig"       : felsigen
    |else:
    }
