Route details.script:
{Log("EVENT: Route details")}

{_ Route details _}
{_ Triggered when a route has been generated or updated _}

{_ Context _}
{SetState('eddi_context_search_system', event.system)}
{SetState('eddi_context_search_station', event.station)}
{SetState('eddi_context_search_distance', event.distance)}
{SetState('eddi_context_search_count', event.count)}

{set clip_notify to false}
{set type to event.routetype}

{if type = "encoded" || type = "facilitator" || type = "guardian"
    || type = "human" || type = "manufactured" || type = "raw":

    {if type = "facilitator":
        {set service to "interstellar Factor Kontakt"}
    |elif type = "encoded" || type = "manufactured" || type = "raw":
        {set service to cat(type, " Materialien Händler")}
    |elif type = "guardian" || type = "human":
        {set service to cat(type, " Technologie Händler")}
    |elif type = "scorpion":
        {set service to "Scorpion SRV Händler"}        
    }

    {if event.system && event.system != "":
        Der kürzeste Weg zum {service} ist zu der Station {event.station}
        {if event.system = system.name: {OneOf("in diesem", "im aktuellen", "im derzeitigen")} System
        |else:
            {set clip_notify to true}
            im System {event.system}, rund {round(event.distance, 1)} Lichtjahre entfernt
        }.
    |else:
        kein {service} in Sensorreichweite.
    }

|elif type = "expiring":
    {if event.system && event.system != "":
        {set mission to MissionDetails(event.missionids[0])}

        {if find(mission.invariantTags, "Transport") > -1:
            {set type to "Transport "}
            {set type to cat(type, mission.passengertype)}
        |else:
            {set type to mission.tags[0]}
        }
        {set type to cat(type, " mission")}

        {type} {if mission.faction: für Fraktion {mission.faction}}
        {if mission.destinationsystem:
            {if mission.destinationsystem = system.name:
            	{OneOf("in diesem", "im aktuellen", "im derzeitigen")} System
            |else:
                {set clip_notify to true}
                im System {mission.destinationsystem}
            }
        } läuft als nächstes ab.
    |else:
        Keine aktive Mission gefunden.
    }
|elif type = "farthest":
    {if event.system && event.system != "":
        Unser {if missionsCount = 1: einziges |else: entferntestes}
        Missionsziel ist
        {if event.system = system.name:
            {OneOf("in diesem", "im aktuellen", "im derzeitigen")} System
        |else:
            {set clip_notify to true}
            im System {event.system}, rund {round(event.distance, 1)} Lichtjahre entfernt
        }.
    |else:
        Keine Mission im genannten Zielsystem gefunden.
    }
|elif type = "most":
    {if missionsCount = 1:
        Unser einziges Missionsziel {Occasionally(2,"befindet sich")}
        {if missions[0].destinationsystem = "":
            in einem unbekannten
        |elif missions[0].destinationsystem = system.name:
            in diesem
        |else:
            im System {missions[0].destinationsystem}
        }
        {Occasionally(2,"Stern")} System.
    |elif event.system && event.system != "":
        {set systems to split(event.route, "_")}
        {set systems_count to len(systems)}

        {if systems_count = 1:
            {if event.system = system.name:
                {set system_most to "Das aktuelle System"}
            |else:
                {set clip_notify to true}
                {SetClipboard(event.system)}
                {set system_most to cat("Das ", event.system, " System")}
            }
            {OneOf("Die meisten unserer Missionen {Occasionally(2,'befinden sich')} im System {system_most}",
                "System {system_most} hat die meisten unserer Missionen")}.               
        |elif systems_count > 2:
            Wir haben {if event.count > 1: {event.count} Missionsziele |else: ein Missionsziel} ,

            {_ Don't include the home system from the route list _}
            {set cur to 0}
            {set systems_count to systems_count - 1}
            {while cur < len(systems_count):
                {systems[cur]}
                {set cur to cur + 1}
                {if cur = systems_count - 1 : und |elif cur < systems_count: ,}
            } System.
        }
    |else:
        Keine Mission im genannten Zielsystem gefunden.
    }
|elif type = "nearest":
    {if event.system && event.system != "":
        Unser {if missionsCount = 1: einziges |else: nähestes}
        Missionsziel ist
        {if event.system = system.name: in {OneOf("diesem", "aktuellen")} System
        |else:
            {set clip_notify to true}
            im System {event.system}, {round(event.distance, 1)} Lichtjahre entfernt.
        }.
    |else:
        Keine Mission im genannten Zielsystem gefunden.
    }
|elif type = "route":
    {if event.system && event.system != "":
        Missions Route berechnet für {event.count - 1}
        {if event.count - 1 > 1: Systeme |else: ein System.}
        Die komplette Routenlänge beträgt {round(event.routedistance, 1)} Lichtjahre.
        {if event.system != system.systemname:
        	Das erste Missionsziel ist das System {event.system}, rund {round(event.distance, 1)} Lichtjahre entfernt.
        	{set clip_notify to true}
        	{SetClipboard(event.system)}
    	|else:
    		{F("Mission check system")}
    	}
        Sorry, keine Routenberechnung möglich.
    }
|elif type = "source":
    {if event.count > 0:
        {set systems to split(event.route, "_")}

        {set pos to 0}
        {while pos < event.count:

            {set source_cargo to []}
            {for missionid in event.missionids:
                {set haulage to HaulageDetails(missionid)}
                {if haulage.sourcesystem = systems[pos]:
                    {set cargo to CargoDetails(missionid)}
                    {if find(source_cargo, cargo.name) = -1:
                        {set source_cargo to cat(source_cargo, [cargo.name])}
                    }
                }
            }

            {if pos = 0: Unsere {if event.count = 1: einzige |else: näheste}
            |elif pos = 1: zusätzlich, your
            |elif pos = event.count - 1: Lastly, your
            |else: Your
            }

            Frachtquelle für
            {set cur to 0}
            {while cur < len(source_cargo):
                {source_cargo[cur]}
                {set cur to cur + 1}
                {if cur = len(source_cargo) - 1: und |elif cur < len(source_cargo): ,}
            }

            ist
            {if systems[pos] = system.name: {OneOf("in diesem", "im aktuellen")} System
            |else:
                {set clip_notify to true}
                {SetClipboard(systems[pos])}
                im System {systems[pos]},
                {set distance to Distance(system.name, systems[pos])}
                {round(distance, 1)} Lichtjahre entfern
            }.
            {set pos to pos + 1}
       }
         {SetState('eddi_context_cargo_source', event.route)}
    |else:
        Frachtquelle nicht mit einem bestimmten Zielsystem gefunden.
    }

|elif type = "update":
    {if event.system && event.system != "":
        {if event.system != system.systemname:
            {_ Next system }
            Nächstes {OneOf("System", "Sternsystem", "Ziel")} ist {event.system}, {round(event.distance, 1)} Lichtjahre entfernt.
            {set clip_notify to true}
            {SetClipboard(event.system)}
        |else:
            {_ Next station }
            {F("Mission check system")}
        }
    }

|elif type = "scoop":
    {if event.system && event.system != "":
        {if event.system = system.name:
            {set scoopable_bodies to []}
            {for body in system.bodies:
                {if body.scoopable:
                    {set scoopable_bodies to cat(scoopable_bodies, [body.shortname])}
                }
            }
            {if len(scoopable_bodies) > 1:
                Die nächsten tankbaren Sterne sind: 
                {List(scoopable_bodies)}
            |else:
                Der am nächsten tankbare Stern ist:
                {scoopable_bodies[0]}
            }
            {Pause(50)}
            Der am nächsten tankbare Stern befindet sich {OneOf("im aktuellen", "in diesem")} System.
        |else:
            Der am nächsten tankbare Stern befindet sich im System {event.system}, {round(event.distance, 1)} Lichtjahre entfernt.
            {set clip_notify to true}
            {SetClipboard(event.system)}
        }
        {Pause(500)}
        {if maxjump < 0.5:
            {Occasionally(2, "Treibstoff")} {OneOf("scooping", "schöpfen", "Aufnahme")} ist äußerst dringend {title}.
        |else:
            {Occasionally(2, "Treibstoff")} {OneOf("scooping", "schöpfen", "Aufnahme")} wird dringend empfohlen {title}.
        }
    |else:
        Gefahr! Innerhalb unserer aktuellen Sprungreichweite gibt es keine schöpfbaren Sterne.
        {Pause(500)}
        Es wird empfohlen, nicht lebensnotwendige Systeme abzuschalten!
        {Pause(500)}
        Notfalltransponder in Bereitschaft {title}.
    }
|elif type = "neutron":
    {if event.system && event.system != "" && event.system != system.systemname:
        Pulsar-Route berechnet.
        {if ship && ship.phoneticname:
        	{ship.phoneticname}
        |else:
            Unser {ship.phoneticmodel}
        }
        wird
        {event.route[event.count - 1]}
        in
        {event.count - 1} {if event.count - 1 = 1: einem Sprung |else: Sprüngen} erreichen.
        {if event.count - 1 > 1: 
            Die komplette Routenlänge beträgt {round(event.routedistance, 1)} Lichtjahre.
            Erstes Ziel ist System {event.system}, {round(event.distance)} Lichtjahre entfernt.
        }
        {set clip_notify to true}
        {SetClipboard(event.system)}
    |else:
        Pulsar-Route kann nicht berechnet werden.
    }
|elif type = "carrier":
    {if event.system && event.system != "" && event.system != system.systemname:
        Route berechnet.
        {if carrier && carrier.name:
        	{carrier.name}
        |else:
            Unser {OneOf("flieht carrier", "Flottenträger", "Träger", "Carrier")}
        }
        wird
        {event.route[event.count - 1]}
        in
        {event.count - 1} {if event.count - 1 = 1: einem Sprung |else: Sprüngen} erreichen.
        {if event.count - 1 > 1: 
            {event.tritiumused} Tonnen Tritium werden benötigt, um die Reise zu beenden.
            Erstes Ziel is das System {event.system}, {round(event.distance)} Lichtjahre entfernt.
        }
        {set clip_notify to true}
        {SetClipboard(event.system)}
    |else:
        Carrier-Route kann nicht berechnet werden.
    }
    
    
|elif type = "set":
	{if state.eddi_context_remaining_jumps > 5: Ziel in die Liste aufgenommen.}
|elif type = "cancel"			: Ziel entfernt.
|elif type = "recalculating"	: {Occasionally(2, " Routen-")}Neuberechnung.
}

{if clip_notify && !va_active:
    {Pause(1000)}
    Zielsystem in die Zwischenablage kopiert.
}

{_
count 			- A count of missions, systems, or mission expiry seconds, depending on the route type 
distance 		- The distance to the next star system waypoint 
missionids 		- The mission ID(s) associated with the next waypoint star system, if applicable 
route 			- A list of the system names which are visited during the route, if applicable 
route[<index>] 
routedistance 	- The total distance of the route, if applicable 
routetype 		- Type of route query 
station 		- The station returned from the search, if applicable 
system 			- The next star system waypoint in the route 
tritiumused 	- Required total tritium required to complete the route, if applicable 
_}

