    }
|elif type = "neutron":
    {if event.system && event.system != "" && event.system != system.systemname:
        Pulsar route calculated.
        {if ship && ship.phoneticname:
        	{ship.phoneticname}
        |else:
            Your {ship.phoneticmodel}
        }
        will reach
        {event.route[event.count - 1]}
        in
        {event.count - 1} {if event.count - 1 = 1: jump. |else: jumps.}
        {if event.count - 1 > 1: 
            Total route distance is {round(event.routedistance, 1)} lightyears.
            First destination is {P(event.system, "starsystem")}, {round(event.distance)} lightyears away.
        }
        {set clip_notify to true}
        {SetClipboard(event.system)}
    |else:
        Unable to calculate pulsar route.
    }
|elif type = "carrier":
    {if event.system && event.system != "" && event.system != system.systemname:
        Route calculated. 
        {if carrier && carrier.name:
        	{carrier.name}
        |else:
            Your fleet carrier
        }
        will reach
        {event.route[event.count - 1]}
        in
        {event.count - 1} {if event.count - 1 = 1: jump. |else: jumps.}
        {if event.count - 1 > 1: 
            {event.tritiumused} tons of tritium shall be required to complete the journey.
            First destination is {P(event.system, "starsystem")}, {round(event.distance)} lightyears away.
        }
        {set clip_notify to true}
        {SetClipboard(event.system)}
    |else:
        Unable to calculate carrier route.
    }
|elif type = "set":
    Guidance enabled.
|elif type = "cancel":
    Guidance deactivated.
|elif type = "recalculating":
    Recalculating{Occasionally(2, " route")}.
}

{if clip_notify && !va_active:
    {Pause(200)}
    Clipboard set.
}

