{Log("Fktn.: Security presence report")}

{_ Fetch from context _}
{set reportsystem to SystemDetails(state.eddi_context_system_system)}
{SetState('eddi_context_system_name', event.system)}
{SetState('eddi_context_system_system', event.system)}
{set trafficDetails to TrafficDetails(event.system)}
{set deathDetails to TrafficDetails(event.system, "deaths")}

{if !reportsystem.name || reportbody.name = "": Welches System ist gemeint?
|else:
	{if reportsystem.population > 0 										: {OneOf("Registriere,", "Verzeichne,")}
        {  if system.security = "None"		|| system.security = "Keine"  	: kein
        |elif system.security = "Low"   	|| system.security = "Niedrig"	: {OneOf("minimales", "geringes", "kaum")}
       	|elif system.security = "Medium"	|| system.security = "Mittel" 	: {OneOf("moderates", "mittleres", "duchschnittliches")}
       	|elif system.security = "High"   	|| system.security = "Hoch"   	: {OneOf("hohes", "verstärktes", "starkes")}
       	|elif system.security = "Anarchie"									: {OneOf("Achtung, freies", "Achtung, ungeordnetes", "Achtung, willkürliches",
       																			"Achtung,anarchistisches")}
        |else																: {system.security}
		}
	{OneOf("Polizei","Sicherheits")} Aufkommen {OneOf("vor Ort", "in diesem System", "in dieser Region")}.
    }
	
{Pause(500)}

	{if event.system:
		{if trafficDetails.day > 1 : 
			{if trafficDetails.day = 2 : Mindestens ein Schiff ist heute bereits im System 
				{if (reportsystem.name) = "Shinrarta Dezhra": schin-rahta |else: {reportsystem.name}} gewesen. 
			|else: {trafficDetails.day} Schiffe sind heute schon im System 
				{if (reportsystem.name) = "Shinrarta Dezhra": schin-rahta |else: {reportsystem.name}} gesichtet worden.
					{if deathDetails.day != 0		: Dabei kam es zu Feindseligkeiten in deren Verlauf
