Ship fsd.script:
{Log("EVENT: Ship FSD")}


{if event.is_cooldown = true                : 
|elif event.is_supercruise_boosting = true  : supercruise boost
|elif event.was_hyperspace = true           : 
|elif event.was_hyperdrive_charging = true  : {_ && GetState('hd_geladen') = true : OneOf("Laden", "Aufladen", "Ladesequenz") abgebrochen, _}
|elif event.was_supercruise = true          : 
|elif event.was_supercruise_assisted = true : Supercruise Assitend beendet
|elif event.was_supercruise_boosting = true : supercruise boosting beendet
|elif event.was_supercruise_charging = true : {_ && GetState('sc_geladen') = true : OneOf("Laden", "Aufladen", "Ladesequenz") abgebrochen, _} 
|else: 
}

{if event.was_mass_locked = true :
    {OneOf("Masse-Sperre,", "Gravitationssprerre", "Schwerkraftsperre")} 
    {OneOf("inaktiv,", "aufgehoben,", "ausser Kraft,", "nicht mehr aktiv,")}
|elif event.is_hyperdrive_charging = true :
    {if GetState('hd_geladen') = true :
    |else:
        {SetState('hd_geladen', true)}
        {OneOf(
            '{OneOf(
                "Ladesequenz gestartet,",
                "Sprung in Vorbereitung,",
                "aufladen des F S A,",
                "Aufladen gestartet,",
                "Sprung wird vorbereitett,")},',
            '{OneOf("F S A", "Antrieb", "Hühper-Antrieb", "Sprungantrieb")} wird {OneOf("aufgeladen,","geladen")}')}
        {SetState('eddi_fine_check', fine_check)} 
    }
|elif event.is_supercruise_charging = true :
    {if GetState('sc_geladen') = true :
    |else:
        {SetState('sc_geladen', true)}
        {OneOf(
            '{OneOf(
                "Ladesequenz gestartet,",
                "aufladen des F S A,",
                "Aufladen gestartet,")},',
            '{OneOf("F S A", "Antrieb", "Super cruise")} wird {OneOf("aufgeladen,","geladen,")}')}
    }          
|elif event.is_supercruise = true           : {SetState('hd_geladen', false)}
|elif event.is_supercruise_assisted = true  : Supercruise Assitend {OneOf('gestartet', 'in Betrieb')}
|elif event.is_hyperspace = true            : {SetState('sc_geladen', false)}
|elif event.was_cooldown = true             : Sprungantrieb {OneOf("betriebsbereit,","bereit,", "einsatzbereit,", "steht zur Verfügung,")}
|elif event.is_mass_locked = true           : {OneOf("Masse-Sperre", "Gravitationssprerre","Schwerkraftsperre")} {OneOf("in Kraft,", "aktiv,")}
}

{_
is_cooldown                 - True if the FSD is currently cooling down after a jump to hyperspace or supercruise. 
is_hyperdrive_charging      - True if the FSD is currently charging for a jump to hyperspace. 
is_hyperspace               - True if the FSD is currently in hyperspace. 
is_mass_locked              - True if the FSD is currently mass locked. 
is_supercruise              - True if the FSD is currently in supercruise. 
is_supercruise_assisted     - True if FSD supercruise assist (SCA) mode is currently activated. 
is_supercruise_boosting     - True if FSD supercruise overdrive (SCO) mode is currently activated. 
is_supercruise_charging     - True if the FSD is currently charging for a jump to supercruise. 
was_cooldown                - True if the FSD was cooling down after a jump to hyperspace or supercruise. 
was_hyperdrive_charging     - True if the FSD was charging for a jump to hyperspace. 
was_hyperspace              - True if the FSD was in hyperspace. 
was_mass_locked             - True if the FSD was mass locked. 
was_supercruise             - True if the FSD was in supercruise. 
was_supercruise_assisted    - True if FSD supercruise assist (SCA) mode was activated. 
was_supercruise_boosting    - True if FSD supercruise overdrive (SCO) mode was activated. 
was_supercruise_charging    - True if the FSD was charging for a jump to supercruise. 
_}

Ship fsd.defaultValue:
{set hazardDistance to 100} {_ Warn me when hazardous stars are within this distance (in ls) from the main star }

{_ Fetch from context _}
{if state.eddi_context_next_system_name:
    {set reportSystem to SystemDetails(state.eddi_context_selected_system_name)}
|else:
    {set reportSystem to nextsystem}
}

{if event.is_hyperdrive_charging: 
    {if len(reportSystem.systemname) > 0:
    
        {Pause(2000)}
    
        {if reportSystem.systemname != homesystem.systemname:
            {for compartment in ship.compartments:
                {if compartment.module.invariantName = 'Fuel Scoop':
                    {set scoop_present to true}
                }
            } 
            
            {set nearbyWhiteDwarfStars to []}
            {set nearbyNeutronStars to []}
            {set nearbyBlackHolesStars to []}
            {for aBody in reportSystem.bodies:
                {if aBody.bodyType.invariantName = 'Star':
                    {if aBody.mainstar:
                        {set mainstar to aBody}
                    |elif scoop_present && aBody.scoopable && aBody.distance < secondaryScoopStar.distance:
                        {set secondaryScoopStar to aBody)}
                    }
                    {if slice(aBody.stellarclass, 0, 1) = "D" && aBody.distance < hazardDistance:
                        {set nearbyWhiteDwarfStars to cat(nearbyWhiteDwarfStars, [aBody])} 
                    |elif aBody.stellarclass = 'N' && aBody.distance < hazardDistance:
                        {set nearbyNeutronStars to cat(nearbyNeutronStars, [aBody])}
                    |elif aBody.stellarclass = 'H' && aBody.distance < hazardDistance:                    
                        {set nearbyBlackHolesStars to cat(nearbyBlackHolesStars, [aBody])}
                    }
                }
            }
            
            {set nearbyHazards to []}            
            {if len(nearbyWhiteDwarfStars) > 0:
                {set hazard to: White Dwarf{if len(nearbyWhiteDwarfStars) > 1:s}}
                {set nearbyHazards to cat(nearbyHazards, [hazard])}
            }
            {if len(nearbyNeutronStars) > 0:
                {set hazard to: Neutron Star{if len(nearbyNeutronStars) > 1:s}}
                {set nearbyHazards to cat(nearbyHazards, [hazard])}
            }
            {if len(nearbyBlackHolesStars) > 0:
                {set hazard to: Black Hole{if len(nearbyBlackHolesStars) > 1:s}}
                {set nearbyHazards to cat(nearbyHazards, [hazard])}
            }
            
            {if len(nearbyHazards) > 0:
                Caution: {List(nearbyHazards)} detected. 
            }

            {if scoop_present && mainstar && !mainstar.scoopable && !secondaryScoopStar:
                {if len(nearbyHazards) = 0: {Occasionally(2, "Alert: ")}}
                No scoopable stars charted in arrival system.
            }
        }
    }
}

Ship loadout.script:
{Log("EVENT: Ship loadout")}

{if ship.role = "Handel": {set ship_role to OneOf('Händler', 'Import-Export', 'Einzelhandel', 'Warenhandel')}}
{SetState('eddi_ship_role', ship_role)}

{SetState('multi_en', false)}				{SetState('ax_multi_en', false)}
{SetState('kanonen_en', false)}				{SetState('rails_en', false)}
{SetState('splitter_en', false)}			{SetState('plasma_en', false)}
{SetState('minenw_en', false)}				{SetState('sminenw_en', false)}
{SetState('rampen_en', false)}				{SetState('ax_rampen_en', false)}
{SetState('pylone_en', false)}				{SetState('dueppelw_en', false)}
{SetState('punktv_en', false)}				{SetState('heatsinkl_en', false)}

{SetState('cellbank_en', false)}			{SetState('afwe_en', false)}
{SetState('std_dockingcomp_inst', false)}	{SetState('std_dockingcomp_en', false)}
{SetState('adv_dockingcomp_inst', false)}	{SetState('adv_dockingcomp_en', false)}
{SetState('fighter_inst', false)}			{SetState('fighter_en', false)}
{SetState('fuelscoop_inst', false)}			{SetState('fuelscoop_en', false)}
{SetState('vehicles_count', 0)}

{SetState('sc_assi_inst', false)}           {SetState('dos_scanner_inst', false)}
{SetState('sc_assi_en', false)}             {SetState('dos_scanner_en', false)}

{_ Variablen für Anzahl Waffen _}
{set multi to 0)} {set ax_multi to 0)} {set kanonen to 0} {set rails to 0}
{set splitter to 0} {set plasma to 0} {set plasma_plus to 0} {set minenw to 0}
{set sminenw to 0} {set rampen to 0} {set ax_rampen to 0} {set pylone to 0}
{set dueppelw to 0} {set punktv to 0} {set heatsinkl to 0} {set cellbank to 0}
{set afwe to 0}

{set ammo_plasma to 0}

{if ship.model = "panthermkii" || ship.model = "Panther Clipper Mk. II" || ship.model = "PantherMkII":
    {set ship_cargocapacity to state.panther_clipper_cap}
|else:
    {set ship_cargocapacity to ship.cargocapacity}
}

{_ --------------------- Munition für Waffen + Düppel, Punktvert. und Kühlkörper ------------------------- _}
{_ for hardpoint in ship.hardpoints : hardpoint.module.name, _}

{Pause(500)}

{for hardpoint in ship.hardpoints:
   	{if  hardpoint.module.name = "Mehrfachgeschütz":	{set multi to multi + 1}
   		{set ammo_multi to ammo_multi + hardpoint.module.ammoinhopper + hardpoint.module.ammoinclip}
      		{SetState('eddi_ammo_multi', ammo_multi)} {SetState('multi_en', hardpoint.module.enabled)}
   	}
   	{if  hardpoint.module.name = "AX Mehrfachgeschütz":	{set ax_multi to ax_multi + 1}
   		{set ammo_ax_multi to ammo_ax_multi + hardpoint.module.ammoinhopper + hardpoint.module.ammoinclip}
      		{SetState('eddi_ammo_ax_multi', ammo_ax_multi)} {SetState('ax_multi_en', hardpoint.module.enabled)}
   	}	
   	{if  hardpoint.module.name = "Kanone": 				{set kanonen to kanonen + 1}
   		{set ammo_kanone to ammo_kanone + hardpoint.module.ammoinhopper + hardpoint.module.ammoinclip}
      		{SetState('eddi_ammo_kanone', ammo_kanone)} {SetState('kanonen_en', hardpoint.module.enabled)}
   	}
   	{if  hardpoint.module.name = "Schienenkanone":		{set rails to rails + 1}
   		{set ammo_rails to ammo_rails + hardpoint.module.ammoinhopper + hardpoint.module.ammoinclip}
      		{SetState('eddi_ammo_rails', ammo_rails)} {SetState('rails_en', hardpoint.module.enabled)}
   	}
   	{if  hardpoint.module.name = "Splitterkanone":		{set splitter to splitter + 1}
   		{set ammo_splitter to ammo_splitter + hardpoint.module.ammoinhopper + hardpoint.module.ammoinclip}
      		{SetState('eddi_ammo_splitter', ammo_splitter)} {SetState('splitter_en', hardpoint.module.enabled)}
   	}
   	{if  hardpoint.module.name = "Plasmabeschleuniger":	{set plasma to plasma + 1}
   		{set ammo_plasma to ammo_plasma + hardpoint.module.ammoinhopper + hardpoint.module.ammoinclip}
      		{SetState('eddi_ammo_plasma', ammo_plasma)} {SetState('plasma_en', hardpoint.module.enabled)}
   	}
   	{if  hardpoint.module.name = "Minenwerfer":			{set minenw to minenw + 1}
   		{set ammo_minenw to ammo_minenw + hardpoint.module.ammoinhopper + hardpoint.module.ammoinclip}
      		{SetState('eddi_ammo_minenw', ammo_minenw)} {SetState('minenw_en', hardpoint.module.enabled)}
   	}
   	{if  hardpoint.module.name = "Schockminenwerfer":	{set sminenw to sminenw + 1}
   		{set ammo_sminenw to ammo_sminenw + hardpoint.module.ammoinhopper + hardpoint.module.ammoinclip}
      		{SetState('eddi_ammo_sminenw', ammo_sminenw)} {SetState('sminenw_en', hardpoint.module.enabled)}
