Ship refuelled.script:
{Log("EVENT: Ship refuelled")}

{_ Context}
{SetState('eddi_context_last_action', 'complete')}
{SetState('eddi_context_fuel_remaining', event.total)}

{set fuelscooped to GetState('eddi_fuel_scooped')}
{set ship_fuel_max to ship.fueltanktotalcapacity}

{if event.source = "Market":
	{set limpetcheck to F("Limpet check")}
	{OneOf('{ShipName()} wurde','Unser Schiff wurde')} für {event.price} credditts aufgetankt.
	
    {if ship.role = "Mining" || ship.role = "Bergbau" :
        {if limpetcheck: {Pause(500)} {limpetcheck}}
    |elif ship.role = "Kampfunterstützung" || ship.role = "Kampf"
        || ship.role = "AX Unterstützung"  || ship.role = "AX Kampf"
        || ship.role = "Kopfgeldjagd"      || ship.role = "Attentat"
        || ship.role = "Piraterie":       
        {if ammocheck: {Pause(1000)} {ammocheck}}
    |else: }
    
|elif event.source = "Scoop" :
    {SetState('eddi_fuel_scooped', (GetState('eddi_fuel_scooped')) + event.amount)}
    {SetState('eddi_fuel_session', (GetState('eddi_fuel_session')) + event.amount)}
    
    {_ rund round(GetState('eddi_fuel_scooped'),1) Tonnen aufgenommen,}
             
    {set total to (100/ship_fuel_max)*event.total}              {_ total in % _}
    {set trim to round((100/ship_fuel_max) * event.amount,1)}   {_ GetState('eddi_fuel_scooped') }
    {F("ZeroTrim")}
    {set percent to trim}

    {set trim to round(event.amount,1)}                         {_ GetState('eddi_fuel_scooped') }
    {F("ZeroTrim")}
    {set sp_menge to trim}

    {if sp_menge <= 0.5                     : {_ set tonnes to "weniger als eine halbe"} {_ set tons to "tonne"}
    |elif sp_menge > 0.5 && sp_menge < 1.4  : {set tonnes to "rund eine"} {set tons to "tonne"}
    |else                                   : {set tonnes to sp_menge} {set tons to "tonnen"}
    }
		
    {if percent = 1: {set percent to "ein"}}
    
    {Pause(500)}
    
    {if total > 0.1 && total < 100:
        {if state.fuel_ansagen = 1:
            {percent}% {OneOf("aufgetankt,", "Treibstoff gewonnen,", "Sprit an Bord genommen,")}
        |elif state.fuel_ansagen = 2:
            {if sp_menge <= 0.5 : {_ halt die Klappe }
            |else: {tonnes} {tons} {OneOf("aufgetankt,", "Treibstoff gewonnen,", "Sprit an Bord genommen,")}
            }
            {set trim to total} {F("ZeroTrim")}    
            {OneOf("Der Tank ist {Occasionally(3, 'jetzt')} zu {round(trim, 1)}% gefüllt.", 
            "Füllstand Tank {round(trim, 1)}%, ")}
        |elif state.fuel_ansagen = 3:
            {OneOf("{OneOf('{percent}%','{tonnes} {tons}')} Treibstoff gewonnen,", 
            "{OneOf('{percent}%','{tonnes} {tons}')} Sprit an Bord genommen,")},
        |elif state.fuel_ansagen = 4:
            {set trim to total} {F("ZeroTrim")}
            {OneOf("Der Tank ist {Occasionally(3, 'jetzt')} zu {round(trim, 1)}% gefüllt.", 
            "Füllstand Tank {round(trim, 1)}%, ")}
        |elif state.fuel_ansagen = 5 :
            {if total < 25 : Der Tank ist nicht mal ein Viertel voll.                   {_ unter 25% }
            |elif total < 33 && total > 28 : Der Tank ist zu cirka einem Drittel voll.  {_ fast 1/3 } 
            |elif total < 52 && total > 48 : Der Tank ist ungefähr halbvoll             {_ cirka 50% } 
            |elif total < 68 && total > 64 : Der Tank ist cirka zu zwei Drittel voll.   {_ fast 2/3 }
            |elif total < 100 && total > 80 : Der Tank ist fast voll.                   {_ fast voll }
            |else:}
            
        |else:
            {OneOf("{percent}% aufgetankt,",
            "{OneOf('{percent}%','{tonnes} {tons}')} Treibstoff gewonnen,",
            "{OneOf('{percent}%','{tonnes} {tons}')} Sprit an Bord genommen,")},
            {set trim to total} {F("ZeroTrim")}	
            {OneOf("Der Tank ist {Occasionally(3, 'jetzt')} zu {round(trim, 1)}% gefüllt.", 
            "Füllstand Tank {round(trim, 1)}%, ")}
            }
    |elif total = 100: Der
        {Occasionally(2,"Haupt-")} {Occasionally(2,"Treibstoff-")}tank 
        {OneOf("ist jetzt zu 100% gefüllt!","ist wieder voll!","wurde wieder komplett aufgefüllt!")}
    |else:
        {F('JumpFuel')}  {_ F('Fuel check')... wäre auch möglich _}
    }
    {Occasionally(3,"{F('Honorific')}")}.
    {if event.amount < 5 :  {SetState('eddi_fuel_scooped',0)}}
}

{_ status.fuel - in Tonnen mit Reserve _}
{_ status.fuel_percent _}


{_
amount 	- The amount of fuel obtained 
full 	- Whether this is a full refuel 
price 	- The price of refuelling (only available if the source is Market) 
source 	- The source of the fuel (Market or Scoop) 
total 	- The new fuel level (only available if the source is Scoop) 
_}

