Ship repaired.script:
{Log("EVENT: Ship repaired")}

{Pause(500)}
{set repairedSystems to []}
{if len(event.items) > 0:
    {for item in event.items:
    	{if item = "Hull":
            {set systemDescription to cat("Hülle wurde ", OneOf("erneuert.",  "repariert.", 
            "instandgesetzt.", "wieder hergestellt."))}
            {set repairedSystems to cat(repairedSystems, [systemDescription])} 
        |elif item = "Paint":
            {set systemDescription to cat(Occasionally(2, "{ShipName()}"), " Lackierung ", 
            OneOf("wiederaufbereitet", "erneuert")))}
            {set repairedSystems to cat(repairedSystems, [systemDescription])}        
        |elif item = "Schiffszustand" || item = "Wear" || item = "Ship Integrity":
            {set systemDescription to cat(OneOf("reguläre", "Standard-") " Wartung ", 
            Occasionally(2, "für {ShipName()}"), OneOf(" durchgeführt."," beendet."))}
            {set repairedSystems to cat(repairedSystems, [systemDescription])}                 
        }
    }
}

{set repairedModules to []}
{set repairList to []}

{if len(event.modules) > 0:
    {for module in event.modules:
        {if len(module.localizedmount) > 0:
            {_ This is a weapon. We'll identify the class, grade, and mount}
            {set weaponDescription to cat(module.class, " ", module.grade, " ", module.mount, " ", module.name)}
            {set repairedModules to cat(repairedModules, [weaponDescription])}
        |elif find(module.invariantName, "Cockpit") > -1:
            {_ This isn't a weapon. We've matched it to the name of our cockpit module}
            {set repairedModules to cat(repairedModules, [OneOf("Cockpit", "Kanzel", "Kabine", "Pilotenkanzel")])}                 
        |else:
            {_ This isn't a weapon. We haven't matched it to the name of any particular module}
            
            {set translate to module.name} {set module_name to F('Translate Ship Module')} {_ - im Test-Modus - _}
            {set repairedModules to cat(repairedModules, [module_name])}
        }
    }
}

{if len(repairedSystems) > 0:
    {List(repairedSystems)}
}
{if len(repairedModules) > 0:
    {List(repairedModules)}
    {if len(repairedModules) > 1:
    	wurden {_ für set value to event.price F("HuVal") Credditts _} {OneOf("erneuert",  "repariert", 
            "instandgesetzt", "wieder hergestellt"))}.
    |else: {Occasionally(2, "wurde")} {_ für set value to event.price F("HuVal") Credditts _} 
    		{OneOf("erneuert",  "repariert", "instandgesetzt", "wieder hergestellt"))}.
    }		
}
