        {if len(module.localizedmount) > 0:
            {_ This is a weapon. We'll identify the class, grade, and mount}
            {set weaponDescription to cat(module.class, " ", module.grade, " ", module.mount, " ", module.name)}
            {set repairedModules to cat(repairedModules, [weaponDescription])}
        |elif find(module.invariantName, "Cockpit") > -1:
            {_ This isn't a weapon. We've matched it to the name of our cockpit module}
            {set repairedModules to cat(repairedModules, [OneOf("Canopy", "Cockpit")])}                 
        |else:
            {_ This isn't a weapon. We haven't matched it to the name of any particular module}
            {set repairedModules to cat(repairedModules, [module.name])}
        }
    }
}

{if len(repairedSystems) > 0:
    {List(repairedSystems)}.
}
{if len(repairedModules) > 0:
    {List(repairedModules)} repaired.
}
{if len(repairedSystems) = 0 && len(repairedModules) = 0:
    {ShipName()} repaired.
}

